import React from "react";
import { TicketCard } from "@/components/ticket/TicketCard";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { supabase } from "@/lib/supabase";
import { Skeleton } from "@/components/ui/skeleton";
import { toast } from "sonner";

export const AdminTicketManager = () => {
  const queryClient = useQueryClient();

  const { data: tickets, isLoading } = useQuery({
    queryKey: ['admin-tickets'],
    queryFn: async () => {
      const { data, error } = await supabase
        .from('tickets')
        .select(`
          *,
          assigned_user:assigned_to(email),
          reporter_user:reporter(email),
          project:project_id(title)
        `)
        .order('created_at', { ascending: false });

      if (error) throw error;
      return data;
    }
  });

  const updateTicketMutation = useMutation({
    mutationFn: async ({ ticketId, updates }: { ticketId: string, updates: any }) => {
      const { error } = await supabase
        .from('tickets')
        .update(updates)
        .eq('id', ticketId);

      if (error) throw error;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['admin-tickets'] });
      toast.success('Ticket updated successfully');
    },
    onError: (error) => {
      console.error('Error updating ticket:', error);
      toast.error('Failed to update ticket');
    }
  });

  if (isLoading) {
    return (
      <div className="space-y-4">
        <Skeleton className="h-[200px] w-full" />
        <Skeleton className="h-[200px] w-full" />
        <Skeleton className="h-[200px] w-full" />
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {tickets?.map((ticket) => (
        <TicketCard 
          key={ticket.id} 
          ticket={ticket}
          onTicketUpdated={(updates) => {
            updateTicketMutation.mutate({ 
              ticketId: ticket.id, 
              updates 
            });
          }}
        />
      ))}
      {!tickets?.length && (
        <div className="text-center py-8 text-muted-foreground">
          No tickets found
        </div>
      )}
    </div>
  );
};

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Mail, Linkedin } from "lucide-react";
import { toast } from "sonner";
import { supabase } from "@/lib/supabase";

interface RegisterFormProps {
  type: string;
}

export const RegisterForm = ({ type }: RegisterFormProps) => {
  const [isLoading, setIsLoading] = useState(false);
  const [isLinkedInLoading, setIsLinkedInLoading] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isParentAccount, setIsParentAccount] = useState(true);
  const [companyName, setCompanyName] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [organizationId, setOrganizationId] = useState("");

  const handleSignUp = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      // First check if user already exists
      const { data: existingUser } = await supabase
        .from('profiles')
        .select('id')
        .eq('email', email)
        .maybeSingle();

      if (existingUser) {
        toast.error("An account with this email already exists. Please try logging in or reset your password.");
        setIsLoading(false);
        return;
      }

      const metadata: any = {
        user_type: type,
        is_parent: isParentAccount.toString()
      };

      if (isParentAccount && (type === 'business' || type === 'recruiter')) {
        if (!companyName) {
          throw new Error("Company name is required for organization accounts");
        }
        metadata.company_name = companyName;
      } else if (!isParentAccount) {
        if (!organizationId) {
          throw new Error("Organization ID is required for member accounts");
        }
        if (!firstName || !lastName) {
          throw new Error("First and last name are required for member accounts");
        }
        metadata.organization_id = organizationId;
        metadata.first_name = firstName;
        metadata.last_name = lastName;
      }

      const { error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          data: metadata,
          emailRedirectTo: `${window.location.origin}/auth/${type}`
        }
      });

      if (error) {
        if (error.message.includes("User already registered")) {
          toast.error("An account with this email already exists. Please try logging in or reset your password.");
        } else {
          throw error;
        }
        setIsLoading(false);
        return;
      }

      toast.success("Registration successful! Please check your email to confirm your account.");
    } catch (error) {
      console.error('Error:', error);
      toast.error(error instanceof Error ? error.message : "An error occurred during registration");
    } finally {
      setIsLoading(false);
    }
  };

  const handleLinkedInSignUp = async () => {
    setIsLinkedInLoading(true);
    try {
      // Construct query parameters based on account type
      const queryParams: Record<string, string> = {
        userType: type,
        isParent: isParentAccount.toString(),
      };

      // Add company name for parent business/recruiter accounts
      if (isParentAccount && (type === 'business' || type === 'recruiter') && companyName) {
        queryParams.companyName = companyName;
      }

      // Add member account details if not a parent account
      if (!isParentAccount) {
        if (organizationId) queryParams.organizationId = organizationId;
        if (firstName) queryParams.firstName = firstName;
        if (lastName) queryParams.lastName = lastName;
      }

      const { error } = await supabase.auth.signInWithOAuth({
        provider: 'linkedin_oidc',
        options: {
          redirectTo: `${window.location.origin}/auth/${type}`,
          queryParams
        }
      });

      if (error) {
        throw error;
      }
    } catch (error) {
      console.error('LinkedIn signup error:', error);
      toast.error("Failed to sign up with LinkedIn");
    } finally {
      setIsLinkedInLoading(false);
    }
  };

  return (
    <form onSubmit={handleSignUp} className="space-y-4">
      {(type === 'business' || type === 'recruiter') && (
        <div className="space-y-2">
          <Label>Account Type</Label>
          <div className="flex gap-4">
            <Button
              type="button"
              variant={isParentAccount ? "default" : "outline"}
              onClick={() => setIsParentAccount(true)}
              className="flex-1"
            >
              Organization
            </Button>
            <Button
              type="button"
              variant={!isParentAccount ? "default" : "outline"}
              onClick={() => setIsParentAccount(false)}
              className="flex-1"
            >
              Member
            </Button>
          </div>
        </div>
      )}

      <div className="space-y-2">
        <Label htmlFor="registerEmail">Email address</Label>
        <div className="relative">
          <Mail className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
          <Input
            id="registerEmail"
            type="email"
            className="pl-9"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
      </div>

      {(type === 'business' || type === 'recruiter') && isParentAccount && (
        <div className="space-y-2">
          <Label htmlFor="companyName">Company Name</Label>
          <Input
            id="companyName"
            type="text"
            placeholder="Enter company name"
            value={companyName}
            onChange={(e) => setCompanyName(e.target.value)}
            required
          />
        </div>
      )}

      {!isParentAccount && (
        <>
          <div className="space-y-2">
            <Label htmlFor="organizationId">Organization ID</Label>
            <Input
              id="organizationId"
              type="text"
              placeholder="Enter organization ID"
              value={organizationId}
              onChange={(e) => setOrganizationId(e.target.value)}
              required
            />
          </div>
          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="firstName">First Name</Label>
              <Input
                id="firstName"
                type="text"
                placeholder="First name"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                required
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="lastName">Last Name</Label>
              <Input
                id="lastName"
                type="text"
                placeholder="Last name"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                required
              />
            </div>
          </div>
        </>
      )}

      <div className="space-y-2">
        <Label htmlFor="registerPassword">Password</Label>
        <Input
          id="registerPassword"
          type="password"
          placeholder="Create a password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          minLength={6}
        />
        <p className="text-sm text-muted-foreground">Password must be at least 6 characters long</p>
      </div>
      <Button type="submit" className="w-full" disabled={isLoading}>
        {isLoading ? "Creating account..." : "Create account"}
      </Button>

      <div className="relative flex items-center py-2">
        <div className="flex-grow border-t border-gray-300"></div>
        <span className="flex-shrink mx-4 text-sm text-gray-600">or</span>
        <div className="flex-grow border-t border-gray-300"></div>
      </div>

      <Button 
        type="button" 
        variant="outline" 
        className="w-full flex items-center justify-center gap-2"
        onClick={handleLinkedInSignUp}
        disabled={isLinkedInLoading}
      >
        <Linkedin className="h-4 w-4" />
        {isLinkedInLoading ? "Connecting..." : "Sign up with LinkedIn"}
      </Button>
    </form>
  );
};

import { useState } from "react";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { ChevronDown, ChevronUp, Edit, Trash, ExternalLink, Users } from "lucide-react";
import { Card, CardHeader, CardContent } from "@/components/ui/card";
import { TooltipProvider, Tooltip, TooltipTrigger, TooltipContent } from "@/components/ui/tooltip";
import { Link } from "react-router-dom";

interface ActiveRolesTableProps {
  project: any;
}

export const ActiveRolesTable = ({ project }: ActiveRolesTableProps) => {
  const [expandedProjects, setExpandedProjects] = useState<Set<string>>(new Set());
  const [expandedTasks, setExpandedTasks] = useState<Set<string>>(new Set());

  const toggleProject = (projectId: string) => {
    setExpandedProjects(prev => {
      const newSet = new Set(prev);
      if (newSet.has(projectId)) {
        newSet.delete(projectId);
      } else {
        newSet.add(projectId);
      }
      return newSet;
    });
  };

  const toggleTask = (taskId: string) => {
    setExpandedTasks(prev => {
      const newSet = new Set(prev);
      if (newSet.has(taskId)) {
        newSet.delete(taskId);
      } else {
        newSet.add(taskId);
      }
      return newSet;
    });
  };

  // Mock function for application counts - in a real app, this would fetch from the database
  const getApplicationCount = (id: string, type: 'project' | 'task') => {
    // For demonstration purposes, just return a random number between 0 and 5
    return Math.floor(Math.random() * 6);
  };

  return (
    <div className="space-y-4">
      <div className="mb-4 text-sm text-muted-foreground">
        These are the projects with active applications on live projects, and the progress of completion of the projects.
      </div>

      <Card className="border rounded-lg overflow-hidden">
        <div className="border-b cursor-pointer" onClick={() => toggleProject(project.project_id || project.id)}>
          <div className="p-4 flex flex-col md:flex-row justify-between">
            <div className="flex-1">
              <div className="flex items-center">
                <Link 
                  to={`/projects/${project.project_id || project.id}`}
                  className="text-blue-600 hover:underline text-lg font-medium"
                  onClick={(e) => e.stopPropagation()}
                >
                  {project.title}
                </Link>

                <Tooltip>
                  <TooltipTrigger asChild>
                    <Link 
                      to={`/business/applications?project=${project.project_id || project.id}`}
                      className="ml-2 flex items-center text-sm text-blue-500 hover:text-blue-700"
                      onClick={(e) => e.stopPropagation()}
                    >
                      <Users className="h-4 w-4 mr-1" />
                      {getApplicationCount(project.project_id || project.id, 'project')}
                    </Link>
                  </TooltipTrigger>
                  <TooltipContent>
                    <p className="text-xs">View applications for this project</p>
                  </TooltipContent>
                </Tooltip>
              </div>

              <div className="flex items-center flex-wrap text-muted-foreground text-sm mt-1">
                <span className="mr-4">Status: {project.status || "Active"}</span>
                <span>Timeframe: {project.project_timeframe || project.timeframe || 'Not specified'}</span>
              </div>

              <TooltipProvider>
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mt-4">
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <div>
                        <div className="text-xs font-medium">Total Equity Offered</div>
                        <div className="text-sm">{project.equity_allocation}%</div>
                      </div>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p className="text-xs">Total equity allocation for this project</p>
                    </TooltipContent>
                  </Tooltip>

                  <Tooltip>
                    <TooltipTrigger asChild>
                      <div>
                        <div className="text-xs font-medium">Task Equity</div>
                        <div className="text-sm">
                          {project.tasks.reduce((sum: number, task: any) => 
                            sum + (task.equity_allocation || 0), 0)}%
                        </div>
                      </div>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p className="text-xs">Sum of equity allocated to all tasks</p>
                    </TooltipContent>
                  </Tooltip>

                  <Tooltip>
                    <TooltipTrigger asChild>
                      <div>
                        <div className="text-xs font-medium">Agreed Equity</div>
                        <div className="text-sm">0%</div>
                      </div>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p className="text-xs">Total equity agreed with job seekers</p>
                    </TooltipContent>
                  </Tooltip>

                  <Tooltip>
                    <TooltipTrigger asChild>
                      <div>
                        <div className="text-xs font-medium">Equity Earned</div>
                        <div className="text-sm">0%</div>
                      </div>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p className="text-xs">Equity already earned by job seekers</p>
                    </TooltipContent>
                  </Tooltip>
                </div>
              </TooltipProvider>

              <div className="mt-2">
                <div className="text-xs font-medium">Required Skills</div>
                <div className="flex flex-wrap gap-1 mt-1">
                  {Array.isArray(project.skills_required) && project.skills_required.map((skill: string, index: number) => (
                    <Badge key={index} variant="outline" className="text-xs">
                      {skill}
                    </Badge>
                  ))}
                  {(!Array.isArray(project.skills_required) || project.skills_required.length === 0) && (
                    <span className="text-muted-foreground text-xs">No skills specified</span>
                  )}
                </div>
              </div>
            </div>

            <div className="flex items-start space-x-2 mt-4 md:mt-0">
              <Button 
                variant="outline" 
                size="sm" 
                onClick={(e) => {
                  e.stopPropagation();
                  // Edit project logic would go here
                }}
              >
                <Edit className="h-4 w-4" />
              </Button>

              <Button 
                variant="outline" 
                size="sm" 
                onClick={(e) => {
                  e.stopPropagation();
                  // Delete project logic would go here 
                }}
                className="text-destructive hover:text-destructive"
              >
                <Trash className="h-4 w-4" />
              </Button>

              <Button 
                variant="ghost" 
                size="sm" 
                onClick={(e) => {
                  e.stopPropagation();
                  toggleProject(project.project_id || project.id);
                }}
              >
                {expandedProjects.has(project.project_id || project.id) ? (
                  <ChevronUp className="h-4 w-4" />
                ) : (
                  <ChevronDown className="h-4 w-4" />
                )}
              </Button>
            </div>
          </div>
        </div>

        {expandedProjects.has(project.project_id || project.id) && (
          <div className="p-4">
            <h3 className="text-sm font-medium mb-3">Project Tasks</h3>

            {project.tasks.length === 0 ? (
              <p className="text-sm text-muted-foreground">No tasks available for this project.</p>
            ) : (
              <div className="space-y-4">
                {project.tasks.map((task: any) => (
                  <Card key={task.id || task.task_id} className="overflow-hidden">
                    <div className="border-b cursor-pointer" onClick={() => toggleTask(task.id || task.task_id)}>
                      <div className="p-3 flex justify-between items-start">
                        <div>
                          <div className="flex items-center">
                            <div className="font-medium">{task.title}</div>

                            <Tooltip>
                              <TooltipTrigger asChild>
                                <Link 
                                  to={`/business/applications?task=${task.id || task.task_id}`}
                                  className="ml-2 flex items-center text-sm text-blue-500 hover:text-blue-700"
                                  onClick={(e) => e.stopPropagation()}
                                >
                                  <Users className="h-4 w-4 mr-1" />
                                  {getApplicationCount(task.id || task.task_id, 'task')}
                                </Link>
                              </TooltipTrigger>
                              <TooltipContent>
                                <p className="text-xs">View applications for this task</p>
                              </TooltipContent>
                            </Tooltip>
                          </div>
                          <div className="flex items-center text-xs text-muted-foreground mt-1">
                            <Badge variant="outline" className="mr-2">{task.status}</Badge>
                            <span>Timeframe: {task.timeframe || 'Not specified'}</span>
                          </div>
                        </div>

                        <div className="flex items-center space-x-2">
                          <Button 
                            variant="ghost" 
                            size="sm" 
                            onClick={(e) => {
                              e.stopPropagation();
                              toggleTask(task.id || task.task_id);
                            }}
                          >
                            {expandedTasks.has(task.id || task.task_id) ? (
                              <ChevronUp className="h-4 w-4" />
                            ) : (
                              <ChevronDown className="h-4 w-4" />
                            )}
                          </Button>
                        </div>
                      </div>
                    </div>

                    {expandedTasks.has(task.id || task.task_id) && (
                      <div className="p-3 bg-gray-50 border-t">
                        <div className="space-y-3">
                          <div>
                            <div className="text-xs font-medium mb-1">Description</div>
                            <p className="text-sm">{task.description || 'No description provided.'}</p>
                          </div>

                          <TooltipProvider>
                            <div className="grid grid-cols-3 gap-4">
                              <Tooltip>
                                <TooltipTrigger asChild>
                                  <div>
                                    <div className="text-xs font-medium">Task Equity</div>
                                    <div className="text-sm">{task.equity_allocation || 0}%</div>
                                  </div>
                                </TooltipTrigger>
                                <TooltipContent>
                                  <p className="text-xs">Equity allocated to this task</p>
                                </TooltipContent>
                              </Tooltip>

                              <Tooltip>
                                <TooltipTrigger asChild>
                                  <div>
                                    <div className="text-xs font-medium">Agreed Equity</div>
                                    <div className="text-sm">0%</div>
                                  </div>
                                </TooltipTrigger>
                                <TooltipContent>
                                  <p className="text-xs">Equity agreed for this task</p>
                                </TooltipContent>
                              </Tooltip>

                              <Tooltip>
                                <TooltipTrigger asChild>
                                  <div>
                                    <div className="text-xs font-medium">Equity Earned</div>
                                    <div className="text-sm">0%</div>
                                  </div>
                                </TooltipTrigger>
                                <TooltipContent>
                                  <p className="text-xs">Equity earned for this task</p>
                                </TooltipContent>
                              </Tooltip>
                            </div>
                          </TooltipProvider>

                          <div>
                            <div className="text-xs font-medium mb-1">Skills Required</div>
                            <div className="flex flex-wrap gap-1">
                              {Array.isArray(task.skill_requirements) && task.skill_requirements.length > 0 ? 
                                task.skill_requirements.map((skill: any, index: number) => {
                                  const skillName = typeof skill === 'string' ? skill : (skill.skill || '');
                                  const level = typeof skill === 'string' ? 'Intermediate' : (skill.level || '');

                                  return (
                                    <Badge key={index} variant="outline" className="text-xs">
                                      {skillName} {level && <span className="ml-1 opacity-70">({level})</span>}
                                    </Badge>
                                  );
                                }) : 
                                <span className="text-sm text-muted-foreground">No specific skills required</span>
                              }
                            </div>
                          </div>
                        </div>
                      </div>
                    )}
                  </Card>
                ))}
              </div>
            )}
          </div>
        )}
      </Card>
    </div>
  );
};

import { useEffect, useState } from "react";
import { Card, CardHeader, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { supabase } from "@/lib/supabase";
import { toast } from "sonner";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { JobApplication } from "@/types/jobSeeker";
import { useApplicationActions } from "@/components/job-seeker/dashboard/applications/hooks/useApplicationActions";
import { RejectApplicationDialog } from "./applications/RejectApplicationDialog";
import { AcceptJobDialog } from "./applications/AcceptJobDialog";
import { useAcceptedJobs } from "@/hooks/useAcceptedJobs";
import { ApplicationsTable } from "./applications/ApplicationsTable";
import { ActiveProjectsTable } from "./applications/ActiveProjectsTable";
import { PendingApplicationsTable } from "./applications/tables/PendingApplicationsTable";
import { ActiveApplicationsTable } from "./applications/tables/ActiveApplicationsTable";
import { WithdrawnApplicationsTable } from "./applications/tables/WithdrawnApplicationsTable";
import { RejectedApplicationsTable } from "./applications/tables/RejectedApplicationsTable";
import { Application, Project } from "@/types/business";
import { convertApplicationToJobApplication } from "@/utils/applicationUtils";

export const ProjectApplicationsSection = () => {
  const [projects, setProjects] = useState<Project[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [expandedProjects, setExpandedProjects] = useState<Set<string>>(new Set());
  const [expandedApplications, setExpandedApplications] = useState<Set<string>>(new Set());
  const [newApplicationsCount, setNewApplicationsCount] = useState(0);
  const [newMessagesCount, setNewMessagesCount] = useState(0);
  const [rejectDialogOpen, setRejectDialogOpen] = useState(false);
  const [selectedApplicationId, setSelectedApplicationId] = useState<string | null>(null);
  const [acceptJobDialogOpen, setAcceptJobDialogOpen] = useState(false);
  const [selectedApplication, setSelectedApplication] = useState<JobApplication | null>(null);
  const [activeTab, setActiveTab] = useState("pending");
  const { isUpdatingStatus, updateApplicationStatus } = useApplicationActions(() => {
    loadProjectsWithApplications();
  });
  const { acceptJobAsBusiness, isLoading: isAcceptingJobLoading } = useAcceptedJobs(() => {
    window.location.reload();
  });

  const handleTabChange = (tab: string) => {
    setActiveTab(tab);
  };

  useEffect(() => {
    loadProjectsWithApplications();
    setupRealtimeListener();
    return () => {
      cleanupRealtimeListener();
    };
  }, []);

  const setupRealtimeListener = () => {
    const channel = supabase
      .channel('application-updates')
      .on(
        'postgres_changes',
        {
          event: 'INSERT',
          schema: 'public',
          table: 'job_applications'
        },
        () => {
          setNewApplicationsCount(prev => prev + 1);
          toast.info("New application received!");
          loadProjectsWithApplications();
        }
      )
      .on(
        'postgres_changes',
        {
          event: 'UPDATE',
          schema: 'public',
          table: 'job_applications',
          filter: 'task_discourse=neq.null'
        },
        () => {
          setNewMessagesCount(prev => prev + 1);
          toast.info("New message received!");
          loadProjectsWithApplications();
        }
      )
      .subscribe();

    return channel;
  };

  const cleanupRealtimeListener = () => {
    supabase.removeChannel(supabase.channel('application-updates'));
  };

  const loadProjectsWithApplications = async () => {
    try {
      setIsLoading(true);
      const { data: { session } } = await supabase.auth.getSession();
      if (!session) return;

      console.log("Loading projects for business ID:", session.user.id);

      const { data: projectsData, error: projectsError } = await supabase
        .from('business_projects')
        .select('*')
        .eq('business_id', session.user.id);

      if (projectsError) {
        console.error("Error fetching projects:", projectsError);
        throw projectsError;
      }

      console.log("Projects fetched:", projectsData?.length || 0);

      if (!projectsData || projectsData.length === 0) {
        setIsLoading(false);
        setProjects([]);
        return;
      }

      const projectsWithApplications: Project[] = [];

      for (const project of projectsData) {
        const { data: tasksData, error: tasksError } = await supabase
          .from('project_sub_tasks')
          .select('task_id, skill_requirements, equity_allocation, timeframe')
          .eq('project_id', project.project_id);

        if (tasksError) {
          console.error('Error fetching tasks for project:', project.project_id, tasksError);
          continue;
        }

        console.log(`Fetched ${tasksData?.length || 0} tasks for project ${project.project_id}`);

        const taskIds = tasksData.map(task => task.task_id);

        if (taskIds.length === 0) {
          projectsWithApplications.push({
            ...project,
            applications: []
          });
          continue;
        }

        const taskSkillsMap = new Map<string, any>();
        tasksData.forEach(task => {
          taskSkillsMap.set(task.task_id, {
            skill_requirements: task.skill_requirements || [],
            equity_allocation: task.equity_allocation,
            timeframe: task.timeframe
          });
        });

        const { data: applicationsData, error: applicationsError } = await supabase
          .from('job_applications')
          .select('*')
          .in('task_id', taskIds);

        if (applicationsError) {
          console.error('Error fetching applications:', applicationsError);
          continue;
        }

        console.log(`Fetched ${applicationsData?.length || 0} applications for project ${project.project_id}`);

        if (!applicationsData || applicationsData.length === 0) {
          projectsWithApplications.push({
            ...project,
            applications: []
          });
          continue;
        }

        const applicationsWithProfiles = [];

        for (const app of applicationsData) {
          const { data: profileData, error: profileError } = await supabase
            .from('profiles')
            .select('first_name, last_name, title, location, employment_preference, skills')
            .eq('id', app.user_id)
            .maybeSingle();

          if (profileError) {
            console.error('Error fetching profile for user:', app.user_id, profileError);
            continue;
          }

          const { data: taskData, error: taskError } = await supabase
            .from('project_sub_tasks')
            .select('title, description, skills_required, skill_requirements, equity_allocation, timeframe')
            .eq('task_id', app.task_id)
            .maybeSingle();

          if (taskError) {
            console.error('Error fetching task details:', taskError);
            continue;
          }

          let userSkills = [];
          if (profileData?.skills) {
            try {
              if (typeof profileData.skills === 'string') {
                const parsedSkills = JSON.parse(profileData.skills);
                if (Array.isArray(parsedSkills)) {
                  userSkills = parsedSkills.map(s => 
                    typeof s === 'string' ? { skill: s, level: "Intermediate" } : s
                  );
                }
              } else if (Array.isArray(profileData.skills)) {
                userSkills = profileData.skills.map(s => 
                  typeof s === 'string' ? { skill: s, level: "Intermediate" } : s
                );
              }
            } catch (e) {
              console.error("Error parsing skills:", e);
            }
          }

          const userSkillNames = userSkills.map(s => s.skill.toLowerCase());
          const taskRequiredSkills = taskData?.skill_requirements || [];

          let matchedSkills = 0;
          if (Array.isArray(taskRequiredSkills)) {
            taskRequiredSkills.forEach(skillObj => {
              const skillName = typeof skillObj === 'string' ? 
                skillObj.toLowerCase() : 
                (skillObj.skill ? skillObj.skill.toLowerCase() : '');

              if (skillName && userSkillNames.includes(skillName)) {
                matchedSkills++;
              }
            });
          }

          const skillMatch = taskRequiredSkills.length > 0 
            ? Math.round((matchedSkills / taskRequiredSkills.length) * 100) 
            : 0;

          applicationsWithProfiles.push({
            ...app,
            profile: {
              ...profileData,
              skills: userSkills
            },
            business_roles: {
              ...(taskData || {}),
              project: {
                title: project.title
              }
            },
            skillMatch
          });
        }

        projectsWithApplications.push({
          ...project,
          applications: applicationsWithProfiles
        });
      }

      console.log("Final projects with applications:", projectsWithApplications.length);
      setProjects(projectsWithApplications);

      const oneDayAgo = new Date();
      oneDayAgo.setDate(oneDayAgo.getDate() - 1);

      let newApps = 0;
      let newMsgs = 0;

      projectsWithApplications.forEach(project => {
        project.applications.forEach(app => {
          const appDate = new Date(app.applied_at);
          if (appDate > oneDayAgo && app.status === 'pending') {
            newApps++;
          }

          if (app.task_discourse) {
            const lastMessageMatch = app.task_discourse.match(/\[([^\]]+)\]/);
            if (lastMessageMatch) {
              try {
                const msgDate = new Date(lastMessageMatch[1]);
                if (msgDate > oneDayAgo) {
                  newMsgs++;
                }
              } catch (e) {
                console.error("Error parsing message date:", e);
              }
            }
          }
        });
      });

      setNewApplicationsCount(newApps);
      setNewMessagesCount(newMsgs);
    } catch (error) {
      console.error('Error loading projects with applications:', error);
      toast.error("Failed to load applications data");
    } finally {
      setIsLoading(false);
    }
  };

  const findApplicationById = (applicationId: string): Application | undefined => {
    for (const project of projects) {
      const application = project.applications.find(app => app.job_app_id === applicationId);
      if (application) return application;
    }
    return undefined;
  };

  const handleStatusChange = async (applicationId: string, newStatus: string) => {
    const application = findApplicationById(applicationId);

    if (!application) {
      toast.error("Application not found");
      return;
    }

    if (application.accepted_business || application.accepted_jobseeker) {
      toast.error("Cannot change status after acceptance. Use contract management instead.");
      return;
    }

    if (newStatus === 'rejected') {
      setSelectedApplicationId(applicationId);
      setRejectDialogOpen(true);
      return;
    }

    await updateApplicationStatus(applicationId, newStatus);
  };

  const openAcceptJobDialog = async (application: Application) => {
    const jobApp = convertApplicationToJobApplication(application);
    setSelectedApplication(jobApp);
    setAcceptJobDialogOpen(true);
    return Promise.resolve();
  };

  const handleRejectWithNote = async (applicationId: string, note: string) => {
    try {
      const { data: application, error: fetchError } = await supabase
        .from('job_applications')
        .select('task_discourse')
        .eq('job_app_id', applicationId)
        .single();

      if (fetchError) throw fetchError;

      const timestamp = new Date().toLocaleString();
      const rejectMessage = `[${timestamp}] Business: ${note} (Rejection reason)`;

      const updatedDiscourse = application.task_discourse 
        ? `${application.task_discourse}\n\n${rejectMessage}`
        : rejectMessage;

      const { error: updateError } = await supabase
        .from('job_applications')
        .update({ 
          status: 'rejected',
          task_discourse: updatedDiscourse
        })
        .eq('job_app_id', applicationId);

      if (updateError) throw updateError;

      toast.success("Application rejected with note");
      loadProjectsWithApplications();
    } catch (error) {
      console.error('Error rejecting application:', error);
      toast.error("Failed to reject application");
    }
  };

  const toggleProjectExpanded = (projectId: string) => {
    setExpandedProjects(prev => {
      const newExpanded = new Set(prev);
      if (newExpanded.has(projectId)) {
        newExpanded.delete(projectId);
      } else {
        newExpanded.add(projectId);
      }
      return newExpanded;
    });
  };

  const toggleApplicationExpanded = (applicationId: string) => {
    setExpandedApplications(prev => {
      const newExpanded = new Set(prev);
      if (newExpanded.has(applicationId)) {
        newExpanded.delete(applicationId);
      } else {
        newExpanded.clear();
        newExpanded.add(applicationId);
      }
      return newExpanded;
    });
  };

  const handleAcceptJob = async (application: JobApplication) => {
    try {
      await acceptJobAsBusiness(application);
      toast.success("Job accepted successfully");
      loadProjectsWithApplications();
    } catch (error) {
      console.error("Error accepting job:", error);
      toast.error("Failed to accept job");
    }
  };

  const getPendingApplications = () => {
    const pendingApps: Application[] = [];
    projects.forEach(project => {
      project.applications.forEach(app => {
        if (['pending', 'in review'].includes(app.status.toLowerCase())) {
          pendingApps.push(app);
        }
      });
    });
    return pendingApps;
  };

  const getActiveApplications = () => {
    const activeApps: Application[] = [];
    projects.forEach(project => {
      project.applications.forEach(app => {
        if (['negotiation', 'accepted'].includes(app.status.toLowerCase())) {
          activeApps.push(app);
        }
      });
    });
    return activeApps;
  };

  const getWithdrawnApplications = () => {
    const withdrawnApps: Application[] = [];
    projects.forEach(project => {
      project.applications.forEach(app => {
        if (app.status.toLowerCase() === 'withdrawn') {
          withdrawnApps.push(app);
        }
      });
    });
    return withdrawnApps;
  };

  const getRejectedApplications = () => {
    const rejectedApps: Application[] = [];
    projects.forEach(project => {
      project.applications.forEach(app => {
        if (app.status.toLowerCase() === 'rejected') {
          rejectedApps.push(app);
        }
      });
    });
    return rejectedApps;
  };

  const pendingApplications = getPendingApplications();
  const activeApplications = getActiveApplications();
  const withdrawnApplications = getWithdrawnApplications();
  const rejectedApplications = getRejectedApplications();

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <h2 className="text-lg font-semibold">Project Applications</h2>
        </CardHeader>
        <CardContent>
          <div className="flex justify-center p-4">
            <p>Loading applications...</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <h2 className="text-lg font-semibold">Project Applications</h2>
        <p className="text-muted-foreground">
          Applications received for your projects. Review applications, negotiate between parties, agree accepted terms.
        </p>
      </CardHeader>
      <CardContent>
        {projects.length === 0 ? (
          <p className="text-muted-foreground text-center p-4">No projects found.</p>
        ) : (
          <Tabs value={activeTab} onValueChange={handleTabChange}>
            <TabsList>
              <TabsTrigger value="pending" className="relative">
                Pending
                {newApplicationsCount > 0 && (
                  <Badge className="absolute -top-2 -right-2 bg-red-500 text-white h-5 w-5 flex items-center justify-center p-0 rounded-full">
                    {newApplicationsCount}
                  </Badge>
                )}
              </TabsTrigger>
              <TabsTrigger value="active" className="relative">
                Active Projects
                {newMessagesCount > 0 && (
                  <Badge className="absolute -top-2 -right-2 bg-red-500 text-white h-5 w-5 flex items-center justify-center p-0 rounded-full">
                    {newMessagesCount}
                  </Badge>
                )}
              </TabsTrigger>
              <TabsTrigger value="completed">Completed</TabsTrigger>
              <TabsTrigger value="withdrawn">Withdrawn</TabsTrigger>
              <TabsTrigger value="rejected">Rejected</TabsTrigger>
            </TabsList>

            <TabsContent value="pending" className="py-4">
              {pendingApplications.length === 0 ? (
                <p className="text-muted-foreground text-center p-4">No pending applications found.</p>
              ) : (
                <>
                  <ApplicationsTable status="pending" />
                  <PendingApplicationsTable 
                    applications={pendingApplications}
                    expandedApplications={expandedApplications}
                    toggleApplicationExpanded={toggleApplicationExpanded}
                    handleStatusChange={handleStatusChange}
                    isUpdatingStatus={isUpdatingStatus}
                  />
                </>
              )}
            </TabsContent>

            <TabsContent value="active" className="py-4">
              {activeApplications.length === 0 ? (
                <p className="text-muted-foreground text-center p-4">No active projects found.</p>
              ) : (
                <>
                  <p className="text-muted-foreground mb-4">
                    When both users (jobseeker and business) agree on the terms both are required to 'accept terms' which will then allow the user to work on the tasks and as the tasks are completed the equity allocation is reviewed in the Live Projects tab.
                  </p>
                  <ActiveProjectsTable status="active" />
                  <ActiveApplicationsTable 
                    applications={activeApplications}
                    expandedApplications={expandedApplications}
                    toggleApplicationExpanded={toggleApplicationExpanded}
                    handleStatusChange={handleStatusChange}
                    isUpdatingStatus={isUpdatingStatus}
                    onApplicationUpdate={() => loadProjectsWithApplications()}
                    openAcceptJobDialog={openAcceptJobDialog}
                    handleAcceptJob={handleAcceptJob}
                    isAcceptingJobLoading={isAcceptingJobLoading}
                  />
                </>
              )}
            </TabsContent>

            <TabsContent value="completed" className="py-4">
              <ApplicationsTable status="completed" />
            </TabsContent>

            <TabsContent value="withdrawn" className="py-4">
              {withdrawnApplications.length === 0 ? (
                <p className="text-muted-foreground text-center p-4">No withdrawn applications found.</p>
              ) : (
                <WithdrawnApplicationsTable 
                  applications={withdrawnApplications}
                  expandedApplications={expandedApplications}
                  toggleApplicationExpanded={toggleApplicationExpanded}
                  handleStatusChange={handleStatusChange}
                  isUpdatingStatus={isUpdatingStatus}
                />
              )}
            </TabsContent>

            <TabsContent value="rejected" className="py-4">
              {rejectedApplications.length === 0 ? (
                <p className="text-muted-foreground text-center p-4">No rejected applications found.</p>
              ) : (
                <RejectedApplicationsTable 
                  applications={rejectedApplications}
                  expandedApplications={expandedApplications}
                  toggleApplicationExpanded={toggleApplicationExpanded}
                  handleStatusChange={handleStatusChange}
                  isUpdatingStatus={isUpdatingStatus}
                />
              )}
            </TabsContent>
          </Tabs>
        )}
      </CardContent>

      <RejectApplicationDialog
        isOpen={rejectDialogOpen}
        onOpenChange={setRejectDialogOpen}
        onReject={(note) => {
          if (selectedApplicationId) {
            handleRejectWithNote(selectedApplicationId, note);
          }
        }}
      />

      <AcceptJobDialog
        isOpen={acceptJobDialogOpen}
        onOpenChange={setAcceptJobDialogOpen}
        application={selectedApplication}
        onAccept={handleAcceptJob}
        isLoading={isAcceptingJobLoading}
      />
    </Card>
  );
};

import { useEffect, useState } from "react";
import { Card, CardHeader, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { PlusCircle } from "lucide-react";
import { toast } from "sonner";
import { ProjectTabs } from "./projects/tabs/ProjectTabs";
import { DeleteProjectDialog } from "./projects/dialogs/DeleteProjectDialog";
import { loadProjects, deleteProject } from "./projects/services/ProjectSectionService";

// Define the types directly here to avoid conflicts
interface SkillRequirement {
  skill: string;
  level: string;
}

interface Task {
  task_id: string;
  title: string;
  description: string;
  status: string;
  hours_logged?: number;
  equity_earned?: number;
  equity_allocation: number;
  timeframe: string;
  skills_required: string[];
  skill_requirements: SkillRequirement[];
  dependencies: string[];
  task_status?: string;
  completion_percentage?: number;
}

interface Project {
  project_id: string;
  title: string;
  description: string;
  status: string;
  equity_allocation: number;
  equity_allocated: number;
  skills_required: string[];
  project_timeframe: string;
  tasks: Task[];
}

export const ProjectsSection = () => {
  const [projects, setProjects] = useState<Project[]>([]);
  const [showProjectForm, setShowProjectForm] = useState(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [projectToDelete, setProjectToDelete] = useState<string | null>(null);

  useEffect(() => {
    loadProjectsData();
  }, []);

  const loadProjectsData = async () => {
    const projectsData = await loadProjects();
    setProjects(projectsData as unknown as Project[]);
  };

  const handleProjectCreated = (newProject: Project) => {
    setProjects([...projects, { ...newProject, tasks: [] }]);
    setShowProjectForm(false);
    toast.success("Project created successfully");
  };

  const handleProjectUpdated = (updatedProject: Project) => {
    setProjects(projects.map(project => 
      project.project_id === updatedProject.project_id ? updatedProject : project
    ));
    toast.success("Project updated successfully");
  };

  const handleProjectDeleted = (projectId: string) => {
    setProjects(projects.filter(project => project.project_id !== projectId));
    toast.success("Project deleted successfully");
  };

  const showDeleteConfirmation = (projectId: string) => {
    setProjectToDelete(projectId);
    setDeleteDialogOpen(true);
  };

  const confirmDeleteProject = async () => {
    if (projectToDelete) {
      const success = await deleteProject(projectToDelete, projects as any);
      if (success) {
        handleProjectDeleted(projectToDelete);
      }
    }
    setDeleteDialogOpen(false);
    setProjectToDelete(null);
  };

  return (
    <Card>
      <CardHeader className="flex flex-row items-center justify-between">
        <h2 className="text-lg font-semibold">Projects & Tasks</h2>
        <Button onClick={() => setShowProjectForm(true)}>
          <PlusCircle className="mr-2 h-4 w-4" />
          New Project
        </Button>
      </CardHeader>
      <CardContent>
        <ProjectTabs
          projects={projects}
          showProjectForm={showProjectForm}
          setShowProjectForm={setShowProjectForm}
          handleProjectCreated={handleProjectCreated}
          handleProjectUpdated={handleProjectUpdated}
          handleProjectDeleted={showDeleteConfirmation}
        />
      </CardContent>

      <DeleteProjectDialog
        isOpen={deleteDialogOpen}
        onOpenChange={setDeleteDialogOpen}
        onCancel={() => setProjectToDelete(null)}
        onConfirm={confirmDeleteProject}
      />
    </Card>
  );
};

import { useState, useEffect } from "react";
import { Card, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { Application } from "@/types/business";
import { ChevronDown, ChevronRight, Bell, CheckCircle, FileText, Loader2 } from "lucide-react";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";
import { useAcceptedJobsCore, AcceptedJob } from "@/hooks/jobs/useAcceptedJobsCore";
import { ExpandedApplicationContent } from "./ExpandedApplicationContent";
import { previewApplicationCV } from "@/utils/setupStorage";

interface ApplicationCardProps {
  application: Application;
  isExpanded: boolean;
  toggleExpand: () => void;
  openAcceptJobDialog: (application: Application) => void;
  handleStatusChange: (applicationId: string, newStatus: string) => Promise<void>;
}

export const ApplicationCard = ({
  application,
  isExpanded,
  toggleExpand,
  openAcceptJobDialog,
  handleStatusChange
}: ApplicationCardProps) => {
  const [acceptedJob, setAcceptedJob] = useState<AcceptedJob | null>(null);
  const [isUpdatingStatus, setIsUpdatingStatus] = useState<string | null>(null);
  const { getAcceptedJob } = useAcceptedJobsCore(() => {
    loadAcceptedJob();
  });

  const loadAcceptedJob = async () => {
    if (application.accepted_business && application.accepted_jobseeker) {
      const jobData = await getAcceptedJob(application.job_app_id);
      setAcceptedJob(jobData);
    }
  };

  useEffect(() => {
    if (isExpanded) {
      loadAcceptedJob();
    }
  }, [isExpanded, application.accepted_business, application.accepted_jobseeker]);

  // Check if CV exists and is from the job_applications bucket
  const hasCvInJobApplicationsBucket = application.cv_url && 
    (application.cv_url.includes('job_applications/') || application.cv_url.includes('job-applications/'));

  // Handle CV download safely
  const handleCvDownload = (e: React.MouseEvent) => {
    e.stopPropagation();
    if (application.cv_url) {
      previewApplicationCV(application.cv_url);
    }
  };

  // Handle status change with loading state
  const onStatusChange = async (e: React.ChangeEvent<HTMLSelectElement>) => {
    e.stopPropagation();
    setIsUpdatingStatus(application.job_app_id);
    try {
      await handleStatusChange(application.job_app_id, e.target.value);
    } finally {
      setIsUpdatingStatus(null);
    }
  };

  return (
    <Card className="shadow-sm hover:shadow transition-shadow">
      <Collapsible open={isExpanded} onOpenChange={toggleExpand}>
        <CardHeader className="p-4 pb-3">
          {/* Two-row grid layout */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-x-4 gap-y-2">
            {/* First row */}
            <div className="flex items-center space-x-2">
              <span className="font-semibold">Applicant:</span>
              <span>{application.profile?.first_name} {application.profile?.last_name}</span>
            </div>

            <div className="flex items-center space-x-2">
              <span className="font-semibold">Role:</span>
              <span>{application.business_roles?.title || "Untitled Role"}</span>
            </div>

            <div className="flex items-center space-x-2">
              <span className="font-semibold">Description:</span>
              <span className="truncate">{application.business_roles?.description || "No description"}</span>
            </div>

            <div className="flex items-center space-x-2">
              <span className="font-semibold">Equity:</span>
              <span>{application.business_roles?.equity_allocation ? `${application.business_roles.equity_allocation}%` : "N/A"}</span>
            </div>

            <div className="flex items-center space-x-2">
              <span className="font-semibold">Status:</span>
              <div className="flex items-center">
                <select 
                  className="px-2 py-1 border rounded text-sm bg-white"
                  value={application.status}
                  onChange={onStatusChange}
                  onClick={(e) => e.stopPropagation()}
                  disabled={isUpdatingStatus === application.job_app_id}
                >
                  <option value="negotiation">Negotiation</option>
                  <option value="accepted">Accepted</option>
                  <option value="rejected">Rejected</option>
                </select>
                {isUpdatingStatus === application.job_app_id && (
                  <Loader2 className="animate-spin ml-2 h-4 w-4" />
                )}
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <span className="font-semibold">Jobseeker Accepted:</span>
              <span>{application.accepted_jobseeker ? "Yes" : "No"}</span>
            </div>

            {/* Only show CV download button if CV exists in job_applications bucket */}
            {hasCvInJobApplicationsBucket && (
              <div className="flex gap-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={handleCvDownload}
                >
                  <FileText className="mr-1 h-4 w-4" />
                  Download Application CV
                </Button>
              </div>
            )}
          </div>

          {/* Second row */}
          <div className="mt-2 grid grid-cols-1 md:grid-cols-2 gap-x-4 gap-y-2">
            <div className="flex items-center space-x-2">
              <span className="font-semibold">Skills Required:</span>
              <div className="flex flex-wrap gap-1">
                {application.business_roles?.skill_requirements?.map((skill, index) => (
                  <Badge key={index} variant="outline" className="bg-slate-50">
                    {typeof skill === 'string' ? skill : skill.skill}
                    {typeof skill !== 'string' && skill.level && 
                      <span className="ml-1 opacity-70">({skill.level})</span>
                    }
                  </Badge>
                ))}
                {(!application.business_roles?.skill_requirements || 
                  application.business_roles.skill_requirements.length === 0) && 
                  <span className="text-muted-foreground">No specific skills required</span>
                }
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <span className="font-semibold">Timeframe:</span>
              <span>{application.business_roles?.timeframe || "Not specified"}</span>
            </div>
          </div>

          <div className="flex justify-between items-center mt-3">
            <CollapsibleTrigger asChild>
              <Button variant="ghost" size="sm" className="flex items-center">
                {isExpanded ? (
                  <>
                    <ChevronDown className="h-4 w-4 mr-1" />
                    Hide Details
                  </>
                ) : (
                  <>
                    <ChevronRight className="h-4 w-4 mr-1" />
                    Show Details
                  </>
                )}
              </Button>
            </CollapsibleTrigger>

            {application.status === 'accepted' && (
              <TooltipProvider>
                <Tooltip>
                  <TooltipTrigger asChild>
                    <Button 
                      size="sm" 
                      variant="outline" 
                      className="h-8 px-2"
                      onClick={(e) => {
                        e.stopPropagation();
                        openAcceptJobDialog(application);
                      }}
                      disabled={application.accepted_business}
                    >
                      {application.accepted_business ? (
                        <CheckCircle className="h-4 w-4 mr-1 text-green-500" />
                      ) : (
                        <Bell className="h-4 w-4 mr-1" />
                      )}
                      {application.accepted_business ? "Accepted" : "Accept Contract"}
                    </Button>
                  </TooltipTrigger>
                  <TooltipContent>
                    {application.accepted_business 
                      ? "You have accepted this job contract" 
                      : "Accept job contract and finalize equity agreement"}
                  </TooltipContent>
                </Tooltip>
              </TooltipProvider>
            )}
          </div>
        </CardHeader>

        <CollapsibleContent>
          <ExpandedApplicationContent 
            application={application}
            acceptedJob={acceptedJob}
            onUpdate={loadAcceptedJob}
          />
        </CollapsibleContent>
      </Collapsible>
    </Card>
  );
};

//  Enhanced ApplicationsTable with Auto Contract Generation - FIXED EQUITY REFERENCES
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader } from '@/components/ui/card';
import { 
  Select, 
  SelectContent, 
  SelectItem, 
  SelectTrigger, 
  SelectValue 
} from '@/components/ui/select';
import { supabase } from '@/lib/supabase';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { Badge } from '@/components/ui/badge';
import { toast } from 'sonner';

interface ApplicationsTableProps {
  status: string;
}

export const ApplicationsTable = ({ status }: ApplicationsTableProps) => {
  const navigate = useNavigate();
  const [searchParams, setSearchParams] = useSearchParams();
  const [projectFilter, setProjectFilter] = useState<string | null>(searchParams.get('project') || null);
  const [projects, setProjects] = useState<any[]>([]);
  const [applicationCount, setApplicationCount] = useState<number>(0);
  const [pendingActionCount, setPendingActionCount] = useState<number>(0);
  const [loading, setLoading] = useState<boolean>(true);

  //  FIXED: Auto-create accepted_jobs when status changes to 'accepted'
  const createAcceptedJobRecord = async (application: any) => {
    try {
      console.log('Creating accepted_jobs record for application:', application.job_app_id);

      // Check if accepted_jobs record already exists
      const { data: existingJob, error: checkError } = await supabase
        .from('accepted_jobs')
        .select('id')
        .eq('job_app_id', application.job_app_id)
        .single();

      if (existingJob) {
        console.log('Accepted job record already exists:', existingJob.id);
        return existingJob;
      }

      //  FIXED: Get task details with correct column names for equity calculation
      // Based on your schema, business_roles contains the task-level equity allocation
      const { data: taskData, error: taskError } = await supabase
        .from('business_roles')
        .select(`
          project_id,
          equity_allocation,
          role_id,
          title,
          description
        `)
        .eq('role_id', application.role_id)
        .single();

      if (taskError) {
        console.error('Error fetching task data:', taskError);
        console.error('Attempted to fetch role_id:', application.role_id);
        throw new Error(`Failed to fetch task equity information: ${taskError.message}`);
      }

      if (!taskData) {
        throw new Error('No task data found for the specified role');
      }

      //  EQUITY FLOW EXPLANATION:
      // 1. business_roles.equity_allocation = Total equity allocated to this specific task/role
      // 2. accepted_jobs.equity_agreed = Agreed equity when job is accepted (copied from business_roles.equity_allocation)
      // 3. accepted_jobs.jobs_equity_allocated = Actually allocated equity as work progresses (starts at 0)

      const taskEquityAllocation = taskData.equity_allocation || 0;

      console.log('Task equity allocation:', taskEquityAllocation);

      //  ENHANCED: Create the accepted_jobs record with proper equity tracking
      const { data: newAcceptedJob, error: createError } = await supabase
        .from('accepted_jobs')
        .insert({
          job_app_id: application.job_app_id,
          user_id: application.user_id,
          business_id: application.business_id,
          project_id: taskData.project_id,
          role_id: application.role_id,
          //  EQUITY AGREED: This is the total equity the job seeker will earn for completing this task
          equity_agreed: taskEquityAllocation,
          //  EQUITY ALLOCATED: This starts at 0 and increases as work is completed
          jobs_equity_allocated: 0,
          date_accepted: new Date().toISOString(),
          status: 'active',
          created_at: new Date().toISOString(),
          updated_at: new Date().toISOString()
        })
        .select()
        .single();

      if (createError) {
        console.error('Error creating accepted job:', createError);
        throw new Error(`Failed to create contract record: ${createError.message}`);
      }

      console.log('Created accepted_jobs record:', newAcceptedJob);

      //  ENHANCED: Update the application to mark both parties as accepted
      const { error: updateError } = await supabase
        .from('job_applications')
        .update({
          accepted_business: true,
          accepted_jobseeker: true,
          updated_at: new Date().toISOString()
        })
        .eq('job_app_id', application.job_app_id);

      if (updateError) {
        console.error('Error updating application acceptance:', updateError);
        throw new Error(`Failed to update application status: ${updateError.message}`);
      }

      //  SUCCESS: Log to system for audit trail
      try {
        await supabase
          .from('system_logs')
          .insert({
            event_type: 'auto_contract_creation',
            description: `Automatically created contract for application ${application.job_app_id}`,
            metadata: {
              job_app_id: application.job_app_id,
              user_id: application.user_id,
              business_id: application.business_id,
              equity_agreed: taskEquityAllocation,
              role_title: taskData.title
            }
          });
      } catch (logError) {
        // Don't fail the main operation if logging fails
        console.warn('Failed to log contract creation:', logError);
      }

      toast.success(`Job accepted! Contract created with ${taskEquityAllocation}% equity allocation.`);
      return newAcceptedJob;

    } catch (error) {
      console.error('Error in createAcceptedJobRecord:', error);
      toast.error(`Failed to process job acceptance: ${error.message}`);
      throw error;
    }
  };

  //  ENHANCED: Monitor for newly accepted applications and auto-process them
  useEffect(() => {
    const monitorAcceptedApplications = async () => {
      if (status !== 'accepted') return;

      try {
        // Get all accepted applications that don't have accepted_jobs records yet
        const { data: acceptedApps, error: fetchError } = await supabase
          .from('job_applications')
          .select(`
            job_app_id,
            user_id,
            role_id,
            status,
            accepted_business,
            accepted_jobseeker,
            business_id,
            project_id
          `)
          .eq('status', 'accepted');

        if (fetchError) {
          console.error('Error fetching accepted applications:', fetchError);
          return;
        }

        if (acceptedApps && acceptedApps.length > 0) {
          console.log(`Found ${acceptedApps.length} accepted applications to check`);

          // Check which ones need accepted_jobs records
          for (const app of acceptedApps) {
            // Check if accepted_jobs record exists
            const { data: existingJob } = await supabase
              .from('accepted_jobs')
              .select('id')
              .eq('job_app_id', app.job_app_id)
              .single();

            if (!existingJob && (!app.accepted_business || !app.accepted_jobseeker)) {
              console.log('Auto-processing accepted application:', app.job_app_id);
              await createAcceptedJobRecord(app);
            }
          }

          // Refresh the count after processing
          setTimeout(() => {
            fetchApplicationCount();
          }, 1000);
        }
      } catch (error) {
        console.error('Error monitoring accepted applications:', error);
      }
    };

    monitorAcceptedApplications();
  }, [status]);

  // Fetch available projects for filtering
  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const { data: businessProjects, error } = await supabase
          .from('business_projects')
          .select('project_id, title');

        if (error) {
          console.error('Error fetching projects for filtering:', error);
          return;
        }

        setProjects(businessProjects || []);
      } catch (error) {
        console.error('Error in project fetching:', error);
      }
    };

    fetchProjects();
  }, []);

  //  ENHANCED: Fetch application count with better error handling
  const fetchApplicationCount = async () => {
    setLoading(true);
    try {
      // Base query builder
      let query = supabase
        .from('job_applications')
        .select('job_app_id, status, accepted_business, accepted_jobseeker', { count: 'exact' })
        .eq('status', status);

      // Add project filter if selected
      if (projectFilter) {
        query = query.eq('project_id', projectFilter);
      }

      // Execute the query
      const { data, count, error } = await query;

      if (error) {
        console.error('Error fetching application count:', error);
        return;
      }

      setApplicationCount(count || 0);

      //  ENHANCED: Count applications that need processing
      if (status === 'accepted') {
        // Count applications that are accepted but don't have both party acceptances
        const needsProcessing = data?.filter(app => 
          !app.accepted_business || !app.accepted_jobseeker
        ).length || 0;

        setPendingActionCount(needsProcessing);
      } else if (status === 'pending') {
        // For pending, all applications need attention
        setPendingActionCount(count || 0);
      } else {
        setPendingActionCount(0);
      }
    } catch (error) {
      console.error('Error counting applications:', error);
      toast.error('Failed to load application count');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchApplicationCount();
  }, [status, projectFilter]);

  // Update URL params when project filter changes
  const handleProjectFilterChange = (value: string) => {
    setProjectFilter(value === 'all' ? null : value);

    // Update URL search params
    if (value === 'all') {
      searchParams.delete('project');
    } else {
      searchParams.set('project', value);
    }
    setSearchParams(searchParams);
  };

  // Navigate to applications list with filter
  const handleViewApplications = () => {
    let url = `/business/dashboard?tab=applications&status=${status}`;
    if (projectFilter) {
      url += `&project=${projectFilter}`;
    }
    navigate(url);
  };

  //  ENHANCED: Better status descriptions with equity context
  const getStatusInfo = () => {
    switch (status) {
      case 'pending':
        return {
          title: 'Pending Applications',
          description: 'Applications awaiting your review and decision'
        };
      case 'accepted':
        return {
          title: 'Accepted Applications', 
          description: 'Applications accepted by business. Contract generation with equity allocation happens automatically when both parties agree.'
        };
      case 'active':
        return {
          title: 'Active Contracts',
          description: 'Live contracts with ongoing work and equity allocation in progress.'
        };
      case 'completed':
        return {
          title: 'Completed Contracts',
          description: 'Finished contracts where all agreed equity has been allocated.'
        };
      default:
        return {
          title: 'Applications',
          description: 'View and manage job applications'
        };
    }
  };

  const statusInfo = getStatusInfo();

  return (
    <Card>
      <CardHeader className="flex flex-row items-center justify-between">
        <div>
          <div className="text-lg font-semibold mb-1">
            {statusInfo.title}
            <Badge variant="secondary" className="ml-2">
              {applicationCount}
              {pendingActionCount > 0 && (
                <span className="ml-1 text-amber-600 font-bold">
                  {status === 'accepted' 
                    ? `(${pendingActionCount} auto-processing)` 
                    : `(${pendingActionCount} need attention)`
                  }
                </span>
              )}
            </Badge>
          </div>
          <p className="text-sm text-muted-foreground">
            {statusInfo.description}
          </p>
        </div>
        <div className="w-[200px]">
          <Select 
            value={projectFilter || 'all'} 
            onValueChange={handleProjectFilterChange}
          >
            <SelectTrigger>
              <SelectValue placeholder="Filter by project" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Projects</SelectItem>
              {projects.map((project) => (
                <SelectItem key={project.project_id} value={project.project_id}>
                  {project.title}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </CardHeader>
      <CardContent className="p-4">
        {loading ? (
          <div className="text-center py-4">
            <p className="text-muted-foreground">Loading applications...</p>
          </div>
        ) : applicationCount === 0 ? (
          <div className="text-center py-4">
            <p className="text-muted-foreground">
              No {status} applications found{projectFilter && ' for this project'}
            </p>
          </div>
        ) : (
          <div className="text-center py-4">
            <p className="text-muted-foreground">
              <button 
                onClick={handleViewApplications}
                className="text-blue-600 hover:underline"
              >
                View {applicationCount} {status} application{applicationCount !== 1 ? 's' : ''}
                {pendingActionCount > 0 && (
                  status === 'accepted' 
                    ? ` (${pendingActionCount} being processed)`
                    : ` (${pendingActionCount} need attention)`
                )}
              </button>
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { ContractUploadDialog } from "./ContractUploadDialog";
import { useContractManagement } from "@/hooks/jobs/useContractManagement";
import { Application } from "@/types/business";
import { AcceptedJob } from "@/hooks/useAcceptedJobs";
import { Badge } from "@/components/ui/badge";
import { ArrowUpRight, Download, FileText, Upload, FileCheck } from "lucide-react";
import { toast } from "sonner";
import { useWorkContractManagement } from "@/hooks/useWorkContractManagement";
import { useAwardAgreementManagement } from "@/hooks/useAwardAgreementManagement";
import { DocumentViewer } from "@/components/documents/DocumentViewer";
import { ContractSignatureDialog } from "@/components/documents/ContractSignatureDialog";

interface ContractActionsSectionProps {
  application: Application;
  acceptedJob: AcceptedJob | null;
  onUpdate: () => void;
}

export const ContractActionsSection = ({
  application,
  acceptedJob,
  onUpdate
}: ContractActionsSectionProps) => {
  const [isUploadDialogOpen, setIsUploadDialogOpen] = useState(false);
  const [activeTab, setActiveTab] = useState<'upload' | 'contract' | 'award'>('upload');
  const [workContractDoc, setWorkContractDoc] = useState<any>(null);
  const [awardAgreementDoc, setAwardAgreementDoc] = useState<any>(null);
  const [isSignatureDialogOpen, setIsSignatureDialogOpen] = useState(false);
  const [activeDocumentId, setActiveDocumentId] = useState<string | null>(null);
  const [activeDocumentType, setActiveDocumentType] = useState<'work_contract' | 'award_agreement'>('work_contract');

  const { isUploading, uploadContract } = useContractManagement(onUpdate);

  // Only initialize these hooks if the dependencies exist
  const workContractHook = useWorkContractManagement ? useWorkContractManagement() : {
    isGenerating: false,
    isLoadingDocuments: false,
    generateWorkContract: async () => {},
    getWorkContract: async () => null
  };

  const awardAgreementHook = useAwardAgreementManagement ? useAwardAgreementManagement() : {
    isGenerating: false,
    isLoadingDocuments: false,
    generateAwardAgreement: async () => {},
    getAwardAgreement: async () => null
  };

  const { 
    isGenerating: isGeneratingWorkContract, 
    isLoadingDocuments: isLoadingWorkContract,
    generateWorkContract,
    getWorkContract
  } = workContractHook;

  const {
    isGenerating: isGeneratingAwardAgreement,
    isLoadingDocuments: isLoadingAwardAgreement,
    generateAwardAgreement,
    getAwardAgreement
  } = awardAgreementHook;

  // Only show contract actions if both parties have accepted
  if (!application.accepted_business || !application.accepted_jobseeker || !acceptedJob) {
    return (
      <Card className="mt-4">
        <CardHeader className="pb-3">
          <CardTitle className="text-base flex items-center">
            <FileText className="h-4 w-4 mr-2" />
            Contract Management
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-4">
            <p className="text-muted-foreground">
              Contract management will be available once both parties have accepted this application.
            </p>
            <div className="mt-2 text-sm">
              <p>Business accepted: {application.accepted_business ? '' : ''}</p>
              <p>Job seeker accepted: {application.accepted_jobseeker ? '' : ''}</p>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  const handleUploadContract = async (jobAppId: string, file: File, notes: string) => {
    try {
      return await uploadContract(jobAppId, file);
    } catch (error) {
      console.error('Error uploading contract:', error);
      toast.error("Failed to upload contract");
      throw error;
    }
  };

  const loadDocuments = async () => {
    if (!acceptedJob?.id) return;

    try {
      // Only try to load documents if the functions exist
      if (getWorkContract) {
        const workContract = await getWorkContract(acceptedJob.id);
        setWorkContractDoc(workContract);
      }

      if (getAwardAgreement) {
        const awardAgreement = await getAwardAgreement(acceptedJob.id);
        setAwardAgreementDoc(awardAgreement);
      }
    } catch (error) {
      console.error("Error loading documents:", error);
      // Don't show error to user for missing functionality
    }
  };

  const handleCreateWorkContract = async () => {
    if (!acceptedJob?.id || !application.job_app_id || !generateWorkContract) return;

    try {
      // Get the business ID from the application
      const businessId = application.businesses?.businesses_id || '';
      const userId = application.user_id || '';
      const projectId = application.project_id || '';

      await generateWorkContract(
        acceptedJob.id,
        businessId,
        userId,
        projectId,
        application.job_app_id
      );

      toast.success("Work contract created");
      loadDocuments();
      setActiveTab('contract');
    } catch (error) {
      console.error("Error generating work contract:", error);
      toast.error("Failed to create work contract");
    }
  };

  const handleCreateAwardAgreement = async () => {
    if (!acceptedJob?.id || !application.job_app_id || !generateAwardAgreement) return;

    try {
      // Get the business ID from the application
      const businessId = application.businesses?.businesses_id || '';
      const userId = application.user_id || '';
      const projectId = application.project_id || '';

      await generateAwardAgreement(
        acceptedJob.id,
        businessId,
        userId,
        projectId,
        application.job_app_id,
        "completed the agreed services and milestones"
      );

      toast.success("Award agreement created");
      loadDocuments();
      setActiveTab('award');
    } catch (error) {
      console.error("Error generating award agreement:", error);
      toast.error("Failed to create award agreement");
    }
  };

  const handleSignDocument = (docId: string, docType: 'work_contract' | 'award_agreement') => {
    setActiveDocumentId(docId);
    setActiveDocumentType(docType);
    setIsSignatureDialogOpen(true);
  };

  const handleTabChange = (tab: 'upload' | 'contract' | 'award') => {
    setActiveTab(tab);
    loadDocuments();
  };

  const handleDocumentSigned = () => {
    loadDocuments();
    onUpdate();
  };

  return (
    <Card className="mt-4">
      <CardHeader className="pb-3">
        <CardTitle className="text-base flex items-center">
          <FileText className="h-4 w-4 mr-2" />
          Contract Management
        </CardTitle>

        <div className="flex space-x-1 rounded-lg bg-muted p-1 text-muted-foreground mt-2">
          <button
            onClick={() => handleTabChange('upload')}
            className={`flex-1 justify-center rounded-md px-3 py-1.5 text-sm font-medium ${
              activeTab === 'upload'
                ? 'bg-background text-foreground shadow-sm'
                : 'hover:bg-background/50'
            }`}
          >
            Upload
          </button>
          <button
            onClick={() => handleTabChange('contract')}
            className={`flex-1 justify-center rounded-md px-3 py-1.5 text-sm font-medium ${
              activeTab === 'contract'
                ? 'bg-background text-foreground shadow-sm'
                : 'hover:bg-background/50'
            }`}
          >
            Work Contract
          </button>
          <button
            onClick={() => handleTabChange('award')}
            className={`flex-1 justify-center rounded-md px-3 py-1.5 text-sm font-medium ${
              activeTab === 'award'
                ? 'bg-background text-foreground shadow-sm'
                : 'hover:bg-background/50'
            }`}
          >
            Award Agreement
          </button>
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        {activeTab === 'upload' && (
          <>
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium">External Contract</p>
                <p className="text-sm text-muted-foreground">
                  {acceptedJob?.document_url 
                    ? "External contract uploaded" 
                    : "Pending external contract upload"}
                </p>
              </div>
              <Badge
                variant={acceptedJob?.document_url ? "default" : "outline"}
                className={acceptedJob?.document_url 
                  ? "bg-green-100 text-green-800 border-green-300" 
                  : ""}
              >
                {acceptedJob?.document_url ? "Uploaded" : "Pending"}
              </Badge>
            </div>

            {acceptedJob?.document_url ? (
              <a 
                href={acceptedJob.document_url} 
                target="_blank" 
                rel="noopener noreferrer"
                className="block w-full"
              >
                <Button variant="outline" className="w-full flex justify-center items-center">
                  <Download className="h-4 w-4 mr-2" />
                  View/Download External Contract
                  <ArrowUpRight className="h-3 w-3 ml-1" />
                </Button>
              </a>
            ) : (
              <Button 
                onClick={() => setIsUploadDialogOpen(true)}
                className="w-full"
              >
                <Upload className="h-4 w-4 mr-2" />
                Upload External Contract Document
              </Button>
            )}
          </>
        )}

        {activeTab === 'contract' && (
          <>
            {workContractDoc ? (
              <DocumentViewer 
                documentId={workContractDoc.id}
                documentType="work_contract"
                documentTitle="Equity Work Contract"
                documentContent={workContractDoc.content}
                documentStatus={workContractDoc.status}
                onSign={workContractDoc.status === 'final' ? 
                  () => handleSignDocument(workContractDoc.id, 'work_contract') : undefined}
              />
            ) : (
              <div className="text-center p-6 border rounded-md">
                {isLoadingWorkContract || isGeneratingWorkContract ? (
                  <p>Loading work contract...</p>
                ) : (
                  <>
                    <p className="mb-4">No work contract has been created yet.</p>
                    {generateWorkContract ? (
                      <Button onClick={handleCreateWorkContract}>
                        <FileCheck className="h-4 w-4 mr-2" />
                        Generate Work Contract
                      </Button>
                    ) : (
                      <p className="text-sm text-muted-foreground">
                        Work contract generation is not available.
                      </p>
                    )}
                  </>
                )}
              </div>
            )}
          </>
        )}

        {activeTab === 'award' && (
          <>
            {awardAgreementDoc ? (
              <DocumentViewer 
                documentId={awardAgreementDoc.id}
                documentType="award_agreement"
                documentTitle="Equity Award Agreement"
                documentContent={awardAgreementDoc.content}
                documentStatus={awardAgreementDoc.status}
                onSign={awardAgreementDoc.status === 'final' ? 
                  () => handleSignDocument(awardAgreementDoc.id, 'award_agreement') : undefined}
              />
            ) : (
              <div className="text-center p-6 border rounded-md">
                {isLoadingAwardAgreement || isGeneratingAwardAgreement ? (
                  <p>Loading award agreement...</p>
                ) : (
                  <>
                    <p className="mb-4">No award agreement has been created yet.</p>
                    {workContractDoc && generateAwardAgreement ? (
                      <Button onClick={handleCreateAwardAgreement}>
                        <FileCheck className="h-4 w-4 mr-2" />
                        Generate Award Agreement
                      </Button>
                    ) : generateAwardAgreement ? (
                      <p className="text-sm text-muted-foreground">
                        You need to create a work contract first before generating an award agreement.
                      </p>
                    ) : (
                      <p className="text-sm text-muted-foreground">
                        Award agreement generation is not available.
                      </p>
                    )}
                  </>
                )}
              </div>
            )}
          </>
        )}

        {/* Contract Upload Dialog */}
        <ContractUploadDialog
          isOpen={isUploadDialogOpen}
          onOpenChange={setIsUploadDialogOpen}
          application={application}
          onUpload={handleUploadContract}
          isUploading={isUploading}
        />

        {/* Contract Signature Dialog */}
        {activeDocumentId && acceptedJob && (
          <ContractSignatureDialog
            open={isSignatureDialogOpen}
            onOpenChange={setIsSignatureDialogOpen}
            documentId={activeDocumentId}
            documentType={activeDocumentType}
            acceptedJobId={acceptedJob.id}
            onSigned={handleDocumentSigned}
          />
        )}
      </CardContent>
    </Card>
  );
};

