import { Link } from "react-router-dom";

interface Task {
  task_id: string; // Changed to task_id for consistency
  id?: string; // Add id as optional for backward compatibility
  title: string;
  description: string;
  status: string;
  hours_logged: number;
  equity_earned: number;
  equity_allocation: number;
  timeframe: string;
  skills_required: string[];
  skill_requirements: any[];
  dependencies: string[];
}

interface Project {
  project_id: string;
  title: string;
  description: string;
  status: string;
  equity_allocation: number;
  skills_required: string[];
  project_timeframe: string;
  tasks: Task[];
}

interface ProjectCardProps {
  project: Project;
  onEdit: (project: Project) => void;
  onDelete: (projectId: string) => void;
}

export const ProjectCard = ({ project, onEdit, onDelete }: ProjectCardProps) => {
  const handleTaskDeleted = (taskId: string) => {
    // This will be handled by the parent's onProjectUpdated
    const updatedTasks = project.tasks.filter(task => task.task_id !== taskId);
    onEdit({ ...project, tasks: updatedTasks });
  };

  const handleTaskUpdated = (updatedTask: Task) => {
    const updatedTasks = project.tasks.map(task => 
      task.task_id === updatedTask.task_id ? updatedTask : task
    );
    onEdit({ ...project, tasks: updatedTasks });
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex justify-between items-start">
          <div>
            <h3 className="text-lg font-medium">
              <Link 
                to={`/projects/${project.project_id}`}
                className="text-blue-600 hover:underline hover:text-blue-800"
              >
                {project.title}
              </Link>
            </h3>
            <p className="text-sm text-muted-foreground">{project.description}</p>
          </div>
          <div className="flex gap-2">
            <Button variant="outline" size="sm" onClick={() => onEdit(project)}>
              <Edit className="h-4 w-4" />
            </Button>
            <Button 
              variant="outline" 
              size="sm" 
              onClick={() => onDelete(project.project_id)}
              className="text-destructive hover:text-destructive"
            >
              <Trash className="h-4 w-4" />
            </Button>
          </div>
        </div>
        <div className="mt-2">
          <div className="flex justify-between items-center">
            <span className="text-sm font-medium">Total Equity: {project.equity_allocation}%</span>
            <span className="text-sm text-muted-foreground">Timeframe: {project.project_timeframe}</span>
          </div>
          <div className="mt-2">
            <p className="text-sm font-medium">Required Skills:</p>
            <div className="flex flex-wrap gap-2 mt-1">
              {project.skills_required.map(skill => (
                <span key={skill} className="px-2 py-1 bg-secondary rounded-full text-xs">
                  {skill}
                </span>
              ))}
            </div>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        <TaskList 
          projectId={project.project_id}
          tasks={project.tasks}
          onTaskDeleted={handleTaskDeleted}
          onTaskUpdated={handleTaskUpdated}
          availableSkills={project.skills_required}
          totalEquity={project.equity_allocation}
        />
      </CardContent>
    </Card>
  );
};

import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { supabase } from "@/lib/supabase";
import { toast } from "sonner";
import { SkillsInput } from "./SkillsInput";

interface Project {
  title: string;
  description: string;
  equity_allocation: number;
  skills_required: string[];
  project_timeframe: string;
}

interface ProjectFormProps {
  initialData?: Project;
  onSubmit?: (data: Project) => void;
  onProjectCreated?: (project: any) => void;
  submitLabel?: string;
}

export const ProjectForm = ({ 
  initialData,
  onSubmit,
  onProjectCreated,
  submitLabel = "Create Project"
}: ProjectFormProps) => {
  const [project, setProject] = useState<Project>({
    title: "",
    description: "",
    equity_allocation: 0,
    skills_required: [],
    project_timeframe: ""
  });

  useEffect(() => {
    if (initialData) {
      setProject(initialData);
    }
  }, [initialData]);

  const handleSubmit = async () => {
    try {
      if (!project.title || !project.description || !project.project_timeframe) {
        toast.error("Please fill in all required fields");
        return;
      }

      if (project.skills_required.length === 0) {
        toast.error("At least one required skill must be specified");
        return;
      }

      if (onSubmit) {
        onSubmit(project);
        return;
      }

      const { data: { session } } = await supabase.auth.getSession();
      if (!session) {
        toast.error("No active session found");
        return;
      }

      const { data, error } = await supabase
        .from('business_projects')
        .insert({
          ...project,
          created_by: session.user.id,
          business_id: session.user.id
        })
        .select()
        .single();

      if (error) throw error;

      if (onProjectCreated) {
        onProjectCreated(data);
      }

      setProject({
        title: "",
        description: "",
        equity_allocation: 0,
        skills_required: [],
        project_timeframe: ""
      });

      toast.success("Project created successfully");
    } catch (error) {
      console.error('Error with project:', error);
      toast.error("Failed to handle project");
    }
  };

  return (
    <div className="space-y-4">
      <div>
        <Label htmlFor="project-title">Project Title *</Label>
        <Input
          id="project-title"
          required
          value={project.title}
          onChange={e => setProject(prev => ({ ...prev, title: e.target.value }))}
        />
      </div>
      <div>
        <Label htmlFor="project-description">Description *</Label>
        <Textarea
          id="project-description"
          required
          value={project.description}
          onChange={e => setProject(prev => ({ ...prev, description: e.target.value }))}
        />
      </div>
      <div>
        <Label htmlFor="project-timeframe">Project Timeframe *</Label>
        <Input
          id="project-timeframe"
          required
          value={project.project_timeframe}
          onChange={e => setProject(prev => ({ ...prev, project_timeframe: e.target.value }))}
          placeholder="e.g., 3 months, Q4 2024"
        />
      </div>
      <div>
        <Label htmlFor="equity-allocation">Total Equity Allocation (%) *</Label>
        <Input
          id="equity-allocation"
          type="number"
          min="0"
          max="100"
          required
          value={project.equity_allocation}
          onChange={e => setProject(prev => ({ ...prev, equity_allocation: parseFloat(e.target.value) }))}
        />
      </div>
      <SkillsInput
        skills={project.skills_required}
        onChange={skills => setProject(prev => ({ ...prev, skills_required: skills }))}
        helperText="These skills will be broken down into specific requirements in sub-tasks after project creation."
      />
      <Button onClick={handleSubmit} className="w-full">{submitLabel}</Button>
    </div>
  );
};

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { toast } from "sonner";
import { supabase } from "@/lib/supabase";

interface SkillRequirement {
  skill: string;
  level: string;
}

interface Task {
  task_id: string;  // Changed from id to task_id to match the database schema
  title: string;
  description: string;
  status: string;
  hours_logged: number;
  equity_earned: number;
  equity_allocation: number;
  timeframe: string;
  skill_requirements: SkillRequirement[];
  dependencies: string[];
}

interface SubTaskFormProps {
  projectId: string;
  availableSkills: string[];
  totalEquity: number;
  currentTotalTaskEquity: number;
  onTaskCreated: (task: any) => void;
  onCancel: () => void;
  initialData?: Task;
}

export const SubTaskForm = ({ 
  projectId, 
  availableSkills, 
  totalEquity,
  currentTotalTaskEquity,
  onTaskCreated, 
  onCancel,
  initialData 
}: SubTaskFormProps) => {
  const [task, setTask] = useState({
    title: initialData?.title || "",
    description: initialData?.description || "",
    timeframe: initialData?.timeframe || "",
    equity_allocation: initialData?.equity_allocation || 0,
    skill_requirements: initialData?.skill_requirements || [],
    dependencies: initialData?.dependencies || []
  });

  const [selectedSkill, setSelectedSkill] = useState("");
  const [skillLevel, setSkillLevel] = useState("beginner");

  const handleAddSkill = () => {
    if (!selectedSkill) return;

    setTask(prev => ({
      ...prev,
      skill_requirements: [
        ...prev.skill_requirements,
        { skill: selectedSkill, level: skillLevel }
      ]
    }));

    setSelectedSkill("");
    setSkillLevel("beginner");
  };

  const handleSubmit = async () => {
    try {
      if (!task.title || !task.timeframe || task.equity_allocation <= 0) {
        toast.error("Please fill in all required fields");
        return;
      }

      if (task.skill_requirements.length === 0) {
        toast.error("At least one required skill must be specified");
        return;
      }

      const availableEquity = initialData 
        ? totalEquity - currentTotalTaskEquity + initialData.equity_allocation
        : totalEquity - currentTotalTaskEquity;

      if (task.equity_allocation > availableEquity) {
        toast.error(`Equity allocation cannot exceed remaining equity (${availableEquity}%)`);
        return;
      }

      if (initialData) {
        // For updating, use task_id instead of id
        console.log("Updating task with task_id:", initialData.task_id);

        const { data, error } = await supabase
          .from('project_sub_tasks')
          .update({
            ...task,
            project_id: projectId
          })
          .eq('task_id', initialData.task_id)  // Changed from id to task_id          .select()
          .select()
          .single();

        if (error) {
          console.error('Error updating task:', error);
          throw error;
        }
        onTaskCreated(data);
      } else {
        const { data, error } = await supabase
          .from('project_sub_tasks')
          .insert({
            ...task,
            project_id: projectId
          })
          .select()
          .single();

        if (error) {
          console.error('Error creating task:', error);
          throw error;
        }
        onTaskCreated(data);
      }

      toast.success(`Task ${initialData ? 'updated' : 'created'} successfully`);
    } catch (error) {
      console.error('Error with task:', error);
      toast.error(`Failed to ${initialData ? 'update' : 'create'} task`);
    }
  };

  const handleRemoveSkill = (index: number) => {
    setTask(prev => ({
      ...prev,
      skill_requirements: prev.skill_requirements.filter((_, i) => i !== index)
    }));
  };

  return (
    <div className="space-y-4">
      <div>
        <Label htmlFor="task-title">Task Title *</Label>
        <Input
          id="task-title"
          required
          value={task.title}
          onChange={e => setTask(prev => ({ ...prev, title: e.target.value }))}
        />
      </div>

      <div>
        <Label htmlFor="task-description">Description</Label>
        <Textarea
          id="task-description"
          value={task.description}
          onChange={e => setTask(prev => ({ ...prev, description: e.target.value }))}
        />
      </div>

      <div>
        <Label htmlFor="task-timeframe">Timeframe *</Label>
        <Input
          id="task-timeframe"
          required
          value={task.timeframe}
          onChange={e => setTask(prev => ({ ...prev, timeframe: e.target.value }))}
          placeholder="e.g., 2 weeks, 1 month"
        />
      </div>

      <div>
        <Label htmlFor="equity-allocation">Equity Allocation (%) *</Label>
        <div className="space-y-1">
          <Input
            id="equity-allocation"
            type="number"
            min="0"
            max={totalEquity - currentTotalTaskEquity}
            required
            value={task.equity_allocation}
            onChange={e => setTask(prev => ({ ...prev, equity_allocation: parseFloat(e.target.value) }))}
          />
          <p className="text-xs text-muted-foreground">
            Remaining available equity: {totalEquity - currentTotalTaskEquity}%
          </p>
        </div>
      </div>

      <div className="space-y-2">
        <Label>Required Skills *</Label>
        <div className="flex gap-2">
          <Select value={selectedSkill} onValueChange={setSelectedSkill}>
            <SelectTrigger className="w-[200px]">
              <SelectValue placeholder="Select skill" />
            </SelectTrigger>
            <SelectContent>
              {availableSkills.map(skill => (
                <SelectItem key={skill} value={skill}>
                  {skill}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>

          <Select value={skillLevel} onValueChange={setSkillLevel}>
            <SelectTrigger className="w-[140px]">
              <SelectValue placeholder="Select level" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="beginner">Beginner</SelectItem>
              <SelectItem value="intermediate">Intermediate</SelectItem>
              <SelectItem value="expert">Expert</SelectItem>
            </SelectContent>
          </Select>

          <Button type="button" onClick={handleAddSkill}>Add Skill</Button>
        </div>

        <div className="flex flex-wrap gap-2 mt-2">
          {task.skill_requirements.map((skill, index) => (
            <span
              key={index}
              className="px-2 py-1 bg-secondary rounded-full text-xs flex items-center gap-1"
            >
              {skill.skill} - {skill.level}
              <button
                onClick={() => handleRemoveSkill(index)}
                className="hover:text-destructive"
              >

              </button>
            </span>
          ))}
        </div>
      </div>

      <div className="flex justify-end gap-2 mt-6">
        <Button variant="outline" onClick={onCancel}>
          Cancel
        </Button>
        <Button onClick={handleSubmit}>
          {initialData ? 'Update Task' : 'Create Task'}
        </Button>
      </div>
    </div>
  );
};

import { useState } from "react";
import { Clock, Edit, Trash } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { SubTaskForm } from "./SubTaskForm";
import { supabase } from "@/lib/supabase";
import { toast } from "sonner";

interface Task {
  task_id: string;
  id?: string; // For backward compatibility
  title: string;
  description: string;
  status: string;
  hours_logged: number;
  equity_earned: number;
  equity_allocation: number;
  timeframe: string;
  skills_required: string[]; // Made required to match other interfaces
  skill_requirements: any[];
  dependencies: string[];
}

interface TaskListProps {
  projectId: string;
  tasks: Task[];
  onTaskDeleted: (taskId: string) => void;
  onTaskUpdated: (task: Task) => void;
  availableSkills: string[];
  totalEquity: number;
}

export const TaskList = ({ 
  projectId,
  tasks,
  onTaskDeleted,
  onTaskUpdated,
  availableSkills,
  totalEquity
}: TaskListProps) => {
  const [editingTask, setEditingTask] = useState<Task | null>(null);

  const handleDeleteTask = async (taskId: string) => {
    try {
      const { error } = await supabase
        .from('project_sub_tasks')
        .delete()
        .eq('task_id', taskId);

      if (error) throw error;

      onTaskDeleted(taskId);
      toast.success("Task deleted successfully");
    } catch (error) {
      console.error('Error deleting task:', error);
      toast.error("Failed to delete task");
    }
  };

  const currentTotalTaskEquity = tasks.reduce(
    (sum, task) => sum + (task.equity_allocation || 0), 
    0
  );

  return (
    <>
      <div className="space-y-4">
        {tasks.map(task => (
          <div key={task.task_id} className="border p-4 rounded-lg">
            <div className="flex justify-between items-start">
              <div>
                <h4 className="font-medium">{task.title}</h4>
                <p className="text-sm text-muted-foreground">{task.description}</p>
                <div className="mt-2">
                  <p className="text-sm font-medium">Required Skills:</p>
                  <div className="flex flex-wrap gap-2 mt-1">
                    {task.skill_requirements.map((skillReq: any, index: number) => (
                      <span key={index} className="px-2 py-1 bg-secondary rounded-full text-xs">
                        {skillReq.skill} - {skillReq.level}
                      </span>
                    ))}
                  </div>
                </div>
                {task.dependencies.length > 0 && (
                  <div className="mt-2">
                    <p className="text-sm font-medium">Dependencies:</p>
                    <div className="flex flex-wrap gap-2 mt-1">
                      {task.dependencies.map(dep => (
                        <span key={dep} className="px-2 py-1 bg-secondary rounded-full text-xs">
                          {dep}
                        </span>
                      ))}
                    </div>
                  </div>
                )}
              </div>
              <div>
                <div className="flex gap-2">
                  <Button variant="outline" size="sm" onClick={() => setEditingTask(task)}>
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button 
                    variant="outline" 
                    size="sm" 
                    onClick={() => handleDeleteTask(task.task_id)}
                    className="text-destructive hover:text-destructive"
                  >
                    <Trash className="h-4 w-4" />
                  </Button>
                </div>
                <div className="text-right mt-2">
                  <div className="flex items-center space-x-2 justify-end">
                    <Clock className="h-4 w-4" />
                    <span>Due: {task.timeframe}</span>
                  </div>
                  <div className="text-sm text-muted-foreground">
                    Equity allocated: {task.equity_allocation}%
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      <Dialog open={!!editingTask} onOpenChange={() => setEditingTask(null)}>
        <DialogContent className="max-w-3xl">
          <DialogHeader>
            <DialogTitle>Edit Task</DialogTitle>
          </DialogHeader>
          {editingTask && (
            <SubTaskForm
              projectId={projectId}
              availableSkills={availableSkills}
              totalEquity={totalEquity}
              currentTotalTaskEquity={currentTotalTaskEquity - editingTask.equity_allocation}
              onTaskCreated={onTaskUpdated}
              onCancel={() => setEditingTask(null)}
              initialData={editingTask}
            />
          )}
        </DialogContent>
      </Dialog>
    </>
  );
};

