                          {tasksData.find(t => t.task_id === app.task_id)?.title || 'Unknown task'}
                        </TableCell>
                        <TableCell>
                          {new Date(app.applied_at).toLocaleDateString()}
                        </TableCell>
                        <TableCell>
                          <Badge className={
                            app.status === 'accepted' ? 'bg-green-500' : 
                            app.status === 'pending' ? 'bg-yellow-500' : 
                            app.status === 'rejected' ? 'bg-red-500' : 
                            'bg-gray-500'
                          }>
                            {app.status}
                          </Badge>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              )}
            </TabsContent>

            <TabsContent value="tasks">
              {tasksData.length === 0 ? (
                <div className="text-center py-8">
                  <p className="text-muted-foreground">No tasks for this project.</p>
                </div>
              ) : (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Task</TableHead>
                      <TableHead>Description</TableHead>
                      <TableHead>Timeframe</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Equity</TableHead>
                      <TableHead>Completion</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {tasksData.map(task => (
                      <TableRow key={task.task_id}>
                        <TableCell className="font-medium">{task.title}</TableCell>
                        <TableCell className="max-w-xs truncate">{task.description}</TableCell>
                        <TableCell>{task.timeframe}</TableCell>
                        <TableCell>
                          <Badge className={
                            task.status === 'completed' ? 'bg-green-500' : 
                            task.status === 'in_progress' ? 'bg-blue-500' : 
                            task.status === 'open' ? 'bg-yellow-500' : 
                            'bg-gray-500'
                          }>
                            {task.status}
                          </Badge>
                        </TableCell>
                        <TableCell>{task.equity_allocation}%</TableCell>
                        <TableCell>{task.completion_percentage}%</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              )}
            </TabsContent>

            <TabsContent value="kanban">
              <KanbanBoard projectId={selectedProject} />
            </TabsContent>

            <TabsContent value="gantt">
              <GanttChartView projectId={selectedProject} tasks={ganttTasks} />
            </TabsContent>
          </Tabs>
        )}
      </CardContent>
    </Card>
  );
}

import { useState, useEffect } from "react";
import { supabase } from "@/lib/supabase";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Clock, PauseCircle, PlayCircle, User } from "lucide-react";
import { toast } from "sonner";
import { Textarea } from "@/components/ui/textarea";
import { Separator } from "@/components/ui/separator";
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { format } from "date-fns";
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { 
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/ui/accordion";
import { Slider } from "@/components/ui/slider";

export const TimeTracker = () => {
  const [isTracking, setIsTracking] = useState(false);
  const [startTime, setStartTime] = useState<Date | null>(null);
  const [timerInterval, setTimerInterval] = useState<NodeJS.Timeout | null>(null);
  const [elapsedTime, setElapsedTime] = useState("00:00:00");
  const [description, setDescription] = useState("");
  const [timeEntries, setTimeEntries] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [ticketId, setTicketId] = useState<string | null>(null);
  const [projectId, setProjectId] = useState<string | null>(null);
  const [taskId, setTaskId] = useState<string | null>(null);

  const [projects, setProjects] = useState<any[]>([]);
  const [tasks, setTasks] = useState<any[]>([]);
  const [tickets, setTickets] = useState<any[]>([]);
  const [loadingProjects, setLoadingProjects] = useState(true);
  const [loadingTasks, setLoadingTasks] = useState(false);
  const [loadingTickets, setLoadingTickets] = useState(false);
  const [selectedProject, setSelectedProject] = useState<string | null>(null);
  const [selectedTask, setSelectedTask] = useState<string | null>(null);
  const [selectedTicket, setSelectedTicket] = useState<string | null>(null);

  useEffect(() => {
    fetchProjects();

    return () => {
      if (timerInterval) {
        clearInterval(timerInterval);
      }
    };
  }, []);

  useEffect(() => {
    if (selectedProject) {
      fetchTasks(selectedProject);
      setSelectedTask(null);
      setSelectedTicket(null);
    }
  }, [selectedProject]);

  useEffect(() => {
    if (selectedTask) {
      fetchTickets(selectedTask);
      setSelectedTicket(null);
    }
  }, [selectedTask]);

  useEffect(() => {
    if (selectedTicket) {
      setTicketId(selectedTicket);
      fetchTimeEntries(selectedTicket);
    }
  }, [selectedTicket]);

  const fetchProjects = async () => {
    setLoadingProjects(true);
    try {
      const { data: { session } } = await supabase.auth.getSession();
      if (!session) return;

      const { data, error } = await supabase
        .from('business_projects')
        .select(`
          *,
          businesses (company_name)
        `)
        .eq('business_id', session.user.id);

      if (error) throw error;

      setProjects(data || []);
      if (data && data.length > 0) {
        setSelectedProject(data[0].project_id);
        setProjectId(data[0].project_id);
      }
    } catch (error) {
      console.error("Error fetching projects:", error);
      toast.error("Failed to load projects");
    } finally {
      setLoadingProjects(false);
    }
  };

  const fetchTasks = async (projectId: string) => {
    setLoadingTasks(true);
    try {
      const { data, error } = await supabase
        .from('project_sub_tasks')
        .select('*')
        .eq('project_id', projectId);

      if (error) throw error;

      setTasks(data || []);
      if (data && data.length > 0) {
        setSelectedTask(data[0].task_id);
        setTaskId(data[0].task_id);
      }
    } catch (error) {
      console.error("Error fetching tasks:", error);
      toast.error("Failed to load tasks");
    } finally {
      setLoadingTasks(false);
    }
  };

  const fetchTickets = async (taskId: string) => {
    setLoadingTickets(true);
    try {
      const { data, error } = await supabase
        .from('tickets')
        .select('*')
        .eq('task_id', taskId);

      if (error) throw error;

      setTickets(data || []);
      if (data && data.length > 0) {
        setSelectedTicket(data[0].id);
        setTicketId(data[0].id);
      }
    } catch (error) {
      console.error("Error fetching tickets:", error);
      toast.error("Failed to load tickets");
    } finally {
      setLoadingTickets(false);
    }
  };

  const fetchTimeEntries = async (ticketId: string) => {
    setLoading(true);
    try {
      const { data, error } = await supabase
        .from('time_entries')
        .select('*')
        .eq('ticket_id', ticketId)
        .order('created_at', { ascending: false });

      if (error) throw error;

      setTimeEntries(data || []);
    } catch (error) {
      console.error("Error fetching time entries:", error);
      toast.error("Failed to load time entries");
    } finally {
      setLoading(false);
    }
  };

  const startTimer = async () => {
    if (!selectedTicket) {
      toast.error("Please select a ticket first");
      return;
    }

    const now = new Date();
    setStartTime(now);
    setIsTracking(true);

    // Create time entry
    try {
      const { data: { session } } = await supabase.auth.getSession();
      if (!session) {
        toast.error("You must be logged in to track time");
        return;
      }

      const { data, error } = await supabase
        .from('time_entries')
        .insert({
          ticket_id: selectedTicket,
          user_id: session.user.id,
          start_time: now.toISOString(),
          description: description
        })
        .select()
        .single();

      if (error) throw error;

      // Start timer interval
      const intervalId = setInterval(() => {
        const currentTime = new Date();
        const diff = currentTime.getTime() - now.getTime();
        const hours = Math.floor(diff / (1000 * 60 * 60));
        const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((diff % (1000 * 60)) / 1000);

        setElapsedTime(
          `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`
        );
      }, 1000);

      setTimerInterval(intervalId);
      toast.success("Time tracking started");
    } catch (error) {
      console.error("Error starting time tracking:", error);
      toast.error("Failed to start time tracking");
      setIsTracking(false);
      setStartTime(null);
    }
  };

  const stopTimer = async () => {
    if (!startTime || !timerInterval) return;

    clearInterval(timerInterval);
    setTimerInterval(null);

    const endTime = new Date();
    const hoursLogged = (endTime.getTime() - startTime.getTime()) / (1000 * 60 * 60);

    try {
      // Update the latest time entry
      const { data: latestEntry, error: fetchError } = await supabase
        .from('time_entries')
        .select('*')
        .eq('ticket_id', selectedTicket)
        .is('end_time', null)
        .order('start_time', { ascending: false })
        .limit(1)
        .single();

      if (fetchError) throw fetchError;

      const { error: updateError } = await supabase
        .from('time_entries')
        .update({
          end_time: endTime.toISOString(),
          hours_logged: hoursLogged,
          description: description
        })
        .eq('id', latestEntry.id);

      if (updateError) throw updateError;

      // Update ticket hours logged
      const { data: ticket, error: ticketFetchError } = await supabase
        .from('tickets')
        .select('hours_logged')
        .eq('id', selectedTicket)
        .single();

      if (ticketFetchError && ticketFetchError.code !== 'PGRST116') throw ticketFetchError;

      const currentHours = ticket?.hours_logged || 0;
      const { error: ticketUpdateError } = await supabase
        .from('tickets')
        .update({
          hours_logged: currentHours + hoursLogged
        })
        .eq('id', selectedTicket);

      if (ticketUpdateError) throw ticketUpdateError;

      setIsTracking(false);
      setStartTime(null);
      setElapsedTime("00:00:00");
      setDescription("");
      fetchTimeEntries(selectedTicket);
      toast.success("Time entry saved successfully");
    } catch (error) {
      console.error("Error stopping time tracking:", error);
      toast.error("Failed to save time entry");
    }
  };

  const updateTaskProgress = async (taskId: string, value: number) => {
    try {
      const { error } = await supabase
        .from('project_sub_tasks')
        .update({ completion_percentage: value })
        .eq('task_id', taskId);

      if (error) throw error;

      // Refresh tasks
      if (selectedProject) {
        fetchTasks(selectedProject);
      }

      toast.success("Task progress updated");
    } catch (error) {
      console.error("Error updating task progress:", error);
      toast.error("Failed to update task progress");
    }
  };

  const formatDuration = (hours: number) => {
    const totalMinutes = Math.floor(hours * 60);
    const h = Math.floor(totalMinutes / 60);
    const m = totalMinutes % 60;

    return `${h}h ${m}m`;
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle>Time & Progress Tracking</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="mb-6 space-y-4">
            <div className="space-y-2">
              <label className="text-sm font-medium">Select Project</label>
              <Select 
                value={selectedProject || ''} 
                onValueChange={setSelectedProject}
                disabled={loadingProjects || projects.length === 0}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select a project" />
                </SelectTrigger>
                <SelectContent>
                  {projects.map(project => (
                    <SelectItem key={project.project_id} value={project.project_id}>
                      {project.title}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <label className="text-sm font-medium">Select Task</label>
              <Select 
                value={selectedTask || ''} 
                onValueChange={setSelectedTask}
                disabled={loadingTasks || tasks.length === 0 || !selectedProject}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select a task" />
                </SelectTrigger>
                <SelectContent>
                  {tasks.map(task => (
                    <SelectItem key={task.task_id} value={task.task_id}>
                      {task.title}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <label className="text-sm font-medium">Select Ticket</label>
              <Select 
                value={selectedTicket || ''} 
                onValueChange={setSelectedTicket}
                disabled={loadingTickets || tickets.length === 0 || !selectedTask}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select a ticket" />
                </SelectTrigger>
                <SelectContent>
                  {tickets.map(ticket => (
                    <SelectItem key={ticket.id} value={ticket.id}>
                      {ticket.title}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {tickets.length === 0 && selectedTask && !loadingTickets && (
                <p className="text-sm text-muted-foreground mt-1">
                  No tickets found for this task. Create a ticket first.
                </p>
              )}
            </div>
          </div>

          {selectedTicket && (
            <>
              <Separator className="my-4" />

              <div className="space-y-4">
                <div>
                  <h3 className="text-lg font-medium mb-2">Task Progress</h3>
                  {selectedTask && tasks.find(t => t.task_id === selectedTask) && (
                    <div className="space-y-4">
                      <div className="flex items-center justify-between">
                        <span className="font-medium">
                          {tasks.find(t => t.task_id === selectedTask)?.title}
                        </span>
                        <span className="text-sm">
                          {tasks.find(t => t.task_id === selectedTask)?.completion_percentage || 0}%
                        </span>
                      </div>
                      <Slider
                        defaultValue={[tasks.find(t => t.task_id === selectedTask)?.completion_percentage || 0]}
                        max={100}
                        step={5}
                        className="w-full"
                        onValueCommit={(value) => {
                          if (selectedTask) {
                            updateTaskProgress(selectedTask, value[0]);
                          }
                        }}
                      />
                    </div>
                  )}
                </div>

                <Separator className="my-4" />

                <div>
                  <h3 className="text-lg font-medium mb-4">Time Tracker</h3>
                  <div className="grid gap-4 md:grid-cols-2">
                    <div className="space-y-4">
                      <div className="rounded-lg border p-4">
                        <div className="text-center text-4xl font-mono mb-4">
                          {isTracking ? elapsedTime : "00:00:00"}
                        </div>
                        <Textarea
                          value={description}
                          onChange={(e) => setDescription(e.target.value)}
                          placeholder="What are you working on?"
                          className="mb-4"
                          disabled={isTracking}
                        />
                        {isTracking ? (
                          <Button 
                            variant="destructive" 
                            className="w-full" 
                            onClick={stopTimer}
                          >
                            <PauseCircle className="mr-2 h-4 w-4" />
                            Stop Timer
                          </Button>
                        ) : (
                          <Button 
                            className="w-full" 
                            onClick={startTimer}
                          >
                            <PlayCircle className="mr-2 h-4 w-4" />
                            Start Timer
                          </Button>
                        )}
                      </div>
                    </div>

                    <div>
                      <div className="rounded-lg border">
                        <div className="p-4 font-medium flex justify-between items-center">
                          <h4>Recent Time Entries</h4>
                          {loading && <p className="text-sm text-muted-foreground">Loading...</p>}
                        </div>
                        <div className="max-h-[300px] overflow-y-auto">
                          {!loading && timeEntries.length === 0 ? (
                            <p className="p-4 text-center text-muted-foreground">No time entries found</p>
                          ) : (
                            <Accordion type="single" collapsible className="w-full">
                              {timeEntries.map((entry, index) => (
                                <AccordionItem key={entry.id} value={entry.id}>
                                  <AccordionTrigger className="px-4 py-2 hover:no-underline">
                                    <div className="flex justify-between items-center w-full">
                                      <div className="flex items-center">
                                        <Clock className="mr-2 h-4 w-4" />
                                        <span>
                                          {entry.hours_logged ? formatDuration(entry.hours_logged) : 'In progress'}
                                        </span>
                                      </div>
                                      <span className="text-xs text-muted-foreground">
                                        {format(new Date(entry.start_time), 'MMM d, yyyy')}
                                      </span>
                                    </div>
                                  </AccordionTrigger>
                                  <AccordionContent className="px-4 pb-3 pt-0">
                                    <div className="text-sm space-y-2">
                                      <div className="flex justify-between">
                                        <span className="font-medium">Start:</span>
                                        <span>{format(new Date(entry.start_time), 'MMM d, yyyy h:mm a')}</span>
                                      </div>
                                      {entry.end_time && (
                                        <div className="flex justify-between">
                                          <span className="font-medium">End:</span>
                                          <span>{format(new Date(entry.end_time), 'MMM d, yyyy h:mm a')}</span>
                                        </div>
                                      )}
                                      {entry.description && (
                                        <div className="pt-2">
                                          <span className="font-medium">Description:</span>
                                          <p className="mt-1">{entry.description}</p>
                                        </div>
                                      )}
                                    </div>
                                  </AccordionContent>
                                </AccordionItem>
                              ))}
                            </Accordion>
                          )}
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

import React, { useEffect, useState, useRef } from "react";
import { supabase } from "@/lib/supabase";
import { FileImage, FileText, Loader2, AlertCircle, RefreshCw } from "lucide-react";
import { Button } from "@/components/ui/button";
import { checkStoragePermissions, getSecureFileUrl } from "@/utils/setupStorage";

interface TicketAttachmentsListProps {
  reporterId: string | undefined;
  ticketId: string;
  onAttachmentsLoaded?: (hasAttachments: boolean) => void;
  userType?: 'jobseeker' | 'business'; // Added userType prop to handle different permission scenarios
}

export const TicketAttachmentsList = ({ 
  reporterId, 
  ticketId,
  onAttachmentsLoaded,
  userType
}: TicketAttachmentsListProps) => {
  const [attachments, setAttachments] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [fileUrls, setFileUrls] = useState<{[key: string]: string}>({});
  const [loadingFiles, setLoadingFiles] = useState<{[key: string]: boolean}>({});
  const [fileErrors, setFileErrors] = useState<{[key: string]: string}>({});
  const [permissionsDetails, setPermissionsDetails] = useState<any>(null);
  const [retryCount, setRetryCount] = useState(0);

  // Add these refs to prevent unnecessary re-renders and fetch loops
  const hasFetchedRef = useRef(false);
  const reporterIdRef = useRef(reporterId);
  const ticketIdRef = useRef(ticketId);
  const hasCalledCallbackRef = useRef(false);

  useEffect(() => {
    // Update refs when props change
    reporterIdRef.current = reporterId;
    ticketIdRef.current = ticketId;
  }, [reporterId, ticketId]);

  useEffect(() => {
    // Reset tracking states when retry is triggered
    if (retryCount > 0) {
      hasFetchedRef.current = false;
      hasCalledCallbackRef.current = false;
    }
  }, [retryCount]);

  useEffect(() => {
    const fetchAttachments = async () => {
      // Skip if already fetched or missing required data
      if (hasFetchedRef.current || !reporterId || !ticketId) {
        if (!reporterId || !ticketId) {
          setLoading(false);
          if (onAttachmentsLoaded && !hasCalledCallbackRef.current) {
            onAttachmentsLoaded(false);
            hasCalledCallbackRef.current = true;
          }
        }
        return;
      }

      hasFetchedRef.current = true; // Mark as fetched to prevent loops

      try {
        console.log(`Fetching attachments from path: ${reporterId}/${ticketId} for ${userType || 'unknown'} user`);

        // Check if we're authenticated
        const { data: { session } } = await supabase.auth.getSession();
        if (!session) {
          throw new Error("Not authenticated");
        }

        // First check storage permissions - apply different strategies based on user type
        const permissionCheck = await checkStoragePermissions(
          'ticket-attachments', 
          `${reporterId}/${ticketId}`,
          userType === 'jobseeker' ? { retryLimit: 1 } : undefined // Limit retries for jobseekers
        );

        setPermissionsDetails(permissionCheck);

        if (!permissionCheck.success) {
          console.error("Storage access denied:", permissionCheck);
          throw new Error(`Storage access denied: ${permissionCheck.error}`);
        }

        // Batch state updates to reduce render cycles
        const updatedState: any = {
          attachmentsData: [],
          urlsMap: {},
          loadingFilesMap: {},
          errorsMap: {}
        };

        const { data, error } = await supabase.storage
          .from('ticket-attachments')
          .list(`${reporterId}/${ticketId}`);

        if (error) {
          console.error("Storage list error:", error);
          throw error;
        }

        console.log("Attachments fetched:", data);
        updatedState.attachmentsData = data || [];

        if (data && data.length > 0) {
          // Pre-populate loading states
          const loadingStates: {[key: string]: boolean} = {};
          data.forEach(file => {
            loadingStates[file.name] = true;
          });
          updatedState.loadingFilesMap = loadingStates;

          // Set initial state before async operations
          setAttachments(updatedState.attachmentsData);
          setLoadingFiles(loadingStates);

          // Get URLs in parallel instead of sequentially
          const urlPromises = data.map(async (file) => {
            try {
              const result = await getSecureFileUrl(
                'ticket-attachments', 
                `${reporterId}/${ticketId}/${file.name}`
              );

              return {
                fileName: file.name,
                url: result.success ? result.url : null,
                error: !result.success ? result.error : null
              };
            } catch (err: any) {
              console.error(`Error getting URL for ${file.name}:`, err);
              return {
                fileName: file.name,
                url: null,
                error: err.message || "Failed to get URL"
              };
            }
          });

          // Process results
          const results = await Promise.all(urlPromises);

          const urlMap: {[key: string]: string} = {};
          const errorMap: {[key: string]: string} = {};
          const finalLoadingStates: {[key: string]: boolean} = {};

          results.forEach(result => {
            if (result.url) {
              urlMap[result.fileName] = result.url;
            }
            if (result.error) {
              errorMap[result.fileName] = result.error;
            }
            finalLoadingStates[result.fileName] = false;
          });

          // Update state with all results at once
          setFileUrls(urlMap);
          setFileErrors(errorMap);
          setLoadingFiles(finalLoadingStates);
        }

        // Call the callback after data is processed
        if (onAttachmentsLoaded && !hasCalledCallbackRef.current) {
          const hasAttachments = data && data.length > 0;
          onAttachmentsLoaded(hasAttachments);
          hasCalledCallbackRef.current = true;
        }
      } catch (err: any) {
        console.error("Error fetching attachments:", err);
        setError(err.message || "Failed to load attachments");

        // Call callback even on error
        if (onAttachmentsLoaded && !hasCalledCallbackRef.current) {
          onAttachmentsLoaded(false);
          hasCalledCallbackRef.current = true;
        }
      } finally {
        setLoading(false);
      }
    };

    fetchAttachments();
  }, [reporterId, ticketId, onAttachmentsLoaded, retryCount, userType]);

  const handleRetry = () => {
    hasFetchedRef.current = false;
    hasCalledCallbackRef.current = false;
    setLoading(true);
    setError(null);
    setFileErrors({});
    setRetryCount(prev => prev + 1);
  };

  const getFileIcon = (mimeType: string) => {
    if (mimeType?.startsWith('image/')) {
      return <FileImage className="h-5 w-5 text-blue-500" />;
    }
    return <FileText className="h-5 w-5 text-gray-500" />;
  };

  const isImageFile = (file: any) => {
    if (file.metadata?.mimetype?.startsWith('image/')) return true;

    const imageSuffixes = ['.jpg', '.jpeg', '.png', '.gif', '.webp', '.bmp', '.heic'];
    return imageSuffixes.some(suffix => file.name.toLowerCase().endsWith(suffix));
  };

  const handleImageError = (filename: string) => {
    setFileErrors(prev => ({ ...prev, [filename]: "Failed to load image preview" }));
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center py-4">
        <Loader2 className="h-6 w-6 animate-spin text-gray-400" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col items-center justify-center py-4 text-red-500">
        <AlertCircle className="h-8 w-8 mb-2" />
        <div className="text-sm">{error}</div>
        <div className="text-xs mt-2">
          {userType === 'jobseeker' ? 
            "You may not have permission to view these attachments." : 
            "Check storage bucket permissions and RLS policies"}
        </div>
        <Button 
          variant="outline" 
          size="sm" 
          onClick={handleRetry}
          className="mt-2"
        >
          <RefreshCw className="h-4 w-4 mr-2" />
          Retry
        </Button>
        {permissionsDetails && userType !== 'jobseeker' && (
          <div className="text-xs mt-2 bg-gray-100 p-2 rounded max-w-full overflow-auto">
            <pre className="whitespace-pre-wrap">
              {JSON.stringify(permissionsDetails, null, 2)}
            </pre>
          </div>
        )}
      </div>
    );
  }

  if (attachments.length === 0) {
    return (
      <div className="text-sm text-gray-500 py-2">
        No attachments found for this ticket.
        {userType !== 'jobseeker' && (
          <div className="text-xs mt-1">
            Path: {reporterId}/{ticketId}
          </div>
        )}
      </div>
    );
  }

  return (
    <div className="space-y-2">
      <h4 className="text-sm font-medium">Attachments ({attachments.length})</h4>
      <div className="grid grid-cols-2 sm:grid-cols-3 gap-2">
        {attachments.map((file) => {
          const isImage = isImageFile(file);
          const fileUrl = fileUrls[file.name] || '';
          const hasError = fileErrors[file.name];
          const isLoading = loadingFiles[file.name];

          return (
            <div 
              key={file.id} 
              className="border rounded-md p-2 flex flex-col gap-1"
            >
              <div className="aspect-square bg-gray-100 rounded flex items-center justify-center overflow-hidden relative">
                {isLoading ? (
                  <Loader2 className="h-5 w-5 animate-spin text-gray-400" />
                ) : isImage && fileUrl ? (
                  <>
                    <img 
                      src={fileUrl} 
                      alt={file.name} 
                      className="h-full w-full object-cover"
                      onError={() => handleImageError(file.name)}
                    />
                    {hasError && (
                      <div className="absolute inset-0 bg-gray-100 bg-opacity-80 flex flex-col items-center justify-center p-2">
                        <AlertCircle className="h-5 w-5 text-red-500 mb-1" />
                        <span className="text-xs text-center text-red-500">Preview not available</span>
                      </div>
                    )}
                  </>
                ) : (
                  <div className="flex flex-col items-center justify-center">
                    {getFileIcon(file.metadata?.mimetype || '')}
                    <span className="text-xs text-gray-500 mt-1">
                      {file.metadata?.size ? `${Math.round(file.metadata.size / 1024)} KB` : ''}
                    </span>
                  </div>
                )}
              </div>
              <div className="text-xs truncate" title={file.name}>
                {file.name}
              </div>
              <div className="flex gap-1 mt-auto">
                <Button 
                  variant="outline" 
                  size="sm" 
                  className="text-xs h-7 flex-1"
                  onClick={() => window.open(fileUrl, '_blank')}
                  disabled={!fileUrl || isLoading}
                >
                  {isLoading ? <Loader2 className="h-3 w-3 animate-spin" /> : "View"}
                </Button>
                <Button 
                  variant="outline" 
                  size="sm" 
                  className="text-xs h-7 flex-1"
                  onClick={() => {
                    const a = document.createElement('a');
                    a.href = fileUrl;
                    a.download = file.name;
                    document.body.appendChild(a);
                    a.click();
                    document.body.removeChild(a);
                  }}
                  disabled={!fileUrl || isLoading}
                >
                  Download
                </Button>
              </div>
              {hasError && !isLoading && (
                <div className="text-xs text-red-500 mt-1">
                  {hasError}
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export const checkTicketAttachments = async (
  reporterId?: string, 
  ticketId?: string,
  userType?: 'jobseeker' | 'business'
): Promise<boolean> => {
  if (!reporterId || !ticketId) return false;

  try {
    console.log(`Checking attachments at path: ${reporterId}/${ticketId} for ${userType || 'unknown'} user`);

    const { data: sessionData } = await supabase.auth.getSession();
    if (!sessionData.session) {
      console.error("Not authenticated");
      return false;
    }

    // Add caching to prevent repeated checks
    const cacheKey = `attachments_${reporterId}_${ticketId}`;
    const cachedResult = sessionStorage.getItem(cacheKey);

    if (cachedResult !== null) {
      return cachedResult === 'true';
    }

    const { data, error } = await supabase.storage
      .from('ticket-attachments')
      .list(`${reporterId}/${ticketId}`);

    if (error) {
      console.error("Error checking attachments:", error);
      throw error;
    }

    const hasAttachments = data && data.length > 0;

    // Cache the result for 5 minutes
    try {
      sessionStorage.setItem(cacheKey, hasAttachments ? 'true' : 'false');
      setTimeout(() => {
        sessionStorage.removeItem(cacheKey);
      }, 5 * 60 * 1000);
    } catch (e) {
      console.warn("Could not cache attachment check result", e);
    }

    return hasAttachments;
  } catch (err) {
    console.error("Error checking ticket attachments:", err);
    return false;
  }
};

