import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { TicketMessage, KanbanColumn } from "@/types/dashboard";
import { AlertTriangle, CheckCircle2, Clock, MessageCircle } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { CreateMessageDialog } from "@/components/job-seeker/dashboard/applications/CreateMessageDialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import KanbanBoard from "@/components/ui/kanban-board";

interface TicketsListProps {
  userTickets: any[];
  ticketMessages: TicketMessage[];
  onTicketAction: (ticketId: string, action: string, data?: any) => void;
}

export const TicketsList = ({ userTickets, ticketMessages, onTicketAction }: TicketsListProps) => {
  const [selectedTicketId, setSelectedTicketId] = useState<string | null>(null);
  const [isMessageDialogOpen, setIsMessageDialogOpen] = useState(false);
  const [tickets, setTickets] = useState<{[key: string]: any}>({});
  const [columns, setColumns] = useState<{[key: string]: KanbanColumn}>({
    'new': { id: 'new', title: 'New', ticketIds: [] },
    'in-progress': { id: 'in-progress', title: 'In Progress', ticketIds: [] },
    'review': { id: 'review', title: 'Review', ticketIds: [] },
    'done': { id: 'done', title: 'Done', ticketIds: [] },
    'blocked': { id: 'blocked', title: 'Blocked', ticketIds: [] }
  });
  const [activeTab, setActiveTab] = useState('list');

  // Use the ticketMessages directly from props instead of fetching again
  useEffect(() => {
    if (userTickets.length > 0) {
      // Process tickets into format needed for kanban board
      const newTickets: {[key: string]: any} = {};
      const newColumns = {
        'new': { id: 'new', title: 'New', ticketIds: [] },
        'in-progress': { id: 'in-progress', title: 'In Progress', ticketIds: [] },
        'review': { id: 'review', title: 'Review', ticketIds: [] },
        'done': { id: 'done', title: 'Done', ticketIds: [] },
        'blocked': { id: 'blocked', title: 'Blocked', ticketIds: [] }
      };

      userTickets.forEach(ticket => {
        newTickets[ticket.id] = {
          id: ticket.id,
          title: ticket.title,
          description: ticket.description,
          priority: ticket.priority,
          status: ticket.status,
          due_date: ticket.due_date
        };

        // Map status to column
        const columnId = ticket.status === 'closed' ? 'done' : ticket.status;
        if (newColumns[columnId]) {
          newColumns[columnId].ticketIds.push(ticket.id);
        } else {
          // Default to new column if status doesn't match
          newColumns['new'].ticketIds.push(ticket.id);
        }
      });

      setTickets(newTickets);
      setColumns(newColumns);
    }
  }, [userTickets]);

  const handleSendMessage = async (message: string) => {
    if (selectedTicketId) {
      await onTicketAction(selectedTicketId, 'reply', { message });
      setIsMessageDialogOpen(false);
    }
  };

  const handleTicketMove = async (ticketId: string, newStatus: string) => {
    try {
      await onTicketAction(ticketId, 'update_status', { status: newStatus });

      // Update local state to reflect the change
      const startColumn = Object.values(columns).find(col => 
        col.ticketIds.includes(ticketId)
      );

      if (startColumn && columns[newStatus]) {
        const newColumns = { ...columns };

        // Remove from current column
        newColumns[startColumn.id].ticketIds = newColumns[startColumn.id].ticketIds
          .filter(id => id !== ticketId);

        // Add to new column
        newColumns[newStatus].ticketIds.push(ticketId);

        setColumns(newColumns);
      }
    } catch (error) {
      console.error("Error moving ticket:", error);
    }
  };

  const handleTicketClick = (ticketId: string) => {
    setSelectedTicketId(ticketId);
    setIsMessageDialogOpen(true);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString();
  };

  // Get ticket messages for a specific ticket from the provided ticketMessages prop
  const getTicketMessages = (ticketId: string) => {
    return ticketMessages.filter(msg => msg.ticketId === ticketId);
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div>
            <CardTitle>My Tickets</CardTitle>
            <CardDescription>Your reported issues and tasks</CardDescription>
          </div>
          <Tabs value={activeTab} onValueChange={setActiveTab} className="w-auto">
            <TabsList>
              <TabsTrigger value="list">List View</TabsTrigger>
              <TabsTrigger value="board">Board View</TabsTrigger>
            </TabsList>
          </Tabs>
        </div>
      </CardHeader>
      <CardContent>
        <TabsContent value="list" className="mt-0">
          {userTickets.length === 0 ? (
            <div className="text-center py-12">
              <AlertTriangle className="h-12 w-12 mx-auto text-gray-400 mb-4" />
              <p className="text-lg font-medium text-gray-700">No tickets found</p>
              <p className="text-sm text-gray-500 mt-1">
                You haven't reported any issues yet or no tickets have been assigned to you.
              </p>
              <p className="text-sm text-gray-500 mt-3">
                Use the "Report Beta Issue" button to report problems or request features.
              </p>
            </div>
          ) : (
            <div className="space-y-4">
              {userTickets.map(ticket => (
                <div key={ticket.id} className="border rounded-lg p-4">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-medium text-lg">{ticket.title}</h3>
                      <p className="text-sm text-gray-500 mt-1">{ticket.description}</p>
                      <div className="flex gap-2 mt-2">
                        <Badge className={getStatusColor(ticket.status)}>
                          {getStatusIcon(ticket.status)} 
                          <span className="ml-1">{ticket.status}</span>
                        </Badge>
                        <Badge className={getPriorityColor(ticket.priority)}>
                          {ticket.priority}
                        </Badge>
                        {ticket.due_date && (
                          <Badge variant="outline">
                            Due: {formatDate(ticket.due_date)}
                          </Badge>
                        )}
                      </div>
                    </div>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => {
                        setSelectedTicketId(ticket.id);
                        setIsMessageDialogOpen(true);
                      }}
                    >
                      <MessageCircle className="h-4 w-4 mr-1" />
                      Reply
                    </Button>
                  </div>

                  {/* Show ticket messages if present */}
                  {getTicketMessages(ticket.id).length > 0 && (
                    <div className="mt-4 border-t pt-3">
                      <h4 className="text-sm font-medium mb-2">Messages</h4>
                      <div className="space-y-2 max-h-40 overflow-y-auto">
                        {getTicketMessages(ticket.id).map((msg, idx) => (
                          <div 
                            key={msg.id || idx} 
                            className="bg-gray-50 p-3 rounded text-sm"
                          >
                            <div className="flex justify-between items-start">
                              <p className="font-medium">{msg.subject || 'Reply'}</p>
                              <span className="text-xs text-gray-500">
                                {formatDate(msg.createdAt)}
                              </span>
                            </div>
                            <p className="mt-1">{msg.message}</p>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </TabsContent>

        <TabsContent value="board" className="mt-0">
          {userTickets.length === 0 ? (
            <div className="text-center py-12">
              <AlertTriangle className="h-12 w-12 mx-auto text-gray-400 mb-4" />
              <p className="text-lg font-medium text-gray-700">No tickets found</p>
              <p className="text-sm text-gray-500 mt-1">
                You haven't reported any issues yet or no tickets have been assigned to you.
              </p>
            </div>
          ) : (
            <KanbanBoard 
              columns={columns}
              tickets={tickets}
              onTicketMove={handleTicketMove}
              onTicketClick={handleTicketClick}
              formatDate={formatDate}
            />
          )}
        </TabsContent>
      </CardContent>

      <CreateMessageDialog
        isOpen={isMessageDialogOpen}
        onOpenChange={setIsMessageDialogOpen}
        onSendMessage={handleSendMessage}
        applicationId={selectedTicketId || ''}
        onMessageSent={() => {
          setSelectedTicketId(null);
        }}
      />
    </Card>
  );

  // Helper functions for displaying status information
  function getStatusIcon(status: string) {
    switch (status) {
      case 'new':
        return <Clock className="h-4 w-4 text-blue-500" />;
      case 'in-progress':
        return <Clock className="h-4 w-4 text-yellow-500" />;
      case 'blocked':
        return <AlertTriangle className="h-4 w-4 text-red-500" />;
      case 'done':
      case 'closed':
        return <CheckCircle2 className="h-4 w-4 text-green-500" />;
      default:
        return <Clock className="h-4 w-4 text-blue-500" />;
    }
  }

  function getStatusColor(status: string) {
    switch (status) {
      case 'new':
        return 'bg-blue-100 text-blue-800';
      case 'in-progress':
        return 'bg-yellow-100 text-yellow-800';
      case 'blocked':
        return 'bg-red-100 text-red-800';
      case 'review':
        return 'bg-purple-100 text-purple-800';
      case 'done':
        return 'bg-green-100 text-green-800';
      case 'closed':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-blue-100 text-blue-800';
    }
  }

  function getPriorityColor(priority: string) {
    switch (priority) {
      case 'high':
        return 'bg-red-100 text-red-800';
      case 'medium':
        return 'bg-yellow-100 text-yellow-800';
      case 'low':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  }
};

import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { supabase } from "@/lib/supabase";
import { toast } from "sonner";
import { Loader2, Check } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { useNDAIntegration } from "@/hooks/useNDAIntegration";
import { DocumentViewer } from "@/components/documents/DocumentViewer";

interface ApplicationFormProps {
  projectId: string;
  taskId: string;
  projectTitle?: string;
  taskTitle?: string;
}

export const ApplicationForm = ({
  projectId,
  taskId,
  projectTitle = "Project",
  taskTitle = "Role"
}: ApplicationFormProps) => {
  const [message, setMessage] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [businessId, setBusinessId] = useState<string>("");
  const [ndaDocumentId, setNdaDocumentId] = useState<string | null>(null);
  const [ndaDocument, setNdaDocument] = useState<any>(null);
  const [ndaStatus, setNdaStatus] = useState<string | null>(null);
  const [requiresNDA, setRequiresNDA] = useState<boolean>(false);
  const navigate = useNavigate();

  const { 
    isProcessingNDA,
    checkNDARequirement,
    getNDAForJobApplication,
    generateApplicationNDA 
  } = useNDAIntegration();

  // Get business ID for the project
  useEffect(() => {
    const getBusinessId = async () => {
      try {
        const { data, error } = await supabase
          .from("business_projects")
          .select("business_id")
          .eq("project_id", projectId)
          .single();

        if (error) throw error;
        if (data) setBusinessId(data.business_id);

        // Check if project requires NDA
        const needsNDA = await checkNDARequirement(projectId);
        setRequiresNDA(needsNDA);
      } catch (error) {
        console.error("Error fetching project business ID:", error);
      }
    };

    if (projectId) getBusinessId();
  }, [projectId]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!projectId || !taskId) {
      toast.error("Missing project or task information");
      return;
    }

    try {
      setIsSubmitting(true);

      const { data: { session } } = await supabase.auth.getSession();
      if (!session) {
        toast.error("You must be logged in to apply");
        return;
      }

      // Create job application
      const { data: applicationData, error: applicationError } = await supabase
        .from("job_applications")
        .insert({
          user_id: session.user.id,
          task_id: taskId,
          project_id: projectId,
          message,
          status: "pending",
          applied_at: new Date().toISOString()
        })
        .select("job_app_id")
        .single();

      if (applicationError) throw applicationError;

      // If project requires NDA, generate it
      if (requiresNDA && applicationData?.job_app_id && businessId) {
        await generateApplicationNDA(
          applicationData.job_app_id,
          businessId,
          session.user.id,
          projectId
        );
      }

      toast.success("Application submitted successfully");
      navigate("/seeker/dashboard?tab=applications");
    } catch (error) {
      console.error("Error submitting application:", error);
      toast.error("Failed to submit application");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div>
        <h2 className="text-lg font-medium mb-2">
          Apply to: {taskTitle}
        </h2>
        <p className="text-sm text-muted-foreground mb-6">
          Project: {projectTitle}
        </p>

        {requiresNDA && (
          <div className="mb-6">
            <div className="bg-amber-50 border border-amber-200 rounded-md p-4 mb-4">
              <h3 className="text-sm font-medium text-amber-800 mb-1">
                Non-Disclosure Agreement Required
              </h3>
              <p className="text-sm text-amber-700">
                This project requires a Non-Disclosure Agreement (NDA). By submitting your application, an NDA will be automatically generated for you to review and sign.
              </p>
            </div>
          </div>
        )}

        <div className="mb-6">
          <label htmlFor="message" className="block text-sm font-medium mb-2">
            Application Message
          </label>
          <Textarea
            id="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Describe why you're a good fit for this role and any relevant experience you have."
            className="min-h-[150px]"
          />
        </div>

        <div className="flex justify-end">
          <Button
            type="submit"
            className="w-full sm:w-auto"
            disabled={isSubmitting || isProcessingNDA}
          >
            {(isSubmitting || isProcessingNDA) ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                {isProcessingNDA ? "Generating NDA..." : "Submitting..."}
              </>
            ) : (
              "Submit Application"
            )}
          </Button>
        </div>
      </div>
    </form>
  );
};

