import { useState, useEffect } from "react";
import { supabase } from "@/lib/supabase";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { TicketDashboard } from "@/components/ticket/TicketDashboard";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Button } from "@/components/ui/button";
import { toast } from "sonner";
import { Ticket } from "@/types/types";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { RefreshCw, Plus } from "lucide-react";

interface BetaTestingTabProps {
  userType: 'business' | 'job_seeker';
  userId?: string;
  includeProjectTickets?: boolean;
}

export const BetaTestingTab = ({ 
  userType, 
  userId, 
  includeProjectTickets = false 
}: BetaTestingTabProps) => {
  const [tickets, setTickets] = useState<Ticket[]>([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState("tickets");
  const [expandedTickets, setExpandedTickets] = useState<Set<string>>(new Set());
  const [projects, setProjects] = useState<any[]>([]);
  const [selectedProject, setSelectedProject] = useState<string | null>(null);
  const [taskStats, setTaskStats] = useState({
    total: 0,
    open: 0,
    closed: 0,
    highPriority: 0
  });

  useEffect(() => {
    if (userId) {
      fetchProjects(userId);
    }
  }, [userId, userType]);

  useEffect(() => {
    if (userId && selectedProject) {
      loadTickets(userId);
    } else if (userId) {
      loadTickets(userId);
    }
  }, [userId, selectedProject]);

  const fetchProjects = async (userId: string) => {
    try {
      if (userType === 'business') {
        const { data, error } = await supabase
          .from('business_projects')
          .select('project_id, title')
          .eq('business_id', userId);

        if (error) throw error;
        setProjects(data || []);

        if (data && data.length > 0) {
          setSelectedProject(data[0].project_id);
        }
      } else {
        const { data, error } = await supabase
          .from('jobseeker_active_projects')
          .select('project_id, project_title')
          .eq('user_id', userId)
          .eq('application_status', 'accepted');

        if (error) throw error;

        const uniqueProjects = Array.from(
          new Map(data?.map(item => [item.project_id, { 
            project_id: item.project_id, 
            title: item.project_title 
          }]) || []).values()
        );

        setProjects(uniqueProjects);

        if (uniqueProjects.length > 0) {
          setSelectedProject(uniqueProjects[0].project_id);
        }
      }
    } catch (error) {
      console.error("Error fetching projects:", error);
      toast.error("Failed to load projects");
    }
  };

  const loadTickets = async (userId: string) => {
    try {
      setLoading(true);

      const userField = userType === 'business' ? 'reporter' : 'assigned_to';

      let query = supabase
        .from('tickets')
        .select('*');

      query = query.or(`${userField}.eq.${userId},${userType === 'business' ? 'assigned_to' : 'reporter'}.eq.${userId}`);

      if (selectedProject) {
        query = query.eq('project_id', selectedProject);
      }

      query = query.order('created_at', { ascending: false });

      const { data, error } = await query;

      if (error) throw error;

      const processedTickets = (data || []).map(ticket => ({
        ...ticket,
        expanded: !!expandedTickets.has(ticket.id),
        description: ticket.description || ""
      }));

      setTickets(processedTickets);

      const stats = {
        total: processedTickets.length,
        open: processedTickets.filter(t => t.status !== 'done' && t.status !== 'closed').length,
        closed: processedTickets.filter(t => t.status === 'done' || t.status === 'closed').length,
        highPriority: processedTickets.filter(t => t.priority === 'high').length
      };

      setTaskStats(stats);
    } catch (error) {
      console.error("Error loading tickets:", error);
      toast.error("Failed to load tickets");
    } finally {
      setLoading(false);
    }
  };

  const handleTicketAction = async (ticketId: string, action: string, data: any) => {
    try {
      switch (action) {
        case 'updateStatus':
          await supabase
            .from('tickets')
            .update({ status: data })
            .eq('id', ticketId);

          toast.success("Status updated");
          break;

        case 'updatePriority':
          await supabase
            .from('tickets')
            .update({ priority: data })
            .eq('id', ticketId);

          toast.success("Priority updated");
          break;

        case 'updateDueDate':
          await supabase
            .from('tickets')
            .update({ due_date: data })
            .eq('id', ticketId);

          toast.success("Due date updated");
          break;

        case 'addNote':
          const { data: ticketData } = await supabase
            .from('tickets')
            .select('notes')
            .eq('id', ticketId)
            .single();

          const { data: profileData } = await supabase
            .from('profiles')
            .select('first_name, last_name')
            .eq('id', userId)
            .single();

          const userName = profileData ? 
            `${profileData.first_name || ''} ${profileData.last_name || ''}`.trim() : 
            'User';

          const newNote = {
            id: Date.now().toString(),
            user: userName,
            timestamp: new Date().toISOString(),
            comment: data
          };

          const currentNotes = ticketData?.notes || [];
          const updatedNotes = [...currentNotes, newNote];

          await supabase
            .from('tickets')
            .update({ notes: updatedNotes })
            .eq('id', ticketId);

          toast.success("Note added");
          break;

        default:
          console.warn("Unknown action:", action);
      }

      if (userId) {
        await loadTickets(userId);
      }
    } catch (error) {
      console.error("Error handling ticket action:", error);
      toast.error("Failed to update ticket");
    }
  };

  const handleRefresh = () => {
    if (userId) {
      loadTickets(userId);
    }
  };

  const handleToggleTicket = (ticketId: string) => {
    setExpandedTickets(prev => {
      const newSet = new Set(prev);
      if (newSet.has(ticketId)) {
        newSet.delete(ticketId);
      } else {
        newSet.add(ticketId);
      }
      return newSet;
    });
  };

  const handleCreateTicket = () => {
    toast.info("Create ticket functionality will be implemented soon");
  };

  if (!userId) {
    return <div>Please log in to view tickets.</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex flex-col space-y-2">
        <h2 className="text-2xl font-bold">My Projects</h2>
        <p className="text-muted-foreground">View and manage your project tasks</p>
      </div>

      <div className="flex items-center justify-between mb-4">
        <Select value={selectedProject || "none"} onValueChange={setSelectedProject}>
          <SelectTrigger className="w-[180px]">
            <SelectValue placeholder="Select project" />
          </SelectTrigger>
          <SelectContent>
            {projects.length === 0 ? (
              <SelectItem value="none">No projects available</SelectItem>
            ) : (
              projects.map(project => (
                <SelectItem key={project.project_id} value={project.project_id}>
                  {project.title || project.project_title}
                </SelectItem>
              ))
            )}
          </SelectContent>
        </Select>

        <div className="flex gap-2">
          <Button size="sm" variant="outline" onClick={handleRefresh}>
            <RefreshCw className="h-4 w-4 mr-1" /> Refresh
          </Button>

          <Button size="sm" onClick={handleCreateTicket}>
            <Plus className="h-4 w-4 mr-1" /> Create Ticket
          </Button>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-sm font-medium text-muted-foreground">All Tickets</div>
            <div className="text-2xl font-bold mt-1">{taskStats.total}</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-sm font-medium text-muted-foreground">Open Tasks</div>
            <div className="text-2xl font-bold mt-1">{taskStats.open}</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-sm font-medium text-muted-foreground">Closed Tasks</div>
            <div className="text-2xl font-bold mt-1">{taskStats.closed}</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-sm font-medium text-muted-foreground">High Priority</div>
            <div className="text-2xl font-bold mt-1">{taskStats.highPriority}</div>
          </CardContent>
        </Card>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="mb-4">
          <TabsTrigger value="tickets">All Tickets</TabsTrigger>
          {includeProjectTickets && (
            <>
              <TabsTrigger value="project-tasks">Project Tasks</TabsTrigger>
              <TabsTrigger value="project-tickets">Project Tickets</TabsTrigger>
            </>
          )}
          <TabsTrigger value="beta-tickets">Beta Testing Tickets</TabsTrigger>
        </TabsList>

        <TabsContent value="tickets">
          <TicketDashboard 
            initialTickets={tickets.filter(t => 
              t.ticket_type === "beta_testing" || 
              t.ticket_type === "beta-test" || 
              t.ticket_type === "beta-testing"
            )}
            onRefresh={handleRefresh}
            onTicketAction={handleTicketAction}
            showTimeTracking={userType === 'job_seeker'}
            userId={userId}
            expandedTickets={expandedTickets}
            toggleTicketExpansion={handleToggleTicket}
          />
        </TabsContent>

        {includeProjectTickets && (
          <>
            <TabsContent value="project-tasks">
              <TicketDashboard 
                initialTickets={tickets.filter(t => t.task_id)}
                onRefresh={handleRefresh}
                onTicketAction={handleTicketAction}
                showTimeTracking={userType === 'job_seeker'}
                userId={userId}
                expandedTickets={expandedTickets}
                toggleTicketExpansion={handleToggleTicket}
              />
            </TabsContent>

            <TabsContent value="project-tickets">
              <TicketDashboard 
                initialTickets={tickets.filter(t => t.project_id && !t.task_id)}
                onRefresh={handleRefresh}
                onTicketAction={handleTicketAction}
                showTimeTracking={userType === 'job_seeker'}
                userId={userId}
                expandedTickets={expandedTickets}
                toggleTicketExpansion={handleToggleTicket}
              />
            </TabsContent>
          </>
        )}

        <TabsContent value="beta-tickets">
          <Card>
            <CardContent className="p-6 text-center">
              <h3 className="text-lg font-medium mb-2">Beta Testing Features</h3>
              <p className="text-muted-foreground mb-4">
                This section will allow you to test new features before they are released.
              </p>
              <Button variant="outline">Join Beta Program</Button>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
};

export const LiveProjectsTab = BetaTestingTab;

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { X } from "lucide-react";
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

export interface BetaTicket {
  id: string;
  title: string;
  description: string;
  status: string;
  priority: string;
  due_date?: string;
}

interface KanbanColumn {
  id: string;
  title: string;
  ticketIds: string[];
}

export interface KanbanBoardProps {
  tickets: BetaTicket[];
  onStatusChange?: (ticketId: string, newStatus: string) => void;
  onTicketClick?: (ticketId: string) => void;
}

export function KanbanBoard({ tickets, onStatusChange = () => {}, onTicketClick = () => {} }: KanbanBoardProps) {
  const getInitialColumns = () => {
    const columns: Record<string, KanbanColumn> = {
      'new': { id: 'new', title: 'New', ticketIds: [] },
      'in-progress': { id: 'in-progress', title: 'In Progress', ticketIds: [] },
      'blocked': { id: 'blocked', title: 'Blocked', ticketIds: [] },
      'review': { id: 'review', title: 'Review', ticketIds: [] },
      'done': { id: 'done', title: 'Done', ticketIds: [] },
      'closed': { id: 'closed', title: 'Closed', ticketIds: [] }
    };

    tickets.forEach(ticket => {
      const status = ticket.status || 'new';
      if (columns[status]) {
        columns[status].ticketIds.push(ticket.id);
      } else {
        columns['new'].ticketIds.push(ticket.id);
      }
    });

    return columns;
  };

  const [columns, setColumns] = useState<Record<string, KanbanColumn>>(getInitialColumns);
  const [ticketsMap, setTicketsMap] = useState(() => {
    const map: Record<string, BetaTicket> = {};
    tickets.forEach(ticket => {
      map[ticket.id] = ticket;
    });
    return map;
  });

  const onDragEnd = (result: any) => {
    const { source, destination, draggableId } = result;

    if (!destination) return;
    if (source.droppableId === destination.droppableId && source.index === destination.index) return;

    // Remove from source column
    const sourceColumn = columns[source.droppableId];
    const sourceTicketIds = [...sourceColumn.ticketIds];
    sourceTicketIds.splice(source.index, 1);

    // Add to destination column
    const destinationColumn = columns[destination.droppableId];
    const destinationTicketIds = [...destinationColumn.ticketIds];
    destinationTicketIds.splice(destination.index, 0, draggableId);

    // Update columns state
    setColumns({
      ...columns,
      [source.droppableId]: {
        ...sourceColumn,
        ticketIds: sourceTicketIds
      },
      [destination.droppableId]: {
        ...destinationColumn,
        ticketIds: destinationTicketIds
      }
    });

    // Update ticket status in the database
    if (onStatusChange) {
      onStatusChange(draggableId, destination.droppableId);
    }
  };

  const formatDate = (dateString?: string) => {
    if (!dateString) return '';
    return new Date(dateString).toLocaleDateString();
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="overflow-x-auto p-4">
        <div className="flex space-x-4 min-w-fit">
          {Object.values(columns).map(column => (
            <div key={column.id} className="w-64 bg-gray-50 rounded-md p-2">
              <h3 className="font-medium mb-2">{column.title} ({column.ticketIds.length})</h3>
              <Droppable droppableId={column.id}>
                {(provided) => (
                  <div 
                    {...provided.droppableProps}
                    ref={provided.innerRef}
                    className="space-y-2 min-h-[100px]"
                  >
                    {column.ticketIds.map((ticketId, index) => {
                      const ticket = ticketsMap[ticketId];
                      if (!ticket) return null;

                      return (
                        <Draggable key={ticketId} draggableId={ticketId} index={index}>
                          {(provided) => (
                            <Card
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                              className={`
                                p-2 cursor-pointer
                                ${ticket.priority === 'high' ? 'border-l-4 border-l-red-500' : 
                                  ticket.priority === 'medium' ? 'border-l-4 border-l-yellow-500' :
                                  'border-l-4 border-l-blue-500'}`
                              }
                            >
                              <div className="text-sm font-medium">{ticket.title}</div>
                              <div className="text-xs text-gray-500 truncate">{ticket.description}</div>
                              <div className="flex justify-between mt-1">
                                <div className="text-xs">{formatDate(ticket.due_date)}</div>
                                <Button
                                  variant="ghost"
                                  size="sm"
                                  className="h-6 text-xs px-2"
                                  onClick={() => onTicketClick(ticket.id)}
                                >
                                  View
                                </Button>
                              </div>
                            </Card>
                          )}
                        </Draggable>
                      );
                    })}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </div>
          ))}
        </div>
      </div>
    </DragDropContext>
  );
}

import React, { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
  DialogClose
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

interface CreateTicketDialogProps {
  open: boolean;
  onClose: () => void;
  onCreateTicket: (ticket: any) => Promise<void>;
  projects: Array<{ project_id: string; project_title?: string; title?: string }>;
}

export const CreateTicketDialog: React.FC<CreateTicketDialogProps> = ({
  open,
  onClose,
  onCreateTicket,
  projects
}) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [priority, setPriority] = useState("medium");
  const [projectId, setProjectId] = useState("no-project");
  const [ticketType, setTicketType] = useState("task");
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!title.trim()) {
      return;
    }

    try {
      setIsSubmitting(true);

      await onCreateTicket({
        title,
        description,
        priority,
        project_id: projectId === "no-project" ? null : projectId,
        health: "good",
        ticket_type: ticketType
      });

      setTitle("");
      setDescription("");
      setPriority("medium");
      setProjectId("no-project");
      setTicketType("task");

    } catch (error) {
      console.error("Error creating ticket:", error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle>Create New Ticket</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <label htmlFor="title" className="text-sm font-medium">
              Title *
            </label>
            <Input
              id="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Enter ticket title"
              required
            />
          </div>

          <div className="space-y-2">
            <label htmlFor="description" className="text-sm font-medium">
              Description
            </label>
            <Textarea
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Provide additional details"
              rows={4}
            />
          </div>

          <div className="grid grid-cols-3 gap-4">
            <div className="space-y-2">
              <label htmlFor="priority" className="text-sm font-medium">
                Priority
              </label>
              <Select value={priority} onValueChange={setPriority}>
                <SelectTrigger id="priority">
                  <SelectValue placeholder="Select priority" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="low">Low</SelectItem>
                  <SelectItem value="medium">Medium</SelectItem>
                  <SelectItem value="high">High</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <label htmlFor="ticketType" className="text-sm font-medium">
                Ticket Type
              </label>
              <Select value={ticketType} onValueChange={setTicketType}>
                <SelectTrigger id="ticketType">
                  <SelectValue placeholder="Select type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="task">Task</SelectItem>
                  <SelectItem value="bug">Bug</SelectItem>
                  <SelectItem value="feature">Feature</SelectItem>
                  <SelectItem value="beta_testing">Beta Test</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <label htmlFor="project" className="text-sm font-medium">
                Project
              </label>
              <Select value={projectId} onValueChange={setProjectId}>
                <SelectTrigger id="project">
                  <SelectValue placeholder="Select project" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="no-project">No project</SelectItem>
                  {projects.map((project) => (
                    <SelectItem key={project.project_id} value={project.project_id}>
                      {project.project_title || project.title || 'Unnamed Project'}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          <DialogFooter>
            <DialogClose asChild>
              <Button type="button" variant="outline">
                Cancel
              </Button>
            </DialogClose>

            <Button type="submit" disabled={isSubmitting || !title.trim()}>
              {isSubmitting ? "Creating..." : "Create Ticket"}
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
};

import React, { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Image, Trash2 } from "lucide-react";
import { Ticket } from "@/types/types";
import { supabase } from "@/lib/supabase";
import { toast } from "sonner";
import { TicketService } from "@/components/ticket/TicketService";
import { TicketAttachmentsList, checkTicketAttachments } from "@/components/dashboard/TicketAttachmentsList";
import { TicketDetailsTab } from "./details/TicketDetailsTab";
import { TicketConversationTab } from "./details/TicketConversationTab";
import { TicketActivityTab } from "./details/TicketActivityTab";
import { TicketTimeLogTab } from "./details/TicketTimeLogTab";
import { DeleteTicketDialog } from "./details/DeleteTicketDialog";

interface ExpandedTicketDetailsProps {
  ticket: Ticket;
  onClose?: () => void;
  onTicketAction?: (ticketId: string, action: string, data: any) => Promise<void>;
  onLogTime?: (ticketId: string) => void;
  userCanEditStatus?: boolean;
  userCanEditDates?: boolean;
  onRefresh?: () => void;
}

export const ExpandedTicketDetails: React.FC<ExpandedTicketDetailsProps> = ({
  ticket: initialTicket,
  onClose,
  onTicketAction = async () => {},
  onLogTime,
  userCanEditStatus = true,
  userCanEditDates = true,
  onRefresh
}) => {
  // Keep local copy of ticket that we can update immediately
  const [localTicket, setLocalTicket] = useState<Ticket>(initialTicket);
  const [activeTab, setActiveTab] = useState("details");
  const [hasAttachments, setHasAttachments] = useState(false);
  const [isCheckingAttachments, setIsCheckingAttachments] = useState(true);
  const [deleteDialogOpen, setIsDeleteDialogOpen] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);
  const [deleteErrorMessage, setDeleteErrorMessage] = useState<string | undefined>();

  // Update local ticket when props change
  useEffect(() => {
    setLocalTicket(initialTicket);
  }, [initialTicket]);

  useEffect(() => {
    if (localTicket.id) {
      checkForAttachments();
    }
  }, [localTicket.id]);

  const checkForAttachments = async () => {
    setIsCheckingAttachments(true);
    const attachmentsExist = await checkTicketAttachments(localTicket.reporter, localTicket.id);
    setHasAttachments(attachmentsExist);
    setIsCheckingAttachments(false);
  };

  const handleAttachmentsLoaded = (hasAttachments: boolean) => {
    setHasAttachments(hasAttachments);
    setIsCheckingAttachments(false);
  };

  const handleTicketAction = async (ticketId: string, action: string, data: any): Promise<void> => {
    try {
      // Update local state immediately for better UX
      setLocalTicket(prev => {
        // Handle different action types
        switch (action) {
          case "updateStatus":
            return { ...prev, status: data };
          case "updatePriority":
            return { ...prev, priority: data };
          case "updateDueDate":
            return { ...prev, due_date: data };
          case "updateEstimatedHours":
            return { ...prev, estimated_hours: data };
          case "updateCompletionPercentage":
            return { ...prev, completion_percentage: data };
          case "updateDescription":
            return { ...prev, description: data };
          default:
            return prev;
        }
      });

      // Call the parent handler (API update)
      await onTicketAction(ticketId, action, data);

      // Refresh data if onRefresh is provided
      if (onRefresh) {
        onRefresh();
      }
    } catch (error) {
      console.error(`Error in ${action}:`, error);
    }
  };

  // Force refresh of child components
  const handleDataChanged = () => {
    if (onRefresh) {
      onRefresh();
    }
  };

  // Check if delete button should be disabled
  const isDeleteDisabled = localTicket.completion_percentage > 0 || 
                          (localTicket.hours_logged && localTicket.hours_logged > 0);

  const handleDeleteTicket = async () => {
    setIsDeleting(true);
    setDeleteErrorMessage(undefined);
    try {
      // Get the current user ID
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        throw new Error("User not authenticated");
      }

      // Check if the ticket has time entries or completion progress
      if (localTicket.completion_percentage > 0) {
        throw new Error("Cannot delete ticket with completion progress");
      }

      if (localTicket.hours_logged && localTicket.hours_logged > 0) {
        throw new Error("Cannot delete ticket with time entries");
      }

      // Use the TicketService for consistent deletion logic
      const success = await TicketService.deleteTicket(localTicket.id, user.id);

      if (!success) {
        throw new Error("Failed to delete ticket");
      }

      // If successful, close the dialog and refresh
      toast.success("Ticket deleted successfully");
      if (onClose) onClose();
      if (onRefresh) onRefresh();

    } catch (error: any) {
      console.error("Error deleting ticket:", error);
      const errorMessage = error?.message || "Failed to delete ticket. Please try again.";
      setDeleteErrorMessage(errorMessage);
      throw error; // Re-throw the error to be caught by the DeleteTicketDialog
    } finally {
      setIsDeleting(false);
    }
  };

  return (
    <div className="max-h-[80vh] overflow-y-auto">
      <div className="flex justify-between items-start mb-4">
        <h2 className="text-xl font-bold">{localTicket.title}</h2>
        <div className="flex gap-2">
          <Button 
            variant="destructive" 
            size="sm" 
            onClick={() => {
              if (isDeleteDisabled) {
                const reason = localTicket.completion_percentage > 0 
                  ? "Cannot delete ticket with completion progress" 
                  : "Cannot delete ticket with time entries";
                toast.error(reason);
                return;
              }
              setIsDeleteDialogOpen(true);
            }}
            disabled={isDeleteDisabled}
            title={isDeleteDisabled ? 
              (localTicket.completion_percentage > 0 ? 
                "Cannot delete ticket with completion progress" : 
                "Cannot delete ticket with time entries") : 
              "Delete ticket"}
          >
            <Trash2 className="h-4 w-4 mr-1" />
            Delete
          </Button>
          <Button variant="outline" size="sm" onClick={onClose}>
            Close
          </Button>
        </div>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="mb-4">
          <TabsTrigger value="details">Details</TabsTrigger>
          <TabsTrigger value="conversation">Conversation</TabsTrigger>
          <TabsTrigger value="activity-log">Activity Log</TabsTrigger>
          {(hasAttachments || isCheckingAttachments) && (
            <TabsTrigger value="attachments">
              <div className="flex items-center">
                <Image className="h-4 w-4 mr-1" />
                Attachments
                {isCheckingAttachments && (
                  <span className="ml-1 h-3 w-3 rounded-full bg-gray-200 animate-pulse"></span>
                )}
              </div>
            </TabsTrigger>
          )}
          <TabsTrigger value="time-log">Time Log</TabsTrigger>
        </TabsList>

        <TabsContent value="details">
          <TicketDetailsTab 
            ticket={localTicket}
            onTicketAction={handleTicketAction}
            onLogTime={onLogTime}
            userCanEditStatus={userCanEditStatus}
            userCanEditDates={userCanEditDates}
          />
        </TabsContent>

        <TabsContent value="conversation">
          <TicketConversationTab 
            ticket={localTicket}
            onTicketAction={handleTicketAction}
            onDataChanged={handleDataChanged}
          />
        </TabsContent>

        <TabsContent value="activity-log">
          <TicketActivityTab 
            ticket={localTicket}
            onTicketAction={handleTicketAction}
            onDataChanged={handleDataChanged}
          />
        </TabsContent>

        <TabsContent value="attachments" className="mt-0">
          <TicketAttachmentsList 
            reporterId={localTicket.reporter} 
            ticketId={localTicket.id}
            onAttachmentsLoaded={handleAttachmentsLoaded}
          />
        </TabsContent>

        <TabsContent value="time-log">
          <TicketTimeLogTab 
            ticketId={localTicket.id}
            onLogTime={onLogTime}
            onDataChanged={handleDataChanged}
          />
        </TabsContent>
      </Tabs>

      <DeleteTicketDialog
        open={deleteDialogOpen}
        onOpenChange={setIsDeleteDialogOpen}
        onConfirm={handleDeleteTicket}
        isDeleting={isDeleting}
        ticketTitle={localTicket.title}
        errorMessage={deleteErrorMessage}
      />
    </div>
  );
};

import React, { useState } from "react";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";

interface FilterBarProps {
  statusFilter?: string;
  setStatusFilter?: (status: string) => void;
  priorityFilter?: string;
  setPriorityFilter?: (priority: string) => void;
  dueDateFilter?: string;
  setDueDateFilter?: (date: string) => void;
  searchQuery?: string;
  setSearchQuery?: (query: string) => void;
  onClearFilters?: () => void;
  // Add the new prop for compatibility with LiveProjectsTab
  onFilterChange?: (newFilters: any) => void;
  // Add the arrays for statuses and priorities
  statuses?: string[];
  priorities?: string[];
}

export const FilterBar: React.FC<FilterBarProps> = ({
  statusFilter = "all",
  setStatusFilter = () => {},
  priorityFilter = "all",
  setPriorityFilter = () => {},
  dueDateFilter = "",
  setDueDateFilter = () => {},
  searchQuery = "",
  setSearchQuery = () => {},
  onClearFilters = () => {},
  onFilterChange,
  statuses = ['all', 'new', 'in-progress', 'blocked', 'review', 'done', 'closed'],
  priorities = ['all', 'low', 'medium', 'high']
}) => {
  const [filters, setFilters] = useState({
    status: statusFilter,
    priority: priorityFilter
  });

  const handleStatusChange = (status: string) => {
    setStatusFilter(status);
    const newFilters = { ...filters, status };
    setFilters(newFilters);
    // Call the new onFilterChange prop if it exists
    if (onFilterChange) {
      onFilterChange(newFilters);
    }
  };

  const handlePriorityChange = (priority: string) => {
    setPriorityFilter(priority);
    const newFilters = { ...filters, priority };
    setFilters(newFilters);
    // Call the new onFilterChange prop if it exists
    if (onFilterChange) {
      onFilterChange(newFilters);
    }
  };

  const handleClearFilters = () => {
    setStatusFilter("all");
    setPriorityFilter("all");
    setDueDateFilter("");
    setSearchQuery("");
    onClearFilters();

    const newFilters = { status: "all", priority: "all" };
    setFilters(newFilters);
    // Call the new onFilterChange prop if it exists
    if (onFilterChange) {
      onFilterChange(newFilters);
    }
  };

  return (
    <div className="bg-gray-50 p-4 rounded-lg mb-6">
      <h3 className="text-sm font-medium mb-3">Filter Tickets</h3>
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div>
          <Label htmlFor="status-filter" className="text-xs block mb-1">Status</Label>
          <Select value={statusFilter} onValueChange={handleStatusChange}>
            <SelectTrigger id="status-filter" className="w-full">
              <SelectValue placeholder="All Statuses" />
            </SelectTrigger>
            <SelectContent>
              {statuses.map((status) => (
                <SelectItem key={status} value={status}>{status === 'all' ? 'All Statuses' : status}</SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div>
          <Label htmlFor="priority-filter" className="text-xs block mb-1">Priority</Label>
          <Select value={priorityFilter} onValueChange={handlePriorityChange}>
            <SelectTrigger id="priority-filter" className="w-full">
              <SelectValue placeholder="All Priorities" />
            </SelectTrigger>
            <SelectContent>
              {priorities.map((priority) => (
                <SelectItem key={priority} value={priority}>{priority === 'all' ? 'All Priorities' : priority}</SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div>
          <Label htmlFor="due-date-filter" className="text-xs block mb-1">Due Date</Label>
          <Input
            id="due-date-filter"
            type="date"
            value={dueDateFilter}
            onChange={(e) => setDueDateFilter(e.target.value)}
            className="w-full"
          />
        </div>

        <div>
          <Label htmlFor="search-query" className="text-xs block mb-1">Search</Label>
          <div className="flex gap-2">
            <Input
              id="search-query"
              placeholder="Search tickets..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full"
            />
            <Button variant="outline" size="sm" onClick={handleClearFilters} className="whitespace-nowrap">
              Clear Filters
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
};

