import React from "react";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";
import { Ticket } from "@/types/types";
import { Button } from "@/components/ui/button";
import { Clock, MoreHorizontal, CheckCircle, Trash } from "lucide-react";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";

export interface KanbanBoardProps {
  tickets: Ticket[];
  onStatusChange: (ticketId: string, newStatus: string) => void;
  onTicketClick?: (ticket: Ticket) => void;  
  onTicketDelete?: (ticket: Ticket) => void;
}

export const KanbanBoard: React.FC<KanbanBoardProps> = ({ 
  tickets, 
  onStatusChange, 
  onTicketClick,
  onTicketDelete
}) => {
  const columns = [
    { id: 'todo', title: 'To Do' },
    { id: 'in-progress', title: 'In Progress' },
    { id: 'review', title: 'Review' },
    { id: 'done', title: 'Done' },
    { id: 'closed', title: 'Closed' }
  ];

  const formatDate = (dateString?: string) => {
    if (!dateString) return 'No due date';
    return new Date(dateString).toLocaleDateString();
  };

  // Function to get ticket count for a specific status
  const getTicketCount = (status: string) => {
    return tickets.filter(ticket => ticket.status === status).length;
  };

  // Function to determine priority badge color
  const getPriorityColor = (priority: string) => {
    if (priority === 'high') return 'bg-red-100 text-red-800';
    if (priority === 'medium') return 'bg-yellow-100 text-yellow-800';
    return 'bg-green-100 text-green-800';
  };

  // Determine if a ticket is overdue
  const isOverdue = (ticket: Ticket) => {
    if (!ticket.due_date) return false;
    const dueDate = new Date(ticket.due_date);
    const today = new Date();
    return dueDate < today;
  };

  // Handle drag end event
  const handleDragEnd = (result: any) => {
    if (!result.destination) return;

    const { draggableId, destination } = result;
    const newStatus = destination.droppableId;

    // Call the onStatusChange callback to update the ticket status
    onStatusChange(draggableId, newStatus);
  };

  // Sort tickets based on priority and due date
  const sortTickets = (tickets: Ticket[]) => {
    return [...tickets].sort((a, b) => {
      // First sort by priority
      const priorityOrder = { high: 0, medium: 1, low: 2 };
      const aPriority = priorityOrder[a.priority as keyof typeof priorityOrder] || 999;
      const bPriority = priorityOrder[b.priority as keyof typeof priorityOrder] || 999;

      if (aPriority !== bPriority) {
        return aPriority - bPriority;
      }

      // Then sort by due date if available
      if (a.due_date && b.due_date) {
        return new Date(a.due_date).getTime() - new Date(b.due_date).getTime();
      }

      // If no due date, tickets with due dates come first
      if (a.due_date && !b.due_date) return -1;
      if (!a.due_date && b.due_date) return 1;

      // Finally sort by creation date
      if (a.created_at && b.created_at) {
        return new Date(b.created_at).getTime() - new Date(a.created_at).getTime();
      }

      return 0;
    });
  };

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
        {columns.map(column => {
          // Map statuses that might be in different formats
          let columnTickets = tickets.filter(ticket => {
            if (ticket.status === column.id) return true;

            // Handle alternative status formats
            if (column.id === 'in-progress' && (ticket.status === 'in_progress' || ticket.status === 'in progress')) return true;
            if (column.id === 'todo' && (ticket.status === 'new' || ticket.status === 'open' || ticket.status === 'backlog')) return true;

            return false;
          });

          return (
            <Droppable key={column.id} droppableId={column.id}>
              {(provided) => (
                <div 
                  {...provided.droppableProps}
                  ref={provided.innerRef}
                  className="bg-gray-50 p-3 rounded-lg min-h-[300px]"
                >
                  <h3 className="font-medium text-sm mb-3">{column.title} ({columnTickets.length})</h3>

                  {sortTickets(columnTickets).map((ticket, index) => (
                    <Draggable key={ticket.id} draggableId={ticket.id} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="bg-white mb-2 p-3 rounded-md shadow-sm border border-gray-100"
                        >
                          <div className="flex justify-between items-start">
                            <h4 
                              className="font-medium cursor-pointer hover:text-blue-600"
                              onClick={() => onTicketClick && onTicketClick(ticket)}
                            >
                              {ticket.title}
                            </h4>
                            <div className={`text-xs font-medium px-2 py-1 rounded-full ${
                              getPriorityColor(ticket.priority)
                            }`}>
                              {ticket.priority}
                            </div>
                          </div>

                          <p className="text-xs text-gray-500 line-clamp-2 mt-1">{ticket.description}</p>

                          {ticket.ticket_type && (
                            <div className="mt-2 flex items-center text-xs text-blue-600">
                              <CheckCircle className="h-3 w-3 mr-1" />
                              <span>{ticket.ticket_type}</span>
                              {ticket.equity_points !== undefined && ticket.equity_points > 0 && (
                                <span className="ml-1">(Equity: {ticket.equity_points}%)</span>
                              )}
                            </div>
                          )}

                          {ticket.due_date && (
                            <div className="mt-2 flex items-center text-xs text-gray-500">
                              <Clock className="h-3 w-3 mr-1" />
                              <span>Due: {formatDate(ticket.due_date)}</span>
                            </div>
                          )}

                          <div className="mt-2 flex justify-end gap-1">
                            {onTicketDelete && (
                              <AlertDialog>
                                <AlertDialogTrigger asChild>
                                  <Button 
                                    variant="ghost" 
                                    size="sm" 
                                    className="h-5 w-5 p-0 text-red-500 hover:text-red-700 hover:bg-red-50" 
                                    onClick={(e) => e.stopPropagation()}
                                  >
                                    <Trash className="h-3 w-3" />
                                  </Button>
                                </AlertDialogTrigger>
                                <AlertDialogContent>
                                  <AlertDialogHeader>
                                    <AlertDialogTitle>Are you sure?</AlertDialogTitle>
                                    <AlertDialogDescription>
                                      This action cannot be undone. This will permanently delete the ticket
                                      and all associated data.
                                    </AlertDialogDescription>
                                  </AlertDialogHeader>
                                  <AlertDialogFooter>
                                    <AlertDialogCancel>Cancel</AlertDialogCancel>
                                    <AlertDialogAction 
                                      onClick={() => onTicketDelete(ticket)} 
                                      className="bg-red-500 hover:bg-red-600"
                                    >
                                      Delete
                                    </AlertDialogAction>
                                  </AlertDialogFooter>
                                </AlertDialogContent>
                              </AlertDialog>
                            )}

                            <Button 
                              variant="ghost" 
                              size="sm" 
                              className="h-5 w-5 p-0" 
                              onClick={(e) => {
                                e.stopPropagation();
                                onTicketClick && onTicketClick(ticket);
                              }}
                            >
                              <MoreHorizontal className="h-3 w-3" />
                            </Button>
                          </div>
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          );
        })}
      </div>
    </DragDropContext>
  );
};

import { useState } from "react";
import { Card, CardHeader, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { ChevronDown, ChevronRight, MessageSquare, Calendar, CheckCircle, Trash2 } from "lucide-react";
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { formatDistance } from "date-fns";
import { toast } from "sonner";
import { supabase } from "@/lib/supabase";

interface TicketCardProps {
  ticket: {
    id: string;
    title: string;
    description: string;
    status: string;
    priority: string;
    health: string;
    assigned_to?: string;
    reporter?: string;
    due_date?: string;
    created_at: string;
    updated_at: string;
    estimated_hours?: number;
    equity_points?: number;
    equity_agreed?: number;
    equity_allocated?: number;
    completion_percentage?: number;
    assigned_user?: {
      email?: string;
    };
    reporter_user?: {
      email?: string;
    };
    project?: {
      title?: string;
    };
    accepted_jobs?: {
      equity_agreed?: number;
      jobs_equity_allocated?: number;
    };
  };
  onTicketUpdated?: (updates: any) => void;
  onTicketAction?: (ticketId: string, action: string, data: any) => Promise<void>;
  renderTicketActions?: (ticket: any) => React.ReactNode;
}

export const TicketCard = ({ ticket, onTicketUpdated, onTicketAction, renderTicketActions }: TicketCardProps) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [comments, setComments] = useState<any[]>([]);
  const [newComment, setNewComment] = useState("");
  const [isLoadingComments, setIsLoadingComments] = useState(false);

  const toggleExpand = async () => {
    setIsExpanded(!isExpanded);

    if (!isExpanded && comments.length === 0) {
      await loadComments();
    }
  };

  const loadComments = async () => {
    setIsLoadingComments(true);
    try {
      const { data, error } = await supabase
        .from('ticket_comments')
        .select(`
          id,
          content,
          created_at,
          user_id,
          profiles:user_id(first_name, last_name, email)
        `)
        .eq('ticket_id', ticket.id)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setComments(data || []);
    } catch (error) {
      console.error('Error loading comments:', error);
      toast.error("Failed to load comments");
    } finally {
      setIsLoadingComments(false);
    }
  };

  const handleStatusChange = async (newValue: string) => {
    setIsUpdating(true);
    try {
      const { error } = await supabase
        .from('tickets')
        .update({ status: newValue })
        .eq('id', ticket.id);

      if (error) throw error;
      toast.success("Ticket status updated");
      if (onTicketUpdated) onTicketUpdated({ status: newValue });
    } catch (error) {
      console.error('Error updating ticket status:', error);
      toast.error("Failed to update ticket status");
    } finally {
      setIsUpdating(false);
    }
  };

  const handlePriorityChange = async (newValue: string) => {
    setIsUpdating(true);
    try {
      const { error } = await supabase
        .from('tickets')
        .update({ priority: newValue })
        .eq('id', ticket.id);

      if (error) throw error;
      toast.success("Ticket priority updated");
      if (onTicketUpdated) onTicketUpdated({ priority: newValue });
    } catch (error) {
      console.error('Error updating ticket priority:', error);
      toast.error("Failed to update ticket priority");
    } finally {
      setIsUpdating(false);
    }
  };

  const handleHealthChange = async (newValue: string) => {
    setIsUpdating(true);
    try {
      const { error } = await supabase
        .from('tickets')
        .update({ health: newValue })
        .eq('id', ticket.id);

      if (error) throw error;
      toast.success("Ticket health updated");
      if (onTicketUpdated) onTicketUpdated({ health: newValue });
    } catch (error) {
      console.error('Error updating ticket health:', error);
      toast.error("Failed to update ticket health");
    } finally {
      setIsUpdating(false);
    }
  };

  const handleAddComment = async () => {
    if (!newComment.trim()) return;

    try {
      const { data: { session } } = await supabase.auth.getSession();
      if (!session) {
        toast.error("You must be logged in to add comments");
        return;
      }

      const { error } = await supabase
        .from('ticket_comments')
        .insert({
          ticket_id: ticket.id,
          content: newComment,
          user_id: session.user.id
        });

      if (error) throw error;

      setNewComment("");
      toast.success("Comment added");
      await loadComments();
    } catch (error) {
      console.error('Error adding comment:', error);
      toast.error("Failed to add comment");
    }
  };

  const handleDeleteTicket = async () => {
    if (window.confirm('Are you sure you want to delete this ticket?')) {
      try {
        if (onTicketAction) {
          await onTicketAction(ticket.id, 'delete', null);
        } else {
          // Fallback delete logic
          const { error } = await supabase
            .from('tickets')
            .delete()
            .eq('id', ticket.id);

          if (error) throw error;
          toast.success('Ticket deleted successfully');
        }
      } catch (error) {
        console.error('Error deleting ticket:', error);
        toast.error('Failed to delete ticket');
      }
    }
  };

  const getHealthBadgeClass = (health: string) => {
    switch (health.toLowerCase()) {
      case 'green': return 'bg-green-100 text-green-800 hover:bg-green-200';
      case 'amber': return 'bg-yellow-100 text-yellow-800 hover:bg-yellow-200';
      case 'red': return 'bg-red-100 text-red-800 hover:bg-red-200';
      default: return 'bg-slate-100 text-slate-800 hover:bg-slate-200';
    }
  };

  const getPriorityBadgeClass = (priority: string) => {
    switch (priority.toLowerCase()) {
      case 'critical': return 'bg-red-100 text-red-800 hover:bg-red-200';
      case 'high': return 'bg-orange-100 text-orange-800 hover:bg-orange-200';
      case 'medium': return 'bg-blue-100 text-blue-800 hover:bg-blue-200';
      case 'low': return 'bg-green-100 text-green-800 hover:bg-green-200';
      default: return 'bg-slate-100 text-slate-800 hover:bg-slate-200';
    }
  };

  const getStatusBadgeClass = (status: string) => {
    switch (status.toLowerCase()) {
      case 'backlog': return 'bg-slate-100 text-slate-800 hover:bg-slate-200';
      case 'todo': return 'bg-blue-100 text-blue-800 hover:bg-blue-200';
      case 'in_progress': return 'bg-purple-100 text-purple-800 hover:bg-purple-200';
      case 'review': return 'bg-yellow-100 text-yellow-800 hover:bg-yellow-200';
      case 'done': return 'bg-green-100 text-green-800 hover:bg-green-200';
      default: return 'bg-slate-100 text-slate-800 hover:bg-slate-200';
    }
  };

  const formatDate = (dateString?: string) => {
    if (!dateString) return 'Not set';
    return new Date(dateString).toLocaleDateString();
  };

  const timeAgo = (dateString: string) => {
    try {
      return formatDistance(new Date(dateString), new Date(), { addSuffix: true });
    } catch (error) {
      return 'Unknown';
    }
  };

  // Ensure we have valid values for the select components
  const safeStatus = ticket.status || "backlog";
  const safePriority = ticket.priority || "medium";
  const safeHealth = ticket.health || "green";

  return (
    <Collapsible
      open={isExpanded}
      onOpenChange={setIsExpanded}
      className="border rounded-lg shadow-sm mb-4 bg-white"
    >
      <div className="p-4 flex items-center justify-between">
        <div className="flex items-center gap-2">
          <CollapsibleTrigger asChild>
            <Button variant="ghost" size="sm" className="p-1 h-auto">
              {isExpanded ? <ChevronDown className="h-4 w-4" /> : <ChevronRight className="h-4 w-4" />}
            </Button>
          </CollapsibleTrigger>
          <span className="font-medium truncate max-w-[300px]">{ticket.title}</span>
        </div>

        <div className="flex items-center gap-2">
          <div>
            <Badge className={getHealthBadgeClass(ticket.health)}>
              {ticket.health || "N/A"}
            </Badge>
          </div>

          <div>
            <Badge className={getPriorityBadgeClass(ticket.priority)}>
              {ticket.priority || "N/A"}
            </Badge>
          </div>

          <div>
            <Badge className={getStatusBadgeClass(ticket.status)}>
              {ticket.status ? ticket.status.replace('_', ' ') : "N/A"}
            </Badge>
          </div>

          {ticket.due_date && (
            <div className="flex items-center text-xs text-gray-500">
              <Calendar className="h-3 w-3 mr-1" />
              {formatDate(ticket.due_date)}
            </div>
          )}

          <Button 
            variant="ghost" 
            size="sm" 
            onClick={(e) => {
              e.stopPropagation();
              handleDeleteTicket();
            }}
            className="p-1 h-6 w-6 text-gray-400 hover:text-red-500"
          >
            <Trash2 className="h-3 w-3" />
          </Button>

          {renderTicketActions && renderTicketActions(ticket)}
        </div>
      </div>

      <CollapsibleContent>
        <div className="border-t p-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
            <div>
              <h3 className="text-sm font-medium mb-2">Description</h3>
              <div className="bg-slate-50 rounded p-3 text-sm whitespace-pre-wrap">
                {ticket.description || "No description provided."}
              </div>
            </div>

            <div className="space-y-4">
              <div>
                <h3 className="text-sm font-medium mb-2">Details</h3>
                <div className="grid grid-cols-2 gap-3">
                  <div>
                    <p className="text-xs text-gray-500">Status</p>
                    <Select
                      value={safeStatus}
                      onValueChange={handleStatusChange}
                      disabled={isUpdating}
                    >
                      <SelectTrigger className="w-full h-8 text-xs">
                        <SelectValue placeholder="Select status" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="backlog">Backlog</SelectItem>
                        <SelectItem value="todo">To Do</SelectItem>
                        <SelectItem value="in_progress">In Progress</SelectItem>
                        <SelectItem value="review">Review</SelectItem>
                        <SelectItem value="done">Done</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <p className="text-xs text-gray-500">Priority</p>
                    <Select
                      value={safePriority}
                      onValueChange={handlePriorityChange}
                      disabled={isUpdating}
                    >
                      <SelectTrigger className="w-full h-8 text-xs">
                        <SelectValue placeholder="Select priority" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="critical">Critical</SelectItem>
                        <SelectItem value="high">High</SelectItem>
                        <SelectItem value="medium">Medium</SelectItem>
                        <SelectItem value="low">Low</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <p className="text-xs text-gray-500">Health</p>
                    <Select
                      value={safeHealth}
                      onValueChange={handleHealthChange}
                      disabled={isUpdating}
                    >
                      <SelectTrigger className="w-full h-8 text-xs">
                        <SelectValue placeholder="Select health" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="green">Green</SelectItem>
                        <SelectItem value="amber">Amber</SelectItem>
                        <SelectItem value="red">Red</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <p className="text-xs text-gray-500">Assignee</p>
                    <p className="text-sm truncate">{ticket.assigned_user?.email || "Unassigned"}</p>
                  </div>

                  <div>
                    <p className="text-xs text-gray-500">Reporter</p>
                    <p className="text-sm truncate">{ticket.reporter_user?.email || "Unknown"}</p>
                  </div>

                  <div>
                    <p className="text-xs text-gray-500">Project</p>
                    <p className="text-sm truncate">{ticket.project?.title || "None"}</p>
                  </div>

                  <div>
                    <p className="text-xs text-gray-500">Completion</p>
                    <p className="text-sm">{ticket.completion_percentage || 0}%</p>
                  </div>

                  <div>
                    <p className="text-xs text-gray-500">Equity</p>
                    <p className="text-sm">
                      {(ticket.equity_agreed || ticket.accepted_jobs?.equity_agreed || ticket.equity_points || 0)}% 
                      {ticket.accepted_jobs?.jobs_equity_allocated && ` (${ticket.accepted_jobs.jobs_equity_allocated}% allocated)`}
                    </p>
                  </div>

                  <div>
                    <p className="text-xs text-gray-500">Created</p>
                    <p className="text-sm">{timeAgo(ticket.created_at)}</p>
                  </div>

                  <div>
                    <p className="text-xs text-gray-500">Due Date</p>
                    <p className="text-sm">{formatDate(ticket.due_date)}</p>
                  </div>
                </div>
              </div>

              <div>
                <h3 className="text-sm font-medium mb-2">Comments</h3>
                <div className="bg-slate-50 rounded p-3 max-h-60 overflow-y-auto space-y-3">
                  {isLoadingComments ? (
                    <p className="text-center text-sm py-2">Loading comments...</p>
                  ) : comments.length > 0 ? (
                    comments.map(comment => (
                      <div key={comment.id} className="border-b pb-2 last:border-b-0 last:pb-0">
                        <div className="flex justify-between items-center mb-1">
                          <p className="text-xs font-medium">
                            {comment.profiles?.first_name || comment.profiles?.email || "User"}
                          </p>
                          <p className="text-xs text-gray-500">{timeAgo(comment.created_at)}</p>
                        </div>
                        <p className="text-sm">{comment.content}</p>
                      </div>
                    ))
                  ) : (
                    <p className="text-center text-sm py-2 text-gray-500">No comments yet</p>
                  )}
                </div>

                <div className="mt-3 flex gap-2">
                  <input
                    type="text"
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Add a comment..."
                    className="flex-1 px-3 py-1 text-sm border rounded"
                  />
                  <Button 
                    size="sm" 
                    onClick={handleAddComment}
                    className="h-8"
                    disabled={!newComment.trim()}
                  >
                    <MessageSquare className="h-4 w-4 mr-1" />
                    Add
                  </Button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </CollapsibleContent>
    </Collapsible>
  );
};

import React, { useState, useEffect } from 'react';
import { Ticket } from '@/types/types';
import { Pagination } from "@/components/ui/pagination";
import { useTicketDashboard } from './hooks/useTicketDashboard';
import { TicketFilters } from './filters/TicketFilters';
import { EmptyTicketState } from './empty/EmptyTicketState';
import { TicketTable } from './table/TicketTable';
import { TicketDetailDialog } from './dialogs/TicketDetailDialog';
import { DeleteTicketDialog } from './dialogs/DeleteTicketDialog';
import { toast } from "sonner";
import { supabase } from "@/lib/supabase";

interface TicketDashboardProps {
  initialTickets: Ticket[];
  onRefresh: () => void;
  onTicketAction: (ticketId: string, action: string, data: any) => Promise<void>;
  showTimeTracking?: boolean;
  userId: string;
  onLogTime?: (ticketId: string) => void;
  renderTicketActions?: (ticket: Ticket) => React.ReactNode;
  expandedTickets: Set<string>;
  toggleTicketExpansion: (ticketId: string) => void;
  userCanEditDates?: boolean;
  userCanEditStatus?: boolean;
  loading?: boolean;
}

export const TicketDashboard: React.FC<TicketDashboardProps> = ({
  initialTickets,
  onRefresh,
  onTicketAction,
  showTimeTracking = false,
  userId,
  onLogTime,
  renderTicketActions,
  expandedTickets = new Set<string>(),
  toggleTicketExpansion = () => {},
  userCanEditDates = false,
  userCanEditStatus = false,
  loading = false
}) => {
  const [isDeleting, setIsDeleting] = useState(false);

  const {
    tickets,
    setTickets,
    displayedTickets,
    searchTerm,
    setSearchTerm,
    statusFilter,
    setStatusFilter,
    priorityFilter,
    setPriorityFilter,
    typeFilter,
    setTypeFilter,
    currentPage,
    setCurrentPage,
    totalPages,
    selectedTicket,
    isDialogOpen,
    openTicketDetails,
    closeTicketDetails,
    ticketToDelete,
    isDeleteDialogOpen,
    showDeleteConfirmation,
    cancelDelete
  } = useTicketDashboard(initialTickets);

  // Add useEffect to fetch and update time entries for all tickets
  useEffect(() => {
    if (showTimeTracking && tickets.length > 0) {
      updateTicketsWithTimeEntries();
    }
  }, [showTimeTracking, tickets.length]);

  // Function to fetch time entries from Supabase and update ticket hours
  const updateTicketsWithTimeEntries = async () => {
    try {
      // Get all unique ticket IDs
      const ticketIds = tickets.map(ticket => ticket.id);

      // Fetch time entries for all tickets in one query
      const { data: timeEntries, error } = await supabase
        .from('time_entries')
        .select('ticket_id, hours_logged')
        .in('ticket_id', ticketIds);

      if (error) throw error;

      // Calculate total hours for each ticket
      const ticketHours = {};
      timeEntries?.forEach(entry => {
        if (!ticketHours[entry.ticket_id]) {
          ticketHours[entry.ticket_id] = 0;
        }
        ticketHours[entry.ticket_id] += entry.hours_logged;
      });

      // Update tickets with the calculated hours
      const updatedTickets = tickets.map(ticket => {
        return {
          ...ticket,
          hours_logged: ticketHours[ticket.id] || 0
        };
      });

      setTickets(updatedTickets);
    } catch (error) {
      console.error('Error fetching time entries for tickets:', error);
    }
  };

  // Update the existing handleTicketAction function to handle soft deletion
  const handleTicketAction = async (ticketId: string, action: string, data: any) => {
    try {
      if (action === 'deleteTicket') {
        // Special handling for deletion
        try {
          // Pass the userId to the deleteTicket function
          await onTicketAction(ticketId, action, userId);

          // Filter out deleted tickets (those with status 'deleted') from the UI
          setTickets(tickets.filter(ticket => ticket.id !== ticketId || ticket.status !== 'deleted'));
          closeTicketDetails();
          toast.success("Ticket deleted successfully");
        } catch (error: any) {
          console.error("Error deleting ticket:", error);
          // Show specific error message
          if (error.message) {
            toast.error(error.message);
          } else if (error.code === 'P0001') {
            const msg = error.message || "Cannot delete this ticket";
            toast.error(msg);
          } else {
            toast.error("Failed to delete ticket");
          }
          throw error; // Re-throw to prevent further processing
        }
        return;
      }

      // For all other actions
      await onTicketAction(ticketId, action, data);

      // Update the local ticket state
      const ticketIndex = tickets.findIndex(t => t.id === ticketId);
      if (ticketIndex !== -1) {
        const updatedTickets = [...tickets];

        switch (action) {
          case 'updateStatus':
            updatedTickets[ticketIndex] = { ...updatedTickets[ticketIndex], status: data };
            break;
          case 'updatePriority':
            updatedTickets[ticketIndex] = { ...updatedTickets[ticketIndex], priority: data };
            break;
          case 'updateCompletionPercentage':
            updatedTickets[ticketIndex] = { ...updatedTickets[ticketIndex], completion_percentage: data };
            break;
          case 'updateEstimatedHours':
            updatedTickets[ticketIndex] = { ...updatedTickets[ticketIndex], estimated_hours: data };
            break;
          case 'updateDueDate':
            updatedTickets[ticketIndex] = { ...updatedTickets[ticketIndex], due_date: data };
            break;
          case 'logTime':
            // After logging time, update the time entries
            if (showTimeTracking) {
              setTimeout(() => updateTicketsWithTimeEntries(), 500);
            }
            break;
          default:
            break;
        }

        setTickets(updatedTickets);
        const actionName = action.replace('update', '');
        const formattedAction = actionName.charAt(0).toUpperCase() + actionName.slice(1).toLowerCase();
        toast.success(`${formattedAction} updated successfully`);
      }
    } catch (error: any) {
      // Only show error if it wasn't already handled in the deleteTicket block
      if (action !== 'deleteTicket') {
        console.error(`Error with ticket action ${action}:`, error);
        toast.error(`Failed to ${action.replace('update', '').toLowerCase()} ticket`);
      }
    }
  };

  // Custom onLogTime handler that updates time entries after logging
  const handleLogTime = (ticketId: string) => {
    if (onLogTime) {
      onLogTime(ticketId);
      // Update time entries after a slight delay to allow for the time entry to be saved
      setTimeout(() => updateTicketsWithTimeEntries(), 1000);
    }
  };

  const handleUpdateStatus = async (ticketId: string, status: string) => {
    try {
      await onTicketAction(ticketId, "updateStatus", status);
      setTickets(
        tickets.map((ticket) =>
          ticket.id === ticketId ? { ...ticket, status } : ticket
        )
      );
      toast.success("Status updated successfully");
    } catch (error) {
      console.error("Error updating status:", error);
      toast.error("Failed to update status");
    }
  };

  const handleUpdatePriority = async (ticketId: string, priority: string) => {
    try {
      await onTicketAction(ticketId, "updatePriority", priority);
      setTickets(
        tickets.map((ticket) =>
          ticket.id === ticketId ? { ...ticket, priority } : ticket
        )
      );
      toast.success("Priority updated successfully");
    } catch (error) {
      console.error("Error updating priority:", error);
      toast.error("Failed to update priority");
    }
  };

  const handleDeleteTicket = async () => {
    if (!ticketToDelete || isDeleting) return;

    setIsDeleting(true);
    try {
      // Call the onTicketAction function which should connect to TicketService.deleteTicket
      await onTicketAction(ticketToDelete.id, "deleteTicket", userId);

      // Only update local state after confirming successful DB operation
      setTickets(tickets.filter(ticket => ticket.id !== ticketToDelete.id || ticket.status === 'deleted'));
      toast.success("Ticket deleted successfully");
      cancelDelete();

      // Refresh to ensure UI is in sync with DB
      onRefresh();
    } catch (error: any) {
      console.error("Error deleting ticket:", error);

      // Show the specific error message from the backend if available
      if (error.message) {
        toast.error(error.message);
      } else if (error.code === 'P0001' && error.message?.includes('time entries')) {
        toast.error("Cannot delete ticket with time entries");
      } else if (error.code === 'P0001' && error.message?.includes('completion progress')) {
        toast.error("Cannot delete ticket with completion progress");
      } else {
        toast.error("Failed to delete ticket");
      }
    } finally {
      setIsDeleting(false);
    }
  };

  return (
    <div className="space-y-4">
      <TicketFilters
        searchTerm={searchTerm}
        statusFilter={statusFilter}
        priorityFilter={priorityFilter}
        typeFilter={typeFilter}
        setSearchTerm={setSearchTerm}
        setStatusFilter={setStatusFilter}
        setPriorityFilter={setPriorityFilter}
        setTypeFilter={setTypeFilter}
        onRefresh={onRefresh}
      />

      {displayedTickets.length === 0 ? (
        <EmptyTicketState />
      ) : (
        <>
          <TicketTable
            tickets={displayedTickets}
            showTimeTracking={showTimeTracking}
            userCanEditStatus={userCanEditStatus}
            openTicketDetails={openTicketDetails}
            handleUpdateStatus={handleUpdateStatus}
            handleUpdatePriority={handleUpdatePriority}
            showDeleteConfirmation={showDeleteConfirmation}
            onLogTime={handleLogTime}
            renderTicketActions={renderTicketActions}
          />

          {totalPages > 1 && (
            <Pagination
              currentPage={currentPage}
              totalPages={totalPages}
              onPageChange={setCurrentPage}
            />
          )}
        </>
      )}

      <TicketDetailDialog
        isOpen={isDialogOpen}
        selectedTicket={selectedTicket}
        onClose={closeTicketDetails}
        onTicketAction={handleTicketAction}
        onLogTime={handleLogTime}
        showTimeTracking={showTimeTracking}
        userCanEditStatus={userCanEditStatus}
        userCanEditDates={userCanEditDates}
      />

      <DeleteTicketDialog
        isOpen={isDeleteDialogOpen}
        ticketToDelete={ticketToDelete}
        onCancel={cancelDelete}
        onConfirm={handleDeleteTicket}
        isDeleting={isDeleting}
      />
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { supabase } from '@/lib/supabase';
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader,
  TableRow 
} from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { 
  ChevronDown, 
  ChevronUp, 
  Clock, 
  Filter, 
  MoreHorizontal, 
  User 
} from 'lucide-react';
import { toast } from 'sonner';
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';

export const TicketList = ({ projectId }) => {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');
  const [expandedTicketId, setExpandedTicketId] = useState(null);
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [currentTicket, setCurrentTicket] = useState(null);
  const [users, setUsers] = useState([]);
  const [projects, setProjects] = useState([]);
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    fetchTickets();
    fetchUsers();
    fetchProjects();
  }, [filter, projectId]);

  useEffect(() => {
    if (projectId) {
      fetchTasks(projectId);
    }
  }, [projectId]);

  const fetchTickets = async () => {
    setLoading(true);
    try {
      let query = supabase
        .from('tickets')
        .select(`
          *,
          project:project_id(title, project_id),
          task:task_id(title, task_id),
          assigned_user:assigned_to(first_name, last_name, email, id)
        `)
        .order('created_at', { ascending: false });

      if (projectId) {
        query = query.eq('project_id', projectId);
      }

      // Apply filters
      if (filter === 'mine') {
        const { data: { session } } = await supabase.auth.getSession();
        if (session) {
          query = query.eq('assigned_to', session.user.id);
        }
      } else if (filter === 'open') {
        query = query.not('status', 'eq', 'done');
      } else if (filter === 'done') {
        query = query.eq('status', 'done');
      }

      const { data, error } = await query;

      if (error) throw error;

      console.log("Tickets data:", data);
      setTickets(data || []);
    } catch (error) {
      console.error('Error fetching tickets:', error);
      toast.error("Failed to load tickets");
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async () => {
    try {
      const { data, error } = await supabase
        .from('profiles')
        .select('id, first_name, last_name, email');

      if (error) throw error;
      setUsers(data || []);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const fetchProjects = async () => {
    try {
      const { data, error } = await supabase
        .from('business_projects')
        .select('project_id, title');

      if (error) throw error;
      setProjects(data || []);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  const fetchTasks = async (projectId) => {
    try {
      const { data, error } = await supabase
        .from('project_sub_tasks')
        .select('task_id, title')
        .eq('project_id', projectId);

      if (error) throw error;
      setTasks(data || []);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleUpdateTicket = async (ticketData) => {
    try {
      const { data: oldTicket, error: fetchError } = await supabase
        .from('tickets')
        .select('*')
        .eq('id', ticketData.id)
        .single();

      if (fetchError) throw fetchError;

      // Track changes for notes
      const changes = [];

      if (oldTicket.status !== ticketData.status) {
        changes.push({
          type: 'status_change',
          from: oldTicket.status,
          to: ticketData.status,
          timestamp: new Date().toISOString()
        });
      }

      if (oldTicket.priority !== ticketData.priority) {
        changes.push({
          type: 'priority_change',
          from: oldTicket.priority,
          to: ticketData.priority,
          timestamp: new Date().toISOString()
        });
      }

      if (oldTicket.assigned_to !== ticketData.assigned_to) {
        changes.push({
          type: 'assignee_change',
          from: oldTicket.assigned_to,
          to: ticketData.assigned_to,
          timestamp: new Date().toISOString()
        });
      }

      // Prepare notes
      let notes = oldTicket.notes || [];
      if (typeof notes === 'string') {
        try {
          notes = JSON.parse(notes);
        } catch (e) {
          notes = [];
        }
      }
      if (!Array.isArray(notes)) {
        notes = [];
      }

      const updatedNotes = [...notes, ...changes];

      // Update the ticket
      const { error: updateError } = await supabase
        .from('tickets')
        .update({
          ...ticketData,
          notes: updatedNotes,
          updated_at: new Date().toISOString()
        })
        .eq('id', ticketData.id);

      if (updateError) throw updateError;

      toast.success("Ticket updated successfully");
      fetchTickets();
      setEditDialogOpen(false);
    } catch (error) {
      console.error('Error updating ticket:', error);
      toast.error("Failed to update ticket");
    }
  };

  const handleDeleteTicket = async (ticketId) => {
    try {
      const { error } = await supabase
        .from('tickets')
        .delete()
        .eq('id', ticketId);

      if (error) throw error;

      toast.success("Ticket deleted successfully");
      fetchTickets();
    } catch (error) {
      console.error('Error deleting ticket:', error);
      toast.error("Failed to delete ticket");
    }
  };

  const toggleExpandTicket = (ticketId) => {
    setExpandedTicketId(expandedTicketId === ticketId ? null : ticketId);
  };

  const openEditDialog = (ticket) => {
    setCurrentTicket(ticket);
    setEditDialogOpen(true);

    // If ticket is associated with a project, load its tasks
    if (ticket.project_id) {
      fetchTasks(ticket.project_id);
    }
  };

  const renderHealthIndicator = (health) => {
    const colors = {
      red: 'bg-red-500',
      amber: 'bg-yellow-500',
      green: 'bg-green-500'
    };

    return (
      <span 
        className={`inline-block w-3 h-3 rounded-full ${colors[health] || 'bg-gray-500'}`} 
        title={`Health: ${health}`}
      />
    );
  };

  const renderStatusBadge = (status) => {
    const badges = {
      backlog: 'bg-gray-100 text-gray-800',
      todo: 'bg-blue-100 text-blue-800',
      in_progress: 'bg-yellow-100 text-yellow-800',
      review: 'bg-purple-100 text-purple-800',
      done: 'bg-green-100 text-green-800'
    };

    return (
      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${badges[status] || 'bg-gray-100 text-gray-800'}`}>
        {status}
      </span>
    );
  };

  if (loading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="flex justify-center">
            <p>Loading tickets...</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader className="pb-3">
        <div className="flex justify-between items-center">
          <CardTitle>Tickets</CardTitle>
          <div className="flex items-center gap-2">
            <Select defaultValue={filter} onValueChange={setFilter}>
              <SelectTrigger className="w-[180px]">
                <SelectValue placeholder="Filter tickets" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Tickets</SelectItem>
                <SelectItem value="mine">My Tickets</SelectItem>
                <SelectItem value="open">Open Tickets</SelectItem>
                <SelectItem value="done">Completed Tickets</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        {tickets.length === 0 ? (
          <div className="text-center py-8">
            <p className="text-muted-foreground">No tickets found.</p>
          </div>
        ) : (
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead className="w-8"></TableHead>
                <TableHead>Title</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Priority</TableHead>
                <TableHead>Project</TableHead>
                <TableHead>Task</TableHead>
                <TableHead>Assigned To</TableHead>
                <TableHead>Created</TableHead>
                <TableHead className="w-8"></TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {tickets.map(ticket => (
                <React.Fragment key={ticket.id}>
                  <TableRow className={expandedTicketId === ticket.id ? 'bg-gray-50' : ''}>
                    <TableCell>
                      <Button 
                        variant="ghost" 
                        size="sm"
                        onClick={() => toggleExpandTicket(ticket.id)}
                      >
                        {expandedTicketId === ticket.id ? 
                          <ChevronUp className="h-4 w-4" /> : 
                          <ChevronDown className="h-4 w-4" />
                        }
                      </Button>
                    </TableCell>
                    <TableCell className="font-medium flex items-center gap-2">
                      {ticket.health && renderHealthIndicator(ticket.health)}
                      {ticket.title}
                    </TableCell>
                    <TableCell>
                      {renderStatusBadge(ticket.status)}
                    </TableCell>
                    <TableCell>
                      <Badge variant={
                        ticket.priority === 'high' ? 'destructive' :
                        ticket.priority === 'medium' ? 'secondary' :
                        'outline'
                      }>
                        {ticket.priority}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      {ticket.project?.title || 'N/A'}
                    </TableCell>
                    <TableCell>
                      {ticket.task?.title || 'N/A'}
                    </TableCell>
                    <TableCell>
                      {ticket.assigned_user ? 
                        `${ticket.assigned_user.first_name} ${ticket.assigned_user.last_name}` : 
                        'Unassigned'
                      }
                    </TableCell>
                    <TableCell>
                      {new Date(ticket.created_at).toLocaleDateString()}
                    </TableCell>
                    <TableCell>
                      <DropdownMenu>
                        <DropdownMenuTrigger asChild>
                          <Button variant="ghost" size="sm">
                            <MoreHorizontal className="h-4 w-4" />
                          </Button>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent align="end">
                          <DropdownMenuItem onClick={() => openEditDialog(ticket)}>
                            Edit
                          </DropdownMenuItem>
                          <DropdownMenuSeparator />
                          <DropdownMenuItem 
                            className="text-red-600" 
                            onClick={() => handleDeleteTicket(ticket.id)}
                          >
                            Delete
                          </DropdownMenuItem>
                        </DropdownMenuContent>
                      </DropdownMenu>
                    </TableCell>
                  </TableRow>

                  {expandedTicketId === ticket.id && (
                    <TableRow className="bg-gray-50">
                      <TableCell colSpan={9} className="p-4">
                        <div className="space-y-4">
                          <div>
                            <h4 className="font-medium mb-1">Description</h4>
                            <p className="text-sm text-muted-foreground whitespace-pre-wrap">
                              {ticket.description || 'No description provided.'}
                            </p>
                          </div>

                          <div className="grid grid-cols-2 gap-4">
                            <div>
                              <h4 className="font-medium mb-1">Details</h4>
                              <dl className="grid grid-cols-2 gap-2 text-sm">
                                {ticket.due_date && (
                                  <>
                                    <dt className="text-muted-foreground">Due Date:</dt>
                                    <dd>{new Date(ticket.due_date).toLocaleDateString()}</dd>
                                  </>
                                )}
                                {ticket.estimated_hours !== null && (
                                  <>
                                    <dt className="text-muted-foreground">Estimated Hours:</dt>
                                    <dd>{ticket.estimated_hours}h</dd>
                                  </>
                                )}
                                {ticket.ticket_type && (
                                  <>
                                    <dt className="text-muted-foreground">Type:</dt>
                                    <dd>{ticket.ticket_type}</dd>
                                  </>
                                )}
                                {ticket.equity_points !== null && (
                                  <>
                                    <dt className="text-muted-foreground">Equity Points:</dt>
                                    <dd>{ticket.equity_points}</dd>
                                  </>
                                )}
                              </dl>
                            </div>

                            <div>
                              <h4 className="font-medium mb-1">Activity</h4>
                              <div className="text-sm space-y-2 max-h-[200px] overflow-y-auto">
                                {ticket.notes && Array.isArray(ticket.notes) && ticket.notes.length > 0 ? (
                                  ticket.notes.map((note, index) => (
                                    <div key={index} className="py-1 border-b border-gray-100">
                                      <div className="flex justify-between">
                                        <span className="font-medium">
                                          {note.type === 'status_change' ? 'Status changed' :
                                           note.type === 'priority_change' ? 'Priority changed' :
                                           note.type === 'assignee_change' ? 'Assignee changed' :
                                           note.type}
                                        </span>
                                        <span className="text-xs text-muted-foreground">
                                          {new Date(note.timestamp).toLocaleString()}
                                        </span>
                                      </div>
                                      {note.from !== undefined && note.to !== undefined && (
                                        <div className="text-xs text-muted-foreground mt-1">
                                          From <span className="font-medium">{note.from || 'none'}</span> to <span className="font-medium">{note.to || 'none'}</span>
                                        </div>
                                      )}
                                      {note.message && (
                                        <p className="mt-1">{note.message}</p>
                                      )}
                                    </div>
                                  ))
                                ) : (
                                  <p className="text-muted-foreground">No activity recorded.</p>
                                )}
                              </div>
                            </div>
                          </div>
                        </div>
                      </TableCell>
                    </TableRow>
                  )}
                </React.Fragment>
              ))}
            </TableBody>
          </Table>
        )}
      </CardContent>

      <Dialog open={editDialogOpen} onOpenChange={setEditDialogOpen}>
        <DialogContent className="sm:max-w-lg">
          <DialogHeader>
            <DialogTitle>Edit Ticket</DialogTitle>
            <DialogDescription>
              Make changes to the ticket details below.
            </DialogDescription>
          </DialogHeader>

          {currentTicket && (
            <form onSubmit={(e) => {
              e.preventDefault();
              handleUpdateTicket(currentTicket);
            }}>
              <div className="grid gap-4 py-4">
                <div className="grid gap-2">
                  <Label htmlFor="title">Title</Label>
                  <Input
                    id="title"
                    value={currentTicket.title || ''}
                    onChange={(e) => setCurrentTicket({...currentTicket, title: e.target.value})}
                    required
                  />
                </div>

                <div className="grid gap-2">
                  <Label htmlFor="description">Description</Label>
                  <Textarea
                    id="description"
                    value={currentTicket.description || ''}
                    onChange={(e) => setCurrentTicket({...currentTicket, description: e.target.value})}
                    rows={4}
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="grid gap-2">
                    <Label htmlFor="status">Status</Label>
                    <Select
                      value={currentTicket.status || undefined}
                      onValueChange={(value) => setCurrentTicket({...currentTicket, status: value})}
                    >
                      <SelectTrigger id="status">
                        <SelectValue placeholder="Select status" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="backlog">Backlog</SelectItem>
                        <SelectItem value="todo">To Do</SelectItem>
                        <SelectItem value="in_progress">In Progress</SelectItem>
                        <SelectItem value="review">Review</SelectItem>
                        <SelectItem value="done">Done</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="grid gap-2">
                    <Label htmlFor="priority">Priority</Label>
                    <Select
                      value={currentTicket.priority || undefined}
                      onValueChange={(value) => setCurrentTicket({...currentTicket, priority: value})}
                    >
                      <SelectTrigger id="priority">
                        <SelectValue placeholder="Select priority" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="low">Low</SelectItem>
                        <SelectItem value="medium">Medium</SelectItem>
                        <SelectItem value="high">High</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="grid gap-2">
                    <Label htmlFor="project">Project</Label>
                    <Select
                      value={currentTicket.project_id || undefined}
                      onValueChange={(value) => {
                        setCurrentTicket({...currentTicket, project_id: value});
                        fetchTasks(value);
                      }}
                    >
                      <SelectTrigger id="project">
                        <SelectValue placeholder="Select project" />
                      </SelectTrigger>
                      <SelectContent>
                        {projects.map(project => (
                          <SelectItem key={project.project_id} value={project.project_id}>
                            {project.title}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="grid gap-2">
                    <Label htmlFor="task">Task</Label>
                    <Select
                      value={currentTicket.task_id || undefined}
                      onValueChange={(value) => setCurrentTicket({...currentTicket, task_id: value})}
                      disabled={!currentTicket.project_id || tasks.length === 0}
                    >
                      <SelectTrigger id="task">
                        <SelectValue placeholder="Select task" />
                      </SelectTrigger>
                      <SelectContent>
                        {tasks.map(task => (
                          <SelectItem key={task.task_id} value={task.task_id}>
                            {task.title}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="grid gap-2">
                    <Label htmlFor="assignee">Assigned To</Label>
                    <Select
                      value={currentTicket.assigned_to || undefined}
                      onValueChange={(value) => setCurrentTicket({...currentTicket, assigned_to: value || null})}
                    >
                      <SelectTrigger id="assignee">
                        <SelectValue placeholder="Select assignee" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="unassigned">Unassigned</SelectItem>
                        {users.map(user => (
                          <SelectItem key={user.id} value={user.id}>
                            {user.first_name} {user.last_name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="grid gap-2">
                    <Label htmlFor="health">Health</Label>
                    <Select
                      value={currentTicket.health || undefined}
                      onValueChange={(value) => setCurrentTicket({...currentTicket, health: value})}
                    >
                      <SelectTrigger id="health">
                        <SelectValue placeholder="Select health" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="green">Green</SelectItem>
                        <SelectItem value="amber">Amber</SelectItem>
                        <SelectItem value="red">Red</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="grid gap-2">
                    <Label htmlFor="due_date">Due Date</Label>
                    <Input
                      id="due_date"
                      type="date"
                      value={currentTicket.due_date ? new Date(currentTicket.due_date).toISOString().split('T')[0] : ''}
                      onChange={(e) => setCurrentTicket({...currentTicket, due_date: e.target.value ? new Date(e.target.value).toISOString() : null})}
                    />
                  </div>

                  <div className="grid gap-2">
                    <Label htmlFor="estimated_hours">Estimated Hours</Label>
                    <Input
                      id="estimated_hours"
                      type="number"
                      min="0"
                      step="0.5"
                      value={currentTicket.estimated_hours || ''}
                      onChange={(e) => setCurrentTicket({...currentTicket, estimated_hours: e.target.value ? parseFloat(e.target.value) : null})}
                    />
                  </div>
                </div>
              </div>

              <DialogFooter>
                <Button type="submit">Save Changes</Button>
              </DialogFooter>
            </form>
          )}
        </DialogContent>
      </Dialog>
    </Card>
  );
};

import { useState, useEffect } from "react";
import { supabase } from "@/lib/supabase";
import { Ticket, TicketStatistics } from "@/types/types";

export const useTicketManagement = (initialTickets: Ticket[] = []) => {
  const [tickets, setTickets] = useState<Ticket[]>(initialTickets);
  const [stats, setStats] = useState<TicketStatistics>({
    totalTickets: 0,
    openTickets: 0,
    inProgressTickets: 0,
    completedTickets: 0,
    overdueTickets: 0,
    total: 0, // Added to match updated interface
  });

  useEffect(() => {
    calculateTicketStats();
  }, [tickets]);

  const calculateTicketStats = () => {
    const totalTickets = tickets.length;
    const openTickets = tickets.filter(t => t.status === 'new').length;
    const inProgressTickets = tickets.filter(t => t.status === 'in-progress').length;
    const completedTickets = tickets.filter(t => ['done', 'closed'].includes(t.status)).length;
    const overdueTickets = tickets.filter(t => {
      if (!t.due_date) return false;
      return new Date(t.due_date) < new Date() && !['done', 'closed'].includes(t.status);
    }).length;

    setStats({
      totalTickets,
      openTickets,
      inProgressTickets,
      completedTickets,
      overdueTickets,
      total: totalTickets, // Added to match updated interface
      open: openTickets, // Added to match updated interface
    });
  };

  const addTicket = (newTicket: Ticket) => {
    setTickets(prevTickets => [...prevTickets, newTicket]);
    calculateTicketStats();
  };

  const updateTicket = (updatedTicket: Ticket) => {
    setTickets(prevTickets =>
      prevTickets.map(ticket =>
        ticket.id === updatedTicket.id ? updatedTicket : ticket
      )
    );
    calculateTicketStats();
  };

  const deleteTicket = (ticketId: string) => {
    setTickets(prevTickets => prevTickets.filter(ticket => ticket.id !== ticketId));
    calculateTicketStats();
  };

  return {
    tickets,
    stats,
    addTicket,
    updateTicket,
    deleteTicket,
  };
};

// TicketManagement.tsx
import React, { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Users, FileText, AlertTriangle, CheckCircle, Clock } from "lucide-react";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { TicketKanbanBoard } from "./KanbanBoard";
import { TicketGanttChart } from "./GanttChart";
import { TicketStatistics } from "./TicketStats";
import { toast } from "sonner";

export interface Ticket {
  id: string;
  title: string;
  description: string;
  status: string;
  priority: string;
  health?: string;
  due_date?: string;
  created_at: string;
  updated_at: string;
  reporter_email?: string;
  reporter?: string;
  expanded?: boolean;
  newNote?: string;
  notes?: Array<{
    action: string;
    user: string;
    timestamp: string;
    comment?: string;
  }> | null;
  system_info?: {
    url: string;
    userAgent: string;
    timestamp: string;
    viewportSize: string;
    referrer: string;
  };
  reported_url?: string;
  attachments?: string[];
  reproduction_steps?: string;
}

export interface StatisticsData {
  totalTickets: number;
  openTickets: number;
  closedTickets: number;
  highPriorityTickets: number;
  byStatus: { [key: string]: number };
  byPriority: { [key: string]: number };
}

interface TicketManagementProps {
  supabase: any;
  initialTickets?: Ticket[];
  fetchTicketsQuery?: string;
  title?: string;
  description?: string;
  onRefreshData?: () => Promise<void>;
}

const TicketManagement: React.FC<TicketManagementProps> = ({
  supabase,
  initialTickets = [],
  fetchTicketsQuery = "title.ilike.%Beta%,description.ilike.%Beta%,title.ilike.%Testing%",
  title = "Ticket Management",
  description = "Manage tickets and issues",
  onRefreshData
}) => {
  const [isLoading, setIsLoading] = useState(true);
  const [tickets, setTickets] = useState<Ticket[]>(initialTickets);
  const [replyDialogOpen, setReplyDialogOpen] = useState(false);
  const [activeTicketId, setActiveTicketId] = useState<string | null>(null);
  const [replyMessage, setReplyMessage] = useState('');
  const [showKanban, setShowKanban] = useState(true);
  const [showGantt, setShowGantt] = useState(true);
  const [ticketStats, setTicketStats] = useState<StatisticsData>({
    totalTickets: 0,
    openTickets: 0,
    closedTickets: 0,
    highPriorityTickets: 0,
    byStatus: {},
    byPriority: {}
  });

  useEffect(() => {
    if (initialTickets.length === 0) {
      fetchTickets();
    } else {
      setTickets(initialTickets);
      calculateTicketStatistics(initialTickets);
      setIsLoading(false);
    }
  }, [initialTickets]);

  const fetchTickets = async () => {
    try {
      setIsLoading(true);
      const { data, error } = await supabase
        .from('tickets')
        .select('*')
        .or(fetchTicketsQuery)
        .order('created_at', { ascending: false });

      if (error) {
        console.error("Error fetching tickets:", error);
        toast.error("Failed to load tickets");
        setIsLoading(false);
        return;
      }

      const processedTickets: Ticket[] = await Promise.all(
        data.map(async (ticket: any) => {
          let reporterEmail = null;

          if (ticket.reporter) {
            const { data: profileData } = await supabase
              .from('profiles')
              .select('email')
              .eq('id', ticket.reporter)
              .maybeSingle();

            reporterEmail = profileData?.email;
          }

          return {
            ...ticket,
            reporter_email: reporterEmail,
            expanded: false,
            newNote: ''
          };
        })
      );

      setTickets(processedTickets);
      calculateTicketStatistics(processedTickets);
      setIsLoading(false);
    } catch (err) {
      console.error("Error in fetchTickets:", err);
      toast.error("Failed to load tickets data");
      setIsLoading(false);
    }
  };

  const calculateTicketStatistics = (tickets: Ticket[]) => {
    const totalTickets = tickets.length;
    const openTickets = tickets.filter(ticket => ticket.status !== 'done' && ticket.status !== 'closed').length;
    const closedTickets = totalTickets - openTickets;
    const highPriorityTickets = tickets.filter(ticket => ticket.priority === 'high').length;

    const byStatus: { [key: string]: number } = {};
    const byPriority: { [key: string]: number } = {};

    tickets.forEach(ticket => {
      byStatus[ticket.status] = (byStatus[ticket.status] || 0) + 1;
      byPriority[ticket.priority] = (byPriority[ticket.priority] || 0) + 1;
    });

    setTicketStats({
      totalTickets,
      openTickets,
      closedTickets,
      highPriorityTickets,
      byStatus,
      byPriority,
    });
  };

  const handleAddTicketNote = async (ticketId: string, note: string) => {
    if (!note.trim()) return;

    try {
      const { data: { user } } = await supabase.auth.getUser();

      if (!user) {
        toast.error("You must be logged in to add notes");
        return;
      }

      const { data: userData } = await supabase
        .from('profiles')
        .select('email, first_name, last_name')
        .eq('id', user.id)
        .single();

      const userName = userData?.first_name 
        ? `${userData.first_name} ${userData.last_name || ''}`
        : userData?.email || user.email || 'Unknown User';

      const { data: ticketData, error: fetchError } = await supabase
        .from('tickets')
        .select('notes')
        .eq('id', ticketId)
        .single();

      if (fetchError) {
        console.error("Error fetching ticket:", fetchError);
        toast.error("Failed to add note");
        return;
      }

      let notes = ticketData.notes || [];

      notes.push({
        action: 'Note added',
        user: userName,
        timestamp: new Date().toISOString(),
        comment: note
      });

      const { error: updateError } = await supabase
        .from('tickets')
        .update({
          notes: notes,
          updated_at: new Date().toISOString()
        })
        .eq('id', ticketId);

      if (updateError) {
        console.error("Error updating ticket:", updateError);
        toast.error("Failed to add note");
        return;
      }

      setTickets(prev => prev.map(t => 
        t.id === ticketId ? {...t, newNote: ''} : t
      ));

      await fetchTickets();
      toast.success("Note added successfully");

    } catch (err) {
      console.error("Error in handleAddTicketNote:", err);
      toast.error("Failed to add note");
    }
  };

  const handleUpdateTicketStatus = async (ticketId: string, newStatus: string) => {
    try {
      const { error } = await supabase
        .from('tickets')
        .update({ status: newStatus })
        .eq('id', ticketId);

      if (error) {
        console.error("Error updating ticket status:", error);
        toast.error("Failed to update ticket status");
        return;
      }

      setTickets(prev => prev.map(ticket =>
        ticket.id === ticketId ? { ...ticket, status: newStatus } : ticket
      ));
      calculateTicketStatistics(tickets.map(ticket =>
        ticket.id === ticketId ? { ...ticket, status: newStatus } : ticket
      ));
      toast.success("Ticket status updated successfully");
    } catch (error) {
      console.error("Error updating ticket status:", error);
      toast.error("Failed to update ticket status");
    }
  };

  const handleUpdateTicketPriority = async (ticketId: string, newPriority: string) => {
    try {
      const { error } = await supabase
        .from('tickets')
        .update({ priority: newPriority })
        .eq('id', ticketId);

      if (error) {
        console.error("Error updating ticket priority:", error);
        toast.error("Failed to update ticket priority");
        return;
      }

      setTickets(prev => prev.map(ticket =>
        ticket.id === ticketId ? { ...ticket, priority: newPriority } : ticket
      ));
      calculateTicketStatistics(tickets.map(ticket =>
        ticket.id === ticketId ? { ...ticket, priority: newPriority } : ticket
      ));
      toast.success("Ticket priority updated successfully");
    } catch (error) {
      console.error("Error updating ticket priority:", error);
      toast.error("Failed to update ticket priority");
    }
  };

  const handleSetDueDate = async (ticketId: string, newDueDate: string) => {
    try {
      const { error } = await supabase
        .from('tickets')
        .update({ due_date: newDueDate })
        .eq('id', ticketId);

      if (error) {
        console.error("Error setting due date:", error);
        toast.error("Failed to set due date");
        return;
      }

      setTickets(prev => prev.map(ticket =>
        ticket.id === ticketId ? { ...ticket, due_date: newDueDate } : ticket
      ));
      toast.success("Due date updated successfully");
    } catch (error) {
      console.error("Error setting due date:", error);
      toast.error("Failed to set due date");
    }
  };

  const handleReplyToReporter = (ticketId: string) => {
    setActiveTicketId(ticketId);
    setReplyDialogOpen(true);
  };

  const sendReplyToReporter = async () => {
    if (!activeTicketId || !replyMessage.trim()) return;

    try {
      const ticket = tickets.find(t => t.id === activeTicketId);
      if (!ticket || !ticket.reporter) {
        toast.error("Cannot find reporter information");
        return;
      }

      const { data: { user } } = await supabase.auth.getUser();

      if (!user) {
        toast.error("You must be logged in to reply");
        return;
      }

      const { data: userData } = await supabase
        .from('profiles')
        .select('email, first_name, last_name')
        .eq('id', user.id)
        .single();

      const userName = userData?.first_name 
        ? `${userData.first_name} ${userData.last_name || ''}`
        : userData?.email || user.email || 'Unknown User';

      // Create the user_messages table if it doesn't exist
      const { error: tableCheckError } = await supabase
        .from('user_messages')
        .select('id', { count: 'exact', head: true });

      if (tableCheckError) {
        // Create the user_messages table if it doesn't exist
        const { error: createTableError } = await supabase.rpc('create_messages_table_if_not_exists');
        if (createTableError) {
          console.error("Error creating messages table:", createTableError);
          toast.error("Failed to send reply: messaging system not available");
          return;
        }
      }

      // Send a message to the reporter
      const { error: messageError } = await supabase
        .from('user_messages')
        .insert({
          sender_id: user.id,
          recipient_id: ticket.reporter,
          subject: `Re: ${ticket.title}`,
          message: replyMessage,
          related_ticket: activeTicketId,
          read: false
        });

      if (messageError) {
        console.error("Error sending message:", messageError);
        toast.error("Failed to send reply to user dashboard, but message recorded in ticket notes");
      } else {
        toast.success("Reply sent to reporter's dashboard");
      }

      // Also update the ticket notes for history tracking
      const { data: ticketData, error: fetchError } = await supabase
        .from('tickets')
        .select('notes')
        .eq('id', activeTicketId)
        .single();

      if (fetchError) {
        console.error("Error fetching ticket:", fetchError);
        toast.error("Failed to update ticket notes");
        return;
      }

      let notes = ticketData.notes || [];

      notes.push({
        action: 'Reply sent to reporter',
        user: userName,
        timestamp: new Date().toISOString(),
        comment: replyMessage
      });

      const { error: updateError } = await supabase
        .from('tickets')
        .update({
          notes: notes,
          updated_at: new Date().toISOString()
        })
        .eq('id', activeTicketId);

      if (updateError) {
        console.error("Error updating ticket notes:", updateError);
        toast.error("Failed to record reply in ticket history");
        return;
      }

      setReplyDialogOpen(false);
      setActiveTicketId(null);
      setReplyMessage('');
      await fetchTickets();
      toast.success("Reply recorded in ticket history");

    } catch (err) {
      console.error("Error in sendReplyToReporter:", err);
      toast.error("Failed to send reply");
    }
  };

  const handleRefreshData = async () => {
    setIsLoading(true);
    if (onRefreshData) {
      await onRefreshData();
    } else {
      await fetchTickets();
    }
    setIsLoading(false);
  };

  const toggleTicketExpanded = (ticketId: string) => {
    setTickets(prev => prev.map(ticket =>
      ticket.id === ticketId ? { ...ticket, expanded: !ticket.expanded } : ticket
    ));
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString();
  };

  return (
    <Card className="mb-6">
      <CardHeader>
        <div className="flex justify-between items-center">
          <CardTitle>{title}</CardTitle>
          <div className="flex gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setShowKanban(!showKanban)}
            >
              {showKanban ? "Hide" : "Show"} Kanban Board
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setShowGantt(!showGantt)}
            >
              {showGantt ? "Hide" : "Show"} Gantt Chart
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={handleRefreshData}
              disabled={isLoading}
            >
              {isLoading ? "Refreshing..." : "Refresh"}
            </Button>
          </div>
        </div>
        <CardDescription>{description}</CardDescription>
      </CardHeader>
      <CardContent>
        <TicketStatistics stats={ticketStats} isLoading={isLoading} />

        {showKanban && (
          <div className="mb-8">
            <h3 className="text-lg font-medium mb-4">Ticket Board</h3>
            <div className="border rounded-lg overflow-hidden">
              <TicketKanbanBoard 
                tickets={tickets} 
                onStatusUpdate={handleUpdateTicketStatus} 
                onToggleExpand={toggleTicketExpanded}
                formatDate={formatDate}
              />
            </div>
          </div>
        )}

        {showGantt && (
          <div className="mb-8">
            <h3 className="text-lg font-medium mb-4">Timeline</h3>
            <div className="border rounded-lg overflow-hidden p-4">
              <TicketGanttChart tickets={tickets} />
            </div>
          </div>
        )}

        <div>
          <h3 className="text-lg font-medium mb-4">All Tickets</h3>
          <Table className="w-full">
            <TableHeader>
              <TableRow>
                <TableHead className="w-[250px]">Title</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Priority</TableHead>
                <TableHead>Created</TableHead>
                <TableHead>Due Date</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {tickets.map(ticket => (
                <React.Fragment key={ticket.id}>
                  <TableRow className="group">
                    <TableCell className="font-medium">{ticket.title}</TableCell>
                    <TableCell>
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                        ticket.status === 'new' ? 'bg-blue-100 text-blue-800' :
                        ticket.status === 'in-progress' ? 'bg-purple-100 text-purple-800' :
                        ticket.status === 'blocked' ? 'bg-red-100 text-red-800' :
                        ticket.status === 'review' ? 'bg-yellow-100 text-yellow-800' :
                        ticket.status === 'done' ? 'bg-green-100 text-green-800' :
                        'bg-gray-100 text-gray-800'
                      }`}>
                        {ticket.status}
                      </span>
                    </TableCell>
                    <TableCell>
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                        ticket.priority === 'high' ? 'bg-red-100 text-red-800' :
                        ticket.priority === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-green-100 text-green-800'
                      }`}>
                        {ticket.priority}
                      </span>
                    </TableCell>
                    <TableCell>{formatDate(ticket.created_at)}</TableCell>
                    <TableCell>{ticket.due_date ? formatDate(ticket.due_date) : '-'}</TableCell>
                    <TableCell className="text-right">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => toggleTicketExpanded(ticket.id)}
                      >
                        {ticket.expanded ? 'Collapse' : 'Expand'}
                      </Button>
                    </TableCell>
                  </TableRow>
                  {ticket.expanded && (
                    <TableRow>
                      <TableCell colSpan={6} className="p-0 border-t-0">
                        <ExpandedTicketDetails 
                          ticket={ticket}
                          onAddNote={handleAddTicketNote}
                          onUpdateStatus={handleUpdateTicketStatus}
                          onUpdatePriority={handleUpdateTicketPriority}
                          onSetDueDate={handleSetDueDate}
                          onReplyToReporter={handleReplyToReporter}
                          formatDate={formatDate}
                        />
                      </TableCell>
                    </TableRow>
                  )}
                </React.Fragment>
              ))}
            </TableBody>
          </Table>
        </div>
      </CardContent>

      <Dialog open={replyDialogOpen} onOpenChange={setReplyDialogOpen}>
        <DialogContent className="sm:max-w-[500px]">
          <DialogHeader>
            <DialogTitle>Reply to Reporter</DialogTitle>
            <DialogDescription>
              Your message will be sent to the user who reported this issue.
            </DialogDescription>
          </DialogHeader>
          <div className="grid gap-4 py-4">
            <Textarea
              placeholder="Write your reply here..."
              value={replyMessage}
              onChange={(e) => setReplyMessage(e.target.value)}
              className="min-h-[150px]"
            />
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setReplyDialogOpen(false)}>Cancel</Button>
            <Button onClick={sendReplyToReporter}>Send Reply</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </Card>
  );
};

interface ExpandedTicketDetailsProps {
  ticket: Ticket;
  onAddNote: (ticketId: string, note: string) => Promise<void>;
  onUpdateStatus: (ticketId: string, newStatus: string) => Promise<void>;
  onUpdatePriority: (ticketId: string, newPriority: string) => Promise<void>;
  onSetDueDate: (ticketId: string, newDueDate: string) => Promise<void>;
  onReplyToReporter: (ticketId: string) => void;
  formatDate: (dateString: string) => string;
}

export const ExpandedTicketDetails: React.FC<ExpandedTicketDetailsProps> = ({
  ticket,
  onAddNote,
  onUpdateStatus,
  onUpdatePriority,
  onSetDueDate,
  onReplyToReporter,
  formatDate
}) => {
  return (
    <div className="p-4 border-t">
      <div className="grid md:grid-cols-2 gap-4 mb-4">
        <div>
          <p className="text-sm text-gray-600 mb-2">{ticket.description}</p>

          <div className="grid grid-cols-2 gap-2 text-sm">
            <div>
              <span className="text-gray-500">Created: </span>
              {formatDate(ticket.created_at)}
            </div>
            {ticket.due_date && (
              <div>
                <span className="text-gray-500">Due: </span>
                {formatDate(ticket.due_date)}
              </div>
            )}
            {ticket.reporter_email && (
              <div>
                <span className="text-gray-500">Reporter: </span>
                {ticket.reporter_email}
              </div>
            )}
            {ticket.reported_url && (
              <div>
                <span className="text-gray-500">URL: </span>
                <span className="text-blue-500 underline">{ticket.reported_url}</span>
              </div>
            )}
          </div>

          {ticket.system_info && (
            <div className="mt-3 p-2 bg-gray-50 rounded text-xs">
              <p className="font-medium mb-1">System Info:</p>
              <div className="grid grid-cols-2 gap-1">
                <div><span className="text-gray-500">Browser: </span>{ticket.system_info.userAgent}</div>
                <div><span className="text-gray-500">Screen: </span>{ticket.system_info.viewportSize}</div>
                <div><span className="text-gray-500">Time: </span>{new Date(ticket.system_info.timestamp).toLocaleString()}</div>
                <div><span className="text-gray-500">Referrer: </span>{ticket.system_info.referrer}</div>
              </div>
            </div>
          )}
        </div>

        {ticket.attachments && ticket.attachments.length > 0 && (
          <div>
            <p className="text-sm font-medium mb-2">Screenshots ({ticket.attachments.length})</p>
            <div className="grid grid-cols-2 gap-2">
              {ticket.attachments.map((url, i) => (
                <div key={i} className="relative group border rounded overflow-hidden h-36">
                  <img 
                    src={url} 
                    alt={`Screenshot ${i+1}`} 
                    className="w-full h-full object-cover"
                  />
                  <div className="absolute inset-0 bg-black bg-opacity-50 opacity-0 group-hover:opacity-100 flex items-center justify-center transition-opacity">
                    <Button 
                      variant="ghost" 
                      size="sm" 
                      className="text-white"
                      onClick={() => window.open(url, '_blank')}
                    >
                      View Full
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      <div className="mb-4">
        <h4 className="text-sm font-medium mb-2">Activity Timeline</h4>
        <div className="space-y-2 text-sm pl-4 border-l-2 border-gray-200">
          {ticket.notes ? (
            ticket.notes.map((activity, index) => (
              <div key={index} className="relative pl-4 pb-2">
                <div className="absolute w-2 h-2 rounded-full bg-blue-500 -left-[5px]"></div>
                <p className="font-medium">{activity.action}</p>
                <p className="text-xs text-gray-500">
                  {new Date(activity.timestamp).toLocaleString()} by {activity.user}
                </p>
                {activity.comment && (
                  <p className="mt-1 bg-gray-50 p-2 rounded border border-gray-100">
                    {activity.comment}
                  </p>
                )}
              </div>
            ))
          ) : (
            <p className="text-gray-500 italic">No activity recorded yet</p>
          )}
        </div>
      </div>

      <div className="border-t pt-4 flex flex-wrap gap-4">
        <div>
          <Label htmlFor={`status-${ticket.id}`} className="text-xs block mb-1">Update Status</Label>
          <Select
            defaultValue={ticket.status}
            onValueChange={(value) => onUpdateStatus(ticket.id, value)}
          >
            <SelectTrigger id={`status-${ticket.id}`} className="w-[140px]">
              <SelectValue placeholder="Status" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="new">New</SelectItem>
              <SelectItem value="in-progress">In Progress</SelectItem>
              <SelectItem value="blocked">Blocked</SelectItem>
              <SelectItem value="review">Review</SelectItem>
              <SelectItem value="done">Done</SelectItem>
              <SelectItem value="closed">Closed</SelectItem>
            </SelectContent>
          </Select>
        </div>

        <div>
          <Label htmlFor={`priority-${ticket.id}`} className="text-xs block mb-1">Update Priority</Label>
          <Select
            defaultValue={ticket.priority}
            onValueChange={(value) => onUpdatePriority(ticket.id, value)}
          >
            <SelectTrigger id={`priority-${ticket.id}`} className="w-[140px]">
              <SelectValue placeholder="Priority" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="low">Low</SelectItem>
              <SelectItem value="medium">Medium</SelectItem>
              <SelectItem value="high">High</SelectItem>
            </SelectContent>
          </Select>
        </div>

        <div>
          <Label htmlFor={`due-date-${ticket.id}`} className="text-xs block mb-1">Set Due Date</Label>
          <Input
            id={`due-date-${ticket.id}`}
            type="date"
            className="w-[180px]"
            value={ticket.due_date ? new Date(ticket.due_date).toISOString().split('T')[0] : ''}
            onChange={(e) => onSetDueDate(ticket.id, e.target.value)}
          />
        </div>

        {ticket.reporter && (
