export default TicketManagement;

import { supabase } from "@/lib/supabase";
import { Ticket } from "@/types/types";
import { toast } from "sonner";

export class TicketService {
  // Method to check if a ticket can be deleted
  static async canDeleteTicket(ticketId: string): Promise<boolean> {
    try {
      // Check for time entries
      const { data: timeEntries, error: timeError } = await supabase
        .from("time_entries")
        .select("id")
        .eq("ticket_id", ticketId)
        .limit(1);

      if (timeError) {
        throw timeError;
      }

      // If there are any time entries, ticket cannot be deleted
      if (timeEntries && timeEntries.length > 0) {
        return false;
      }

      // Check ticket completion percentage
      const { data: ticket, error: ticketError } = await supabase
        .from("tickets")
        .select("completion_percentage, job_app_id")
        .eq("id", ticketId)
        .single();

      if (ticketError) {
        throw ticketError;
      }

      // If completion percentage is greater than 0, ticket cannot be deleted
      if (ticket && ticket.completion_percentage > 0) {
        return false;
      }

      // Check for related documents
      if (ticket?.job_app_id) {
        const { data: documents, error: docError } = await supabase
          .from("legal_documents")
          .select("id")
          .eq("job_application_id", ticket.job_app_id)
          .limit(1);

        if (docError) {
          console.error("Error checking for related documents:", docError);
        } else if (documents && documents.length > 0) {
          // If there are related legal documents, ticket cannot be deleted
          return false;
        }
      }

      return true;
    } catch (error: any) {
      console.error("Error checking if ticket can be deleted:", error);
      return false;
    }
  }

  // Method to delete a ticket
  static async deleteTicket(ticketId: string, userId: string): Promise<boolean> {
    try {
      // First check if the ticket can be deleted
      const canDelete = await this.canDeleteTicket(ticketId);
      if (!canDelete) {
        // Check specifically what prevents deletion to give a more specific error message
        // Check for time entries
        const { data: timeEntries, error: timeError } = await supabase
          .from("time_entries")
          .select("id")
          .eq("ticket_id", ticketId)
          .limit(1);

        if (timeEntries && timeEntries.length > 0) {
          throw new Error("Cannot delete ticket with logged time entries");
        }

        // Check ticket completion percentage
        const { data: ticket, error: ticketError } = await supabase
          .from("tickets")
          .select("completion_percentage, job_app_id")
          .eq("id", ticketId)
          .single();

        if (ticket && ticket.completion_percentage > 0) {
          throw new Error("Cannot delete ticket with completion progress");
        }

        // Check for related documents
        if (ticket?.job_app_id) {
          const { data: documents } = await supabase
            .from("legal_documents")
            .select("id")
            .eq("job_application_id", ticket.job_app_id)
            .limit(1);

          if (documents && documents.length > 0) {
            throw new Error("Cannot delete ticket with associated legal documents");
          }
        }

        // Generic fallback error
        throw new Error("This ticket cannot be deleted");
      }

      // Use the database function for soft deletion
      const { data, error } = await supabase.rpc("soft_delete_ticket", {
        ticket_id: ticketId,
        user_id: userId,
      });

      if (error) {
        console.error("Error in soft_delete_ticket:", error);
        // Create a user-friendly error message
        let errorMessage = "Failed to delete ticket";
        if (error.message.includes("time entries")) {
          errorMessage = "Cannot delete ticket with logged time entries";
        } else if (error.message.includes("completion progress")) {
          errorMessage = "Cannot delete ticket with completion progress";
        } else if (error.message.includes("legal documents")) {
          errorMessage = "Cannot delete ticket with associated legal documents";
        }

        throw new Error(errorMessage);
      }

      return true;
    } catch (error: any) {
      console.error("Error deleting ticket:", error);
      // Don't show toast here, let the calling component handle it
      throw error;
    }
  }

  // Method to get a single ticket by ID
  static async getTicketById(ticketId: string): Promise<Ticket> {
    try {
      const { data, error } = await supabase
        .from("tickets")
        .select("*")
        .eq("id", ticketId)
        .single();

      if (error) {
        throw error;
      }

      return data as Ticket;
    } catch (error: any) {
      console.error("Error fetching ticket:", error);
      toast.error("Failed to fetch ticket details");
      throw error;
    }
  }
}

import React from "react";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

interface DeleteTicketDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onConfirm: () => Promise<void>;
  isDeleting?: boolean;
  ticketTitle: string;
  errorMessage?: string;
}

export const DeleteTicketDialog: React.FC<DeleteTicketDialogProps> = ({
  open,
  onOpenChange,
  onConfirm,
  isDeleting = false,
  ticketTitle,
  errorMessage,
}) => {
  const handleConfirm = async (e: React.MouseEvent) => {
    e.preventDefault();
    try {
      await onConfirm();
      // The parent component will handle the success toast
    } catch (error: any) {
      // Don't display a toast here, as the parent component already handles error notifications
      // This prevents duplicate toast messages
      console.error("Error in DeleteTicketDialog:", error);
      // Keep the dialog open if there was an error
      return;
    }
  };

  return (
    <AlertDialog open={open} onOpenChange={onOpenChange}>
      <AlertDialogContent>
        <AlertDialogHeader>
          <AlertDialogTitle>Are you sure?</AlertDialogTitle>
          <AlertDialogDescription>
            You are about to delete ticket: <strong>{ticketTitle}</strong>
            <br />
            This action cannot be undone. The ticket will be archived and removed from view.
            <br /><br />
            <strong>Note:</strong> Tickets with time entries, completion progress, or associated legal documents cannot be deleted.

            {errorMessage && (
              <div className="mt-2 p-2 bg-red-100 border border-red-200 rounded text-red-800">
                {errorMessage}
              </div>
            )}
          </AlertDialogDescription>
        </AlertDialogHeader>
        <AlertDialogFooter>
          <AlertDialogCancel disabled={isDeleting}>Cancel</AlertDialogCancel>
          <AlertDialogAction
            onClick={handleConfirm}
            disabled={isDeleting}
            className="bg-red-500 hover:bg-red-600 focus:ring-red-500"
          >
            {isDeleting ? "Deleting..." : "Delete"}
          </AlertDialogAction>
        </AlertDialogFooter>
      </AlertDialogContent>
    </AlertDialog>
  );
};

import React, { useState, useEffect } from "react";
