import { createContext, useContext, useEffect, useState, useRef, ReactNode } from 'react';
import { supabase } from '@/lib/supabase';
import { Application, Project } from '@/types/business';
import { toast } from 'sonner';

interface JobApplicationContextType {
  applications: Application[];
  pendingApplications: Application[];
  activeApplications: Application[];
  withdrawnApplications: Application[];
  rejectedApplications: Application[];
  isLoading: boolean;
  refreshApplications: () => Promise<void>;
  newApplicationsCount: number;
  newMessagesCount: number;
}

const JobApplicationContext = createContext<JobApplicationContextType | undefined>(undefined);

export const JobApplicationProvider = ({ children }: { children: ReactNode }) => {
  const [projects, setProjects] = useState<Project[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [newApplicationsCount, setNewApplicationsCount] = useState(0);
  const [newMessagesCount, setNewMessagesCount] = useState(0);
  const channelRef = useRef<ReturnType<typeof supabase.channel> | null>(null);

  const loadProjectsWithApplications = async () => {
    try {
      setIsLoading(true);
      const { data: { session } } = await supabase.auth.getSession();
      if (!session) return;

      console.log("Loading projects for business ID:", session.user.id);

      const { data: projectsData, error: projectsError } = await supabase
        .from('business_projects')
        .select('*')
        .eq('business_id', session.user.id);

      if (projectsError) {
        console.error("Error fetching projects:", projectsError);
        throw projectsError;
      }

      if (!projectsData || projectsData.length === 0) {
        setProjects([]);
        return;
      }

      const projectsWithApplications: Project[] = [];

      for (const project of projectsData) {
        const { data: tasksData, error: tasksError } = await supabase
          .from('project_sub_tasks')
          .select('task_id, skill_requirements, equity_allocation, timeframe')
          .eq('project_id', project.project_id);

        if (tasksError) {
          console.error('Error fetching tasks for project:', project.project_id, tasksError);
          continue;
        }

        const taskIds = tasksData.map(task => task.task_id);

        if (taskIds.length === 0) {
          projectsWithApplications.push({
            ...project,
            applications: []
          });
          continue;
        }

        const { data: applicationsData, error: applicationsError } = await supabase
          .from('job_applications')
          .select('*')
          .in('task_id', taskIds);

        if (applicationsError) {
          console.error('Error fetching applications:', applicationsError);
          continue;
        }

        if (!applicationsData || applicationsData.length === 0) {
          projectsWithApplications.push({
            ...project,
            applications: []
          });
          continue;
        }

        const applicationsWithProfiles = [];

        for (const app of applicationsData) {
          const { data: profileData, error: profileError } = await supabase
            .from('profiles')
            .select('first_name, last_name, title, location, employment_preference, skills')
            .eq('id', app.user_id)
            .maybeSingle();

          if (profileError) {
            console.error('Error fetching profile for user:', app.user_id, profileError);
            continue;
          }

          const { data: taskData, error: taskError } = await supabase
            .from('project_sub_tasks')
            .select('title, description, skills_required, skill_requirements, equity_allocation, timeframe')
            .eq('task_id', app.task_id)
            .maybeSingle();

          if (taskError) {
            console.error('Error fetching task details:', taskError);
            continue;
          }

          let userSkills = [];
          if (profileData?.skills) {
            try {
              if (typeof profileData.skills === 'string') {
                const parsedSkills = JSON.parse(profileData.skills);
                if (Array.isArray(parsedSkills)) {
                  userSkills = parsedSkills.map(s => 
                    typeof s === 'string' ? { skill: s, level: "Intermediate" } : s
                  );
                }
              } else if (Array.isArray(profileData.skills)) {
                userSkills = profileData.skills.map(s => 
                  typeof s === 'string' ? { skill: s, level: "Intermediate" } : s
                );
              }
            } catch (e) {
              console.error("Error parsing skills:", e);
            }
          }

          const userSkillNames = userSkills.map(s => s.skill.toLowerCase());
          const taskRequiredSkills = taskData?.skill_requirements || [];

          let matchedSkills = 0;
          if (Array.isArray(taskRequiredSkills)) {
            taskRequiredSkills.forEach(skillObj => {
              const skillName = typeof skillObj === 'string' ? 
                skillObj.toLowerCase() : 
                (skillObj.skill ? skillObj.skill.toLowerCase() : '');

              if (skillName && userSkillNames.includes(skillName)) {
                matchedSkills++;
              }
            });
          }

          const skillMatch = taskRequiredSkills.length > 0 
            ? Math.round((matchedSkills / taskRequiredSkills.length) * 100) 
            : 0;

          applicationsWithProfiles.push({
            ...app,
            profile: {
              ...profileData,
              skills: userSkills
            },
            business_roles: {
              ...(taskData || {}),
              project: {
                title: project.title
              }
            },
            skillMatch
          });
        }

        projectsWithApplications.push({
          ...project,
          applications: applicationsWithProfiles
        });
      }

      setProjects(projectsWithApplications);

      const oneDayAgo = new Date();
      oneDayAgo.setDate(oneDayAgo.getDate() - 1);

      let newApps = 0;
      let newMsgs = 0;

      projectsWithApplications.forEach(project => {
        project.applications.forEach(app => {
          const appDate = new Date(app.applied_at);
          if (appDate > oneDayAgo && app.status === 'pending') {
            newApps++;
          }

          if (app.task_discourse) {
            const lastMessageMatch = app.task_discourse.match(/\[([^\]]+)\]/);
            if (lastMessageMatch) {
              try {
                const msgDate = new Date(lastMessageMatch[1]);
                if (msgDate > oneDayAgo) {
                  newMsgs++;
                }
              } catch (e) {
                console.error("Error parsing message date:", e);
              }
            }
          }
        });
      });

      setNewApplicationsCount(newApps);
      setNewMessagesCount(newMsgs);
    } catch (error) {
      console.error('Error loading projects with applications:', error);
      toast.error("Failed to load applications data");
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    loadProjectsWithApplications();

    // Only set up the channel once
    if (!channelRef.current) {
      const channel = supabase
        .channel('application-updates')
        .on(
          'postgres_changes',
          {
            event: 'INSERT',
            schema: 'public',
            table: 'job_applications'
          },
          () => {
            setNewApplicationsCount(prev => prev + 1);
            toast.info("New application received!");
            loadProjectsWithApplications();
          }
        )
        .on(
          'postgres_changes',
          {
            event: 'UPDATE',
            schema: 'public',
            table: 'job_applications',
            filter: 'task_discourse=neq.null'
          },
          () => {
            setNewMessagesCount(prev => prev + 1);
            toast.info("New message received!");
            loadProjectsWithApplications();
          }
        )
        .subscribe();

      channelRef.current = channel;
    }

    return () => {
      if (channelRef.current) {
        supabase.removeChannel(channelRef.current);
        channelRef.current = null;
      }
    };
  }, []);

  // Get applications across all projects
  const getAllApplications = (): Application[] => {
    return projects.flatMap(project => project.applications);
  };

  // Get applications by status
  const getApplicationsByStatus = (statuses: string[]): Application[] => {
    return getAllApplications().filter(app => 
      statuses.includes(app.status.toLowerCase())
    );
  };

  // Computed properties
  const pendingApplications = getApplicationsByStatus(['pending', 'in review']);
  const activeApplications = getApplicationsByStatus(['negotiation', 'accepted']);
  const withdrawnApplications = getApplicationsByStatus(['withdrawn']);
  const rejectedApplications = getApplicationsByStatus(['rejected']);

  const value = {
    applications: getAllApplications(),
    pendingApplications,
    activeApplications,
    withdrawnApplications,
    rejectedApplications,
    isLoading,
    refreshApplications: loadProjectsWithApplications,
    newApplicationsCount,
    newMessagesCount
  };

  return (
    <JobApplicationContext.Provider value={value}>
      {children}
    </JobApplicationContext.Provider>
  );
};

export const useJobApplications = () => {
  const context = useContext(JobApplicationContext);
  if (context === undefined) {
    throw new Error('useJobApplications must be used within a JobApplicationProvider');
  }
  return context;
};

import { useState, useEffect, useCallback, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { supabase } from "@/lib/supabase";
import { toast } from "sonner";
import { useProfile } from "../useProfile";
import { useApplications } from "../useApplications";
import { useEquityProjects } from "../useEquityProjects";
import { useCVData } from "../useCVData";
import { useSessionCheck } from "./useSessionCheck";
import { useOpportunitiesLoader } from "./useOpportunitiesLoader";
import { useTicketsAndMessages } from "./useTicketsAndMessages";
import { EquityProject } from "@/types/jobSeeker";

export const useJobSeekerDashboardCore = (refreshTrigger = 0) => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const [availableOpportunities, setAvailableOpportunities] = useState<EquityProject[]>([]);
  const [isSessionChecked, setIsSessionChecked] = useState(false);
  const [isProfileComplete, setIsProfileComplete] = useState(false);
  const [hasBusinessProfile, setHasBusinessProfile] = useState(false);
  const loadingRef = useRef(false);
  const logsDisabledRef = useRef(true);

  const { profile, skills, loadProfile, handleSkillsUpdate } = useProfile();
  const { applications, loadApplications } = useApplications();
  const { equityProjects, setEquityProjects, logEffort, setLogEffort, transformToEquityProjects } = useEquityProjects();
  const { cvUrl, setCvUrl, parsedCvData, setParsedCvData, loadCVData, userCVs, setUserCVs } = useCVData();
  const { checkSession, checkProfileCompletion, checkBusinessProfile, handleSignOut } = useSessionCheck();
  const { loadOpportunities } = useOpportunitiesLoader();
  const { userTickets, ticketMessages, loadUserTickets, handleTicketAction } = useTicketsAndMessages();

  const loadDashboardData = useCallback(async () => {
    if (loadingRef.current) return;

    loadingRef.current = true;

    try {
      setIsLoading(true);

      const session = await checkSession();
      if (!session) {
        loadingRef.current = false;
        return;
      }

      const isComplete = await checkProfileCompletion(session.user.id);
      setIsProfileComplete(isComplete);

      if (!isComplete) {
        navigate('/seeker/profile/complete');
        loadingRef.current = false;
        return;
      }

      const hasProfile = await checkBusinessProfile(session.user.id);
      setHasBusinessProfile(hasProfile);

      await Promise.all([
        loadProfile(session.user.id),
        loadApplications(session.user.id),
        loadCVData(session.user.id),
        loadUserTickets(session.user.id)
      ]);

      // Convert skills to the format expected by loadOpportunities if needed
      const skillsForOpportunities = skills ? skills.map(s => {
        if ('skill' in s) return { ...s }; 
        if ('name' in s) return { skill: s.name, level: s.level };
        return s;
      }) : [];

      const opportunities = await loadOpportunities(session.user.id, skillsForOpportunities);
      setAvailableOpportunities(opportunities);

      const acceptedProjects = transformToEquityProjects(
        applications.filter(app => app.status === 'accepted')
      );

      setEquityProjects(acceptedProjects);
      setIsSessionChecked(true);

    } catch (error) {
      toast.error("Failed to load dashboard data");
    } finally {
      setIsLoading(false);
      loadingRef.current = false;
    }
  }, [
    checkSession, 
    checkProfileCompletion, 
    checkBusinessProfile, 
    loadProfile, 
    loadApplications, 
    loadCVData, 
    loadUserTickets, 
    loadOpportunities, 
    skills, 
    applications, 
    navigate, 
    transformToEquityProjects
  ]);

  useEffect(() => {
    if (refreshTrigger > 0) {
      logsDisabledRef.current = false;

      const timer = setTimeout(() => {
        logsDisabledRef.current = true;
      }, 5000);

      return () => clearTimeout(timer);
    }

    loadDashboardData();

    const sessionCheckInterval = setInterval(async () => {
      await checkSession();
    }, 300000); // 5 minute interval

    return () => {
      clearInterval(sessionCheckInterval);
    };
  }, [checkSession, loadDashboardData, refreshTrigger]);

  const signOut = async () => {
    const result = await handleSignOut();
    if (result.success) {
      navigate('/auth/seeker');
    } else {
      toast.error("Failed to sign out");
    }
  };

  const refreshApplications = async () => {
    try {
      const { data: { session } } = await supabase.auth.getSession();
      if (!session) return;

      await loadApplications(session.user.id);
    } catch (error) {
      console.error('Error refreshing applications:', error);
    }
  };

  const onCvListUpdated = async () => {
    try {
      const { data: { session } } = await supabase.auth.getSession();
      if (session) {
        await loadCVData(session.user.id);
      }
    } catch (error) {
      toast.error("Failed to refresh CV list");
    }
  };

  return {
    isLoading,
    profile,
    cvUrl,
    applications,
    equityProjects,
    availableOpportunities,
    parsedCvData,
    skills,
    logEffort,
    setLogEffort,
    setCvUrl,
    setParsedCvData,
    setEquityProjects,
    handleSignOut: signOut,
    handleSkillsUpdate,
    refreshApplications,
    hasBusinessProfile,
    isProfileComplete,
    userCVs,
    onCvListUpdated,
    userTickets,
    ticketMessages,
    handleTicketAction
  };
};

import { useCallback } from "react";
import { supabase } from "@/lib/supabase";
import { EquityProject } from "@/types/equity";
import { Skill } from "@/types/profile";

export const useOpportunitiesLoader = () => {
  const loadOpportunities = useCallback(async (userId: string, userSkills?: any[]) => {
    try {
      const { data: userApplications, error: applicationsError } = await supabase
        .from('job_applications')
        .select('task_id, status')
        .eq('user_id', userId);

      if (applicationsError) throw applicationsError;

      const unavailableTaskIds = new Set(
        userApplications
          ?.filter(app => ['pending', 'in review', 'negotiation', 'accepted'].includes(app.status))
          .map(app => app.task_id) || []
      );

      // Create an array of just skill names for easier matching
      const userSkillStrings = (userSkills || []).map(skill => {
        if (typeof skill === 'string') return skill.toLowerCase();
        if (skill && typeof skill === 'object') {
          return ('skill' in skill && skill.skill) 
            ? skill.skill.toLowerCase() 
            : ('name' in skill && skill.name)
              ? skill.name.toLowerCase()
              : '';
        }
        return '';
      }).filter(Boolean);

      const formattedUserSkills = Array.isArray(userSkills) 
        ? userSkills.map(s => {
            if (typeof s === 'string') return s.toLowerCase();
            return typeof s === 'object' && s !== null && 'skill' in s && typeof s.skill === 'string' 
              ? s.skill.toLowerCase() 
              : '';
          }).filter(Boolean)
        : [];

      const { data: tasksData, error: tasksError } = await supabase
        .from('project_sub_tasks')
        .select(`
          *,
          business_projects!inner (
            project_id,
            title,
            business_id,
            businesses (
              company_name
            )
          )
        `)
        .eq('status', 'open');

      if (tasksError) {
        console.error("Error fetching tasks:", tasksError);
        throw tasksError;
      }

      if (!tasksData || tasksData.length === 0) {
        console.log("No tasks found");
        return [];
      }

      const opportunities = tasksData
        .filter(task => {
          if (unavailableTaskIds.has(task.task_id)) return false;

          if (formattedUserSkills.length === 0) return true;

          if (!task.skill_requirements || !Array.isArray(task.skill_requirements)) return false;

          const taskSkills = task.skill_requirements.map(s => {
            if (typeof s === 'string') return s.toLowerCase();
            return typeof s === 'object' && s !== null && 'skill' in s && typeof s.skill === 'string' 
              ? s.skill.toLowerCase() 
              : '';
          }).filter(Boolean);

          if (taskSkills.length === 0 || formattedUserSkills.length === 0) return true;

          const hasMatchingSkill = formattedUserSkills.some(skill => 
            taskSkills.includes(skill)
          );

          return hasMatchingSkill;
        })
        .map(task => {
          if (!task.skill_requirements || !Array.isArray(task.skill_requirements)) {
            task.skill_requirements = [];
          }

          // Safely convert skill requirements to string array for comparisons
          const taskSkills = task.skill_requirements.map(s => {
            if (typeof s === 'string') return s.toLowerCase();
            return typeof s === 'object' && s !== null && 'skill' in s && typeof s.skill === 'string'
              ? s.skill.toLowerCase()
              : '';
          }).filter(Boolean);

          // Only compare strings with strings for safety
          const matchingSkills = formattedUserSkills.filter(skill => 
            typeof skill === 'string' && taskSkills.includes(skill)
          );

          const matchPercentage = taskSkills.length > 0 
            ? Math.round((matchingSkills.length / taskSkills.length) * 100) 
            : 0;

          let companyName = "Unknown Company";
          let projectTitle = "Untitled Project";

          if (task.business_projects) {
            projectTitle = task.business_projects.title || "Untitled Project";

            if (task.business_projects.businesses) {
              if (Array.isArray(task.business_projects.businesses)) {
                companyName = task.business_projects.businesses[0]?.company_name || "Unknown Company";
              } else {
                companyName = task.business_projects.businesses.company_name || "Unknown Company";
              }
            }
          }

          return {
            id: task.task_id,
            project_id: task.project_id,
            equity_amount: task.equity_allocation,
            time_allocated: task.timeframe,
            status: task.status,
            start_date: task.created_at,
            effort_logs: [],
            total_hours_logged: 0,
            title: projectTitle,
            created_by: task.created_by,
            updated_at: task.updated_at,  // Add updated_at for filtering new opportunities
            skill_match: matchPercentage,
            sub_tasks: [{
              id: task.task_id,
              task_id: task.task_id,
              project_id: task.project_id,
              title: task.title,
              description: task.description,
              timeframe: task.timeframe,
              status: task.status,
              equity_allocation: task.equity_allocation,
              skill_requirements: task.skill_requirements || [],
              task_status: task.task_status,
              completion_percentage: task.completion_percentage
            }],
            business_roles: {
              title: task.title,
              description: task.description,
              project_title: projectTitle,
              company_name: companyName,
              skill_requirements: task.skill_requirements || []
            }
          };
        });

      return opportunities;
    } catch (error) {
      console.error("Error loading opportunities:", error);
      return [];
    }
  }, []);

  return { loadOpportunities };
};

import { useState, useCallback } from "react";
import { supabase } from "@/lib/supabase";
import { toast } from "sonner";
import { TicketMessage } from "@/types/dashboard";

export const useTicketsAndMessages = () => {
  const [userTickets, setUserTickets] = useState<any[]>([]);
  const [ticketMessages, setTicketMessages] = useState<TicketMessage[]>([]);

  const loadUserTickets = useCallback(async (userId: string) => {
    try {
      const { data: reportedTickets, error: reportedError } = await supabase
        .from('tickets')
        .select('*')
        .eq('reporter', userId);

      if (reportedError) {
        console.error("Error fetching reported tickets:", reportedError);
        return;
      }

      const { data: assignedTickets, error: assignedError } = await supabase
        .from('tickets')
        .select('*')
        .eq('assigned_to', userId);

      if (assignedError) {
        console.error("Error fetching assigned tickets:", assignedError);
        return;
      }

      const allTickets = [...(reportedTickets || []), ...(assignedTickets || [])];
      const uniqueTickets = Array.from(new Map(allTickets.map(ticket => [ticket.id, ticket])).values());

      setUserTickets(uniqueTickets);

      await loadTicketMessages(userId, uniqueTickets.map(t => t.id));

    } catch (error) {
      console.error("Error loading user tickets:", error);
    }
  }, []);

  const loadTicketMessages = useCallback(async (userId: string, ticketIds: string[]) => {
    if (!ticketIds.length) return;

    try {
      const { data: messages, error: messagesError } = await supabase
        .from('user_messages')
        .select('*')
        .eq('recipient_id', userId)
        .in('related_ticket', ticketIds);

      if (messagesError) {
        console.error("Error fetching ticket messages:", messagesError);
        return;
      }

      setTicketMessages(messages || []);

    } catch (error) {
      console.error("Error loading ticket messages:", error);
    }
  }, []);

  const handleTicketAction = useCallback(async (ticketId: string, action: string, data?: any) => {
    try {
      const { data: { user } } = await supabase.auth.getUser();

      if (!user) {
        toast.error("You must be logged in to perform this action");
        return;
      }

      if (action === 'reply') {
        const { message } = data || {};

        if (!message || !message.trim()) {
          toast.error("Message cannot be empty");
          return;
        }

        const { data: ticketData, error: ticketError } = await supabase
          .from('tickets')
          .select('reporter, assigned_to')
          .eq('id', ticketId)
          .single();

        if (ticketError) {
          console.error("Error fetching ticket:", ticketError);
          toast.error("Failed to send reply");
          return;
        }

        let recipientId = user.id === ticketData.reporter 
          ? ticketData.assigned_to 
          : ticketData.reporter;

        if (!recipientId) {
          const { data: adminData } = await supabase
            .from('admin_users')
            .select('user_id')
            .limit(1)
            .single();

          recipientId = adminData?.user_id;
        }

        if (!recipientId) {
          toast.error("No recipient found for your message");
          return;
        }

        const { error: messageError } = await supabase
          .from('user_messages')
          .insert({
            sender_id: user.id,
            recipient_id: recipientId,
            subject: `Re: Ticket #${ticketId.substring(0, 8)}`,
            message: message,
            related_ticket: ticketId,
            read: false
          });

        if (messageError) {
          console.error("Error sending message:", messageError);
          toast.error("Failed to send message");
          return;
        }

        const { data: ticketNotesData, error: notesError } = await supabase
          .from('tickets')
          .select('notes')
          .eq('id', ticketId)
          .single();

        if (notesError) {
          console.error("Error fetching ticket notes:", notesError);
          toast.error("Message sent but failed to update ticket history");
          return;
        }

        let notes = ticketNotesData.notes || [];

        const { data: userData } = await supabase
          .from('profiles')
          .select('first_name, last_name, email')
          .eq('id', user.id)
          .single();

        const userName = userData?.first_name 
          ? `${userData.first_name} ${userData.last_name || ''}`
          : userData?.email || user.email || 'Unknown User';

        notes.push({
          action: user.id === ticketData.reporter ? 'Reply from reporter' : 'Reply from assignee',
          user: userName,
          timestamp: new Date().toISOString(),
          comment: message
        });

        const { error: updateError } = await supabase
          .from('tickets')
          .update({
            notes: notes,
            updated_at: new Date().toISOString()
          })
          .eq('id', ticketId);

        if (updateError) {
          console.error("Error updating ticket notes:", updateError);
          toast.error("Message sent but failed to update ticket history");
          return;
        }

        toast.success("Message sent successfully");

        // Reload ticket messages
        const session = await supabase.auth.getSession();
        if (session.data.session) {
          await loadTicketMessages(session.data.session.user.id, [ticketId]);
        }
      }
    } catch (error) {
      console.error("Error handling ticket action:", error);
      toast.error("Failed to perform ticket action");
    }
  }, [loadTicketMessages]);

  return {
    userTickets,
    ticketMessages,
    loadUserTickets,
    loadTicketMessages,
    handleTicketAction
  };
};

import { useState } from "react";
import { supabase } from "@/lib/supabase";
import { toast } from "sonner";
import { JobApplication, SkillRequirement } from "@/types/jobSeeker";

export const useApplications = () => {
  const [applications, setApplications] = useState<JobApplication[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  const loadApplications = async (userId: string) => {
    try {
      setIsLoading(true);

      // Fetch all job applications for this user
      const { data, error } = await supabase
        .from('job_applications')
        .select(`
          *,
          business_roles:project_sub_tasks (
            task_id,
            title,
            description,
            timeframe,
            skill_requirements,
            equity_allocation,
            completion_percentage,
            task_status,
            project:business_projects (
              project_id,
              title,
              business:businesses (
                company_name
              )
            )
          )
        `)
        .eq('user_id', userId)
        .order('applied_at', { ascending: false });

      if (error) throw error;

      // Process the data to make it fit our JobApplication type
      const processedApplications = data.map((app: any) => {
        // Get company name from businesses relation
        const companyName = app.business_roles?.project?.business?.company_name || "Unknown Company";

        // Properly format skill_requirements to match SkillRequirement type
        const skillRequirements = app.business_roles?.skill_requirements || [];
        const formattedSkillRequirements = skillRequirements.map((req: any) => {
          if (typeof req === 'string') {
            return req;
          }

          // Ensure level is one of the allowed values
          const level = ['Beginner', 'Intermediate', 'Expert'].includes(req.level) 
            ? req.level as 'Beginner' | 'Intermediate' | 'Expert'
            : 'Intermediate';

          return { 
            skill: req.skill, 
            level 
          } as SkillRequirement;
        });

        return {
          ...app,
          id: app.job_app_id, // Ensuring id property is set
          status: app.status || "", // Ensure status is never undefined
          business_roles: {
            title: app.business_roles?.title || "Unknown Role",
            description: app.business_roles?.description || "",
            timeframe: app.business_roles?.timeframe || "",
            skill_requirements: formattedSkillRequirements,
            equity_allocation: app.business_roles?.equity_allocation,
            completion_percentage: app.business_roles?.completion_percentage,
            task_status: app.business_roles?.task_status,
            company_name: companyName,
            project_title: app.business_roles?.project?.title
          }
        };
      });

      // Set all applications without filtering
      setApplications(processedApplications);
    } catch (error) {
      toast.error("Failed to load applications");
    } finally {
      setIsLoading(false);
    }
  };

  return {
    applications,
    isLoading,
    loadApplications
  };
};

import { useState, useEffect, useRef } from "react";
import { supabase } from "@/lib/supabase";
import { toast } from "sonner";

export interface CVFile {
  id: string;
  name: string;
  url: string;
  created_at: string;
  size?: number;
  is_default?: boolean;
}

export interface ParsedCVData {
  skills: any[];
  career_history: any[];
  education: any[];
}

export const useCVData = () => {
  const [cvUrl, setCvUrl] = useState<string | null>(null);
  const [parsedCvData, setParsedCvData] = useState<ParsedCVData>({
    skills: [],
    career_history: [],
    education: []
  });
  const [userCVs, setUserCVs] = useState<CVFile[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const hasLogged = useRef<boolean>(false);
  const dataLoadedRef = useRef<boolean>(false);

  const loadCVData = async (userId: string) => {
    if (!userId || dataLoadedRef.current) return;

    setIsLoading(true);
    try {
      // Fetch the user's profile to get their CV URL
      const { data: profileData, error: profileError } = await supabase
        .from('profiles')
        .select('cv_url')
        .eq('id', userId)
        .single();

      if (profileError && profileError.code !== 'PGRST116') {
        console.error("Error fetching profile CV URL:", profileError);
      } else if (profileData?.cv_url) {
        setCvUrl(profileData.cv_url);
      }

      // Fetch parsed CV data
      const { data: parsedData, error: parsedError } = await supabase
        .from('cv_parsed_data')
        .select('skills, career_history, education')
        .eq('user_id', userId)
        .order('created_at', { ascending: false })
        .limit(1)
        .maybeSingle();

      if (parsedError && parsedError.code !== 'PGRST116') {
        console.error("Error fetching parsed CV data:", parsedError);
      } else if (parsedData) {
        setParsedCvData({
          skills: parsedData.skills || [],
          career_history: parsedData.career_history || [],
          education: parsedData.education || []
        });
      }

      // Fetch the user's CV files
      try {
        const { data: cvFiles, error: cvFilesError } = await supabase
          .storage
          .from('cvs')
          .list(`${userId}`);

        if (cvFilesError) {
          console.error("Error fetching CV files:", cvFilesError);
          setUserCVs([]);
          return;
        }

        // Transform the data to include full URLs
        if (cvFiles && cvFiles.length > 0) {
          const transformedCVs: CVFile[] = await Promise.all(
            cvFiles.map(async (file) => {
              const { data: urlData } = await supabase
                .storage
                .from('cvs')
                .createSignedUrl(`${userId}/${file.name}`, 60 * 60); // 1 hour expiry

              return {
                id: `${userId}_${file.name}`,
                name: file.name,
                url: urlData?.signedUrl || '',
                created_at: file.created_at || new Date().toISOString(),
                size: file.metadata?.size,
                is_default: profileData?.cv_url?.includes(file.name) || false
              };
            })
          );

          if (!hasLogged.current) {
            console.info("CV files loaded:", transformedCVs.length);
            hasLogged.current = true;
          }

          setUserCVs(transformedCVs);
        } else {
          // If no files were found, set an empty array and only log once
          if (!hasLogged.current) {
            console.info("CV files loaded: 0");
            hasLogged.current = true;
          }
          setUserCVs([]);
        }
      } catch (error) {
        // Handle storage errors gracefully
        console.error("Storage error:", error);
        setUserCVs([]);
      }

      // Mark data as loaded to prevent redundant fetches
      dataLoadedRef.current = true;
    } catch (error) {
      console.error("Error loading CV data:", error);
      toast.error("Failed to load CV data");
    } finally {
      setIsLoading(false);
    }
  };

  // Reset the dataLoadedRef when the component unmounts
  useEffect(() => {
    return () => {
      dataLoadedRef.current = false;
      hasLogged.current = false;
    };
  }, []);

  return {
    cvUrl,
    setCvUrl,
    parsedCvData,
    setParsedCvData,
    loadCVData,
    userCVs,
    setUserCVs,
    isLoading
  };
};

import { supabase } from './client';

// Function to test if a user can access files in a storage bucket
async function testStorageAccess(userId: string, bucketId: string, filePath: string) {
  try {
    // For testing storage policies, we need to be authenticated as the user
    console.log(`Testing storage access for user ${userId} to ${bucketId}/${filePath}`);

    // Check if we're authenticated
    const { data: { session } } = await supabase.auth.getSession();
    if (!session) {
      return { success: false, error: "Not authenticated", status: "Not authenticated" };
    }

    // Check if the bucket exists using storage API directly
    const { data, error } = await supabase.storage
      .from(bucketId)
      .list(filePath || '');

    if (error) {
      console.error("Storage access error:", error);
      return { success: false, error, status: error.message };
    }

    return { success: true, data, status: "Access granted" };
  } catch (error: any) {
    console.error("Error testing storage access:", error);
    return { success: false, error, status: error.message || "Unknown error" };
  }
}

