interface FeaturedProject {
  project_id: string;
  title: string;
  description: string;
  equity_allocation: number;
  skills_required: string[];
  sub_tasks: {
    task_id: string;
    title: string;
    description: string;
    equity_allocation: number;
    skill_requirements: any[];
  }[];
  company_name?: string;
  businesses?: {
    company_name?: string;
  };
}

const Index = () => {
  const navigate = useNavigate();
  const [featuredProjects, setFeaturedProjects] = useState<FeaturedProject[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchFeaturedProjects = async () => {
      try {
        setIsLoading(true);
        const { data, error } = await supabase
          .from("business_projects")
          .select(`
            project_id,
            title,
            description,
            equity_allocation,
            skills_required,
            businesses(company_name),
            project_sub_tasks:project_sub_tasks(
              task_id,
              title,
              description,
              equity_allocation,
              skill_requirements
            )
          `)
          .eq("status", "active")
          .limit(4);

        if (error) throw error;

        const processedData: FeaturedProject[] = data.map((project) => {
          let companyName = "Unknown Company";
          if (project.businesses) {
            if (Array.isArray(project.businesses)) {
              companyName = project.businesses[0]?.company_name || "Unknown Company";
            } else if (project.businesses && typeof project.businesses === "object") {
              companyName = (project.businesses as any).company_name || "Unknown Company";
            }
          }

          return {
            project_id: project.project_id,
            title: project.title,
            description: project.description,
            equity_allocation: project.equity_allocation,
            skills_required: project.skills_required || [],
            company_name: companyName,
            sub_tasks: (project.project_sub_tasks || []).map((task: any) => ({
              task_id: task.task_id,
              title: task.title,
              description: task.description,
              equity_allocation: task.equity_allocation,
              skill_requirements: task.skill_requirements || [],
            })),
          };
        });

        setFeaturedProjects(processedData);
      } catch (error) {
        console.error("Error fetching featured projects:", error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchFeaturedProjects();
  }, []);

  const userTypes = [
    {
      title: "Job Seeker",
      description: "Find your next opportunity and showcase your skills",
      icon: User,
      type: "seeker" as const,
      path: "/auth/seeker",
    },
    {
      title: "Business",
      description: "Post jobs and find the perfect candidates",
      icon: Building2,
      type: "business" as const,
      path: "/auth/business",
    },
  ];

  return (
    <div>
      <header className="bg-gradient-to-r from-primary/20 to-primary/10 py-16 md:py-24">
        <div className="container mx-auto px-6 text-center">
          <h1 className="text-4xl md:text-6xl font-bold mb-6">
            Exchange <span className="text-primary">Skills</span> for <span className="text-primary">Equity</span>
          </h1>
          <p className="text-xl md:text-2xl mb-8 max-w-3xl mx-auto">
            Connect job seekers with businesses looking to offer equity in exchange for skills and experience.
             <br /> 
             <br /> Let's start by registering or logging in.
          </p>
                    <div className="grid md:grid-cols-2 gap-6 w-full max-w-4xl mx-auto mb-12">
            {userTypes.map((type) => (
              <Card
                key={type.type}
                className="p-6 landing-card hover:shadow-md transition-shadow cursor-pointer"
                onClick={() => navigate(type.path)}
              >
                <div className="flex flex-col items-center text-center h-full">
                  <div className="mb-4 p-3 rounded-full bg-accent/10 text-accent">
                    <type.icon size={24} />
                  </div>
                  <h2 className="text-xl font-semibold mb-2">{type.title}</h2>
                  <p className="text-sm text-muted-foreground">
                    {type.description}
                  </p>
                </div>
              </Card>
            ))}
          </div>
        </div>
      </header>

      <section className="py-16 bg-slate-50/50">
        <div className="container">
          <RegistrationProcess />
        </div>
      </section>

      <section className="py-16 bg-white">
        <div className="container mx-auto px-4 md:px-6">
          <ContractProcessExplanation />
        </div>
      </section>

      <div className="w-full max-w-6xl mx-auto text-center mb-8">
        <h2 className="text-2xl font-semibold mb-6">Featured Equity Projects</h2>

        {isLoading ? (
          <div className="grid md:grid-cols-2 gap-6 justify-center">
            {[1, 2, 3, 4].map((i) => (
              <Card key={i} className="p-6 h-64 animate-pulse bg-muted/50"></Card>
            ))}
          </div>
        ) : (
          <div className="grid md:grid-cols-2 gap-6 justify-center">
            {featuredProjects.map((project) => (
              <Card key={project.project_id} className="p-6 hover:shadow-md transition-shadow">
                <h3 className="text-lg font-semibold">{project.title}</h3>
                <p className="text-sm text-muted-foreground">{project.company_name}</p>
                <p className="text-sm mb-4 line-clamp-2">{project.description}</p>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Index;

import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { supabase } from "@/lib/supabase";
import { useNavigate } from "react-router-dom";
import { Button } from "@/components/ui/button";
import { PieChart, Pie, Cell, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { ShieldCheck, Users, Briefcase, FileText, LogOut, PieChart as PieChartIcon } from "lucide-react";

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8', '#82ca9d'];

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [isAdmin, setIsAdmin] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [userStats, setUserStats] = useState({
    totalUsers: 0,
    businessUsers: 0,
    jobSeekers: 0
  });
  const [projectStats, setProjectStats] = useState({
    totalProjects: 0,
    activeProjects: 0,
    completedProjects: 0
  });
  const [applicationStats, setApplicationStats] = useState<any[]>([]);
  const [taskStats, setTaskStats] = useState<any[]>([]);

  useEffect(() => {
    const checkAdminAccess = async () => {
      try {
        const { data: { session } } = await supabase.auth.getSession();

        if (!session) {
          navigate('/');
          return;
        }

        // For demo purposes - in production, you would have a proper admin role check
        const adminEmails = ['admin@sweaquity.com', 'your-admin-email@example.com'];
        const isAdminUser = adminEmails.includes(session.user.email as string);

        if (!isAdminUser) {
          navigate('/');
          return;
        }

        setIsAdmin(true);
        fetchDashboardData();
      } catch (error) {
        console.error("Error checking admin access:", error);
        navigate('/');
      }
    };

    checkAdminAccess();
  }, [navigate]);

  const fetchDashboardData = async () => {
    setIsLoading(true);
    try {
      // Fetch user statistics
      const { count: businessCount } = await supabase
        .from('businesses')
        .select('*', { count: 'exact', head: true });

      const { count: profileCount } = await supabase
        .from('profiles')
        .select('*', { count: 'exact', head: true });

      setUserStats({
        totalUsers: (businessCount || 0) + (profileCount || 0),
        businessUsers: businessCount || 0,
        jobSeekers: profileCount || 0
      });

      // Fetch project statistics
      const { data: projects, error: projectError } = await supabase
        .from('business_projects')
        .select('status');

      if (projectError) throw projectError;

      const activeProjects = projects.filter(p => p.status === 'active').length;
      const completedProjects = projects.filter(p => p.status === 'completed').length;

      setProjectStats({
        totalProjects: projects.length,
        activeProjects,
        completedProjects
      });

      // Fetch application statistics by status
      const { data: applications, error: appError } = await supabase
        .from('job_applications')
        .select('status');

      if (appError) throw appError;

      const appStatusCounts: Record<string, number> = {};
      applications.forEach(app => {
        const status = app.status.toLowerCase();
        appStatusCounts[status] = (appStatusCounts[status] || 0) + 1;
      });

      const appStats = Object.entries(appStatusCounts).map(([name, value]) => ({
        name,
        value
      }));

      setApplicationStats(appStats);

      // Fetch task statistics by status
      const { data: tasks, error: taskError } = await supabase
        .from('project_sub_tasks')
        .select('status');

      if (taskError) throw taskError;

      const taskStatusCounts: Record<string, number> = {};
      tasks.forEach(task => {
        const status = task.status.toLowerCase();
        taskStatusCounts[status] = (taskStatusCounts[status] || 0) + 1;
      });

      const taskStats = Object.entries(taskStatusCounts).map(([name, value]) => ({
        name,
        value
      }));

      setTaskStats(taskStats);

    } catch (error) {
      console.error("Error fetching dashboard data:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignOut = async () => {
    try {
      await supabase.auth.signOut();
      navigate('/');
    } catch (error) {
      console.error("Error signing out:", error);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin h-12 w-12 border-4 border-primary border-t-transparent rounded-full"></div>
      </div>
    );
  }

  if (!isAdmin) {
    return null; // Will redirect in useEffect
  }

  return (
    <div className="min-h-screen p-4 md:p-6">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-2">
            <ShieldCheck className="h-8 w-8 text-primary" />
            <h1 className="text-2xl font-bold">Admin Dashboard</h1>
          </div>
          <Button variant="outline" onClick={handleSignOut}>
            <LogOut className="h-4 w-4 mr-2" />
            Sign Out
          </Button>
        </div>

        <Tabs defaultValue="overview" className="space-y-6">
          <TabsList className="grid grid-cols-3 w-full md:w-auto">
            <TabsTrigger value="overview">Overview</TabsTrigger>
            <TabsTrigger value="applications">Applications</TabsTrigger>
            <TabsTrigger value="tasks">Tasks</TabsTrigger>
          </TabsList>

          <TabsContent value="overview">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
              <Card>
                <CardHeader className="pb-2">
                  <CardTitle className="text-sm font-medium flex items-center gap-2">
                    <Users className="h-4 w-4 text-blue-500" />
                    Users
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold">{userStats.totalUsers}</div>
                  <div className="text-xs text-muted-foreground mt-1">
                    {userStats.businessUsers} businesses, {userStats.jobSeekers} job seekers
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader className="pb-2">
                  <CardTitle className="text-sm font-medium flex items-center gap-2">
                    <Briefcase className="h-4 w-4 text-green-500" />
                    Projects
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold">{projectStats.totalProjects}</div>
                  <div className="text-xs text-muted-foreground mt-1">
                    {projectStats.activeProjects} active, {projectStats.completedProjects} completed
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader className="pb-2">
                  <CardTitle className="text-sm font-medium flex items-center gap-2">
                    <FileText className="h-4 w-4 text-yellow-500" />
                    Applications
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold">{applicationStats.reduce((sum, item) => sum + item.value, 0)}</div>
                  <div className="text-xs text-muted-foreground mt-1">
                    Across all projects
                  </div>
                </CardContent>
              </Card>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg font-medium">User Distribution</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="h-[300px] flex items-center justify-center">
                    <ResponsiveContainer width="100%" height="100%">
                      <PieChart>
                        <Pie
                          data={[
                            { name: 'Job Seekers', value: userStats.jobSeekers },
                            { name: 'Businesses', value: userStats.businessUsers }
                          ]}
                          cx="50%"
                          cy="50%"
                          labelLine={false}
                          outerRadius={80}
                          fill="#8884d8"
                          dataKey="value"
                          label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                        >
                          {[
                            { name: 'Job Seekers', value: userStats.jobSeekers },
                            { name: 'Businesses', value: userStats.businessUsers }
                          ].map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                          ))}
                        </Pie>
                        <Tooltip />
                        <Legend />
                      </PieChart>
                    </ResponsiveContainer>
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle className="text-lg font-medium">Application Status</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="h-[300px]">
                    <ResponsiveContainer width="100%" height="100%">
                      <BarChart
                        width={500}
                        height={300}
                        data={applicationStats}
                        margin={{
                          top: 5,
                          right: 30,
                          left: 20,
                          bottom: 5,
                        }}
                      >
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="name" />
                        <YAxis />
                        <Tooltip />
                        <Legend />
                        <Bar dataKey="value" fill="#8884d8" />
                      </BarChart>
                    </ResponsiveContainer>
                  </div>
                </CardContent>
              </Card>
            </div>
          </TabsContent>

          <TabsContent value="applications">
            <Card>
              <CardHeader>
                <CardTitle>Application Status Breakdown</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="h-[400px]">
                  <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                      <Pie
                        data={applicationStats}
                        cx="50%"
                        cy="50%"
                        labelLine={true}
                        outerRadius={150}
                        fill="#8884d8"
                        dataKey="value"
                        label={({ name, value, percent }) => `${name}: ${value} (${(percent * 100).toFixed(0)}%)`}
                      >
                        {applicationStats.map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                        ))}
                      </Pie>
                      <Tooltip />
                      <Legend />
                    </PieChart>
                  </ResponsiveContainer>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="tasks">
            <Card>
              <CardHeader>
                <CardTitle>Task Status Breakdown</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="h-[400px]">
                  <ResponsiveContainer width="100%" height="100%">
                    <BarChart
                      width={500}
                      height={300}
                      data={taskStats}
                      margin={{
                        top: 5,
                        right: 30,
                        left: 20,
                        bottom: 5,
                      }}
                    >
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="name" />
                      <YAxis />
                      <Tooltip />
                      <Legend />
                      <Bar dataKey="value" fill="#82ca9d" />
                    </BarChart>
                  </ResponsiveContainer>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
};

export default AdminDashboard;

